UPDATE locales_npc_text SET entry = 2993, Text0_0_loc2 = 'Ils sont perdus, $r. Les libram écrits par les maîtres elfes. Dispersés sur la surface du monde quand le Fléau a été lâché sur Quel\'Thalas.$B$BNous cherchons maintenant ces libram. Trouvez les secrets qu\'ils recèlent, pour restaurer la puissance de jadis.$B$BJe cherche les dix volumes de Maître Kariel Winthalus. Si vous trouvez un libram, permettez-moi d\'absorber ses propriétés magiques et je créerai pour vous un cristal de pouvoir... Bien sûr, tout a un prix. Vous verrez que le mien est élevé, mais vous paierez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 2993;
