UPDATE locales_npc_text SET entry = 3017, Text0_0_loc2 = 'Vous trouverez où laisser vos biens matériels dans la banque de Darnassus, qui est au centre de notre merveilleuse cité.', Text0_1_loc2 = 'Vous trouverez où laisser vos biens matériels dans la banque de Darnassus, qui est au centre de notre merveilleuse cité.', Text1_0_loc2 = 'On est $gporté:portée; sur les possessions matérielles, hein ? Si vous aimez tant que ça vos petits objets, l\'endroit le plus sûr pour les mettre est la banque de Darnassus, au centre de la ville.', Text1_1_loc2 = 'On est $gporté:portée; sur les possessions matérielles, hein ? Si vous aimez tant que ça vos petits objets, l\'endroit le plus sûr pour les mettre est la banque de Darnassus, au centre de la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3017;
UPDATE locales_npc_text SET entry = 3018, Text0_0_loc2 = 'Les hippogriffes de Vesprystus vous transporteront rapidement à votre destination. Allez le voir au village de Rut\'theran, en passant par le portail qui est dans le quartier ouest de Darnassus.', Text0_1_loc2 = 'Les hippogriffes de Vesprystus vous transporteront rapidement à votre destination. Allez le voir au village de Rut\'theran, en passant par le portail qui est dans le quartier ouest de Darnassus.', Text1_0_loc2 = 'Vous devez voir Vesprystus. Il s\'occupe des hippogriffes qui vont et viennent depuis la base de Teldrassil. Vous le trouverez en prenant le portail à l’ouest de Darnassus. Il mène au village de Rut\'theran.', Text1_1_loc2 = 'Vous devez voir Vesprystus. Il s\'occupe des hippogriffes qui vont et viennent depuis la base de Teldrassil. Vous le trouverez en prenant le portail à l’ouest de Darnassus. Il mène au village de Rut\'theran.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3018;
UPDATE locales_npc_text SET entry = 3019, Text0_0_loc2 = 'Si vous voulez former une communauté, vous devez voir le maître des guildes, Lysheana. Vous la trouverez à la Terrasse des artisans, dans le nord-est de Darnassus.', Text0_1_loc2 = 'Si vous voulez former une communauté, vous devez voir le maître des guildes, Lysheana. Vous la trouverez à la Terrasse des artisans, dans le nord-est de Darnassus.', Text1_0_loc2 = 'Lysheana peut vous procurer les documents adéquats. Elle réside à la Terrasse des Artisans, au nord-est de Darnassus. Que votre compagnie apporte la gloire aux elfes de la nuit !', Text1_1_loc2 = 'Lysheana peut vous procurer les documents adéquats. Elle réside à la Terrasse des Artisans, au nord-est de Darnassus. Que votre compagnie apporte la gloire aux elfes de la nuit !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3019;
UPDATE locales_npc_text SET entry = 3020, Text0_0_loc2 = 'Je trouve que l\'herbe grasse qui recouvre Teldrassil est un lit des plus corrects, mais les matelas de l\'auberge de Saelienne, à la Terrasse des artisans, conviennent mieux à ceux qui ont le goût du luxe.', Text0_1_loc2 = 'Je trouve que l\'herbe grasse qui recouvre Teldrassil est un lit des plus corrects, mais les matelas de l\'auberge de Saelienne, à la Terrasse des artisans, conviennent mieux à ceux qui ont le goût du luxe.', Text1_0_loc2 = 'Le soleil s\'est déjà levé ? La nuit semble toujours trop courte. Enfin, Saelienne prendra bien soin de vous. Vous la trouverez à la Terrasse des Artisans, au nord-est de Darnassus.', Text1_1_loc2 = 'Le soleil s\'est déjà levé ? La nuit semble toujours trop courte. Enfin, Saelienne prendra bien soin de vous. Vous la trouverez à la Terrasse des Artisans, au nord-est de Darnassus.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3020;
UPDATE locales_npc_text SET entry = 3021, Text0_0_loc2 = 'Il y a une boîte aux lettres à l’extérieur de la banque. Vous la trouverez au centre de Darnassus.', Text0_1_loc2 = 'Il y a une boîte aux lettres à l’extérieur de la banque. Vous la trouverez au centre de Darnassus.', Text1_0_loc2 = 'Une boîte aux lettres ? Vous avez une lettre à expédier, ou vous en attendez peut-être une ? J’espère que ce sont de bonnes nouvelles ! Il y a une boîte aux lettres juste à l’extérieur de la banque, au centre de Darnassus.', Text1_1_loc2 = 'Une boîte aux lettres ? Vous avez une lettre à expédier, ou vous en attendez peut-être une ? J’espère que ce sont de bonnes nouvelles ! Il y a une boîte aux lettres juste à l’extérieur de la banque, au centre de Darnassus.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3021;
UPDATE locales_npc_text SET entry = 3022, Text0_0_loc2 = 'Ici, à Darnassus, vous trouverez des maîtres habiles qui ont peaufiné les compétences de leur classe pendant des décennies d\'entraînement. Je vous guiderai vers celui qui peut être votre mentor, vous n\'avez qu\'à nommer la voie que vous avez choisie.', Text0_1_loc2 = 'Ici, à Darnassus, vous trouverez des maîtres habiles qui ont peaufiné les compétences de leur classe pendant des décennies d\'entraînement. Je vous guiderai vers celui qui peut être votre mentor, vous n\'avez qu\'à nommer la voie que vous avez choisie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3022;
UPDATE locales_npc_text SET entry = 3023, Text0_0_loc2 = 'Vous avez de la chance d\'être à Darnassus, vous y trouverez un chasseur aussi accompli que Jocaste parmi nos maîtres. Je vous encourage à lui rendre visite dans l\'Enclave cénarienne et à solliciter son enseignement.', Text0_1_loc2 = 'Vous avez de la chance d\'être à Darnassus, vous y trouverez un chasseur aussi accompli que Jocaste parmi nos maîtres. Je vous encourage à lui rendre visite dans l\'Enclave cénarienne et à solliciter son enseignement.', Text1_0_loc2 = 'Jocaste était la plus grande chasseresse de son temps. Ces temps-ci, vous pouvez la trouver dans l\'Enclave cénarienne. Elle y enseigne à ceux qui viennent d\'emprunter la voie du chasseur.', Text1_1_loc2 = 'Jocaste était la plus grande chasseresse de son temps. Ces temps-ci, vous pouvez la trouver dans l\'Enclave cénarienne. Elle y enseigne à ceux qui viennent d\'emprunter la voie du chasseur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3023;
UPDATE locales_npc_text SET entry = 3024, Text0_0_loc2 = 'Toujours attentifs à la protection du monde naturel, les druides sont guidés par l\'archidruide Fandral Forteramure, qui réside dans l\'Enclave cénarienne. Vous le trouverez, ainsi que les autres maîtres des druides, dans la zone nord de Darnassus.', Text0_1_loc2 = 'Toujours attentifs à la protection du monde naturel, les druides sont guidés par l\'archidruide Fandral Forteramure, qui réside dans l\'Enclave cénarienne. Vous le trouverez, ainsi que les autres maîtres des druides, dans la zone nord de Darnassus.', Text1_0_loc2 = 'Dans les temps sombres, un druide peut être grandement apprécié de ceux qui l\'entourent. Demandez à n\'importe quel druide de l\'Enclave cénarienne et il vous le dira aussi.', Text1_1_loc2 = 'Dans les temps sombres, un druide peut être grandement apprécié de ceux qui l\'entourent. Demandez à n\'importe quel druide de l\'Enclave cénarienne et il vous le dira aussi.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3024;
