UPDATE locales_npc_text SET entry = 3044, Text0_0_loc2 = 'Vous cherchez sans doute Me\'Lynn si vous avez quelque espoir de devenir tailleur. Elle se trouve à la Terrasse des artisans, dans la zone nord-est de Darnassus.', Text0_1_loc2 = 'Vous cherchez sans doute Me\'Lynn si vous avez quelque espoir de devenir tailleur. Elle se trouve à la Terrasse des artisans, dans la zone nord-est de Darnassus.', Text1_0_loc2 = 'Vu ce que Me\'Lynn est capable de coudre, on se dit que son aiguille a été bénie par Elune elle-même. À moins que ce ne soit également votre cas, vous devriez lui rendre visite et l\'écouter. Vous la trouverez à la Terrasse des Artisans, au nord-est de Darnassus.', Text1_1_loc2 = 'Vu ce que Me\'Lynn est capable de coudre, on se dit que son aiguille a été bénie par Elune elle-même. À moins que ce ne soit également votre cas, vous devriez lui rendre visite et l\'écouter. Vous la trouverez à la Terrasse des Artisans, au nord-est de Darnassus.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3044;
UPDATE locales_npc_text SET entry = 3046, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai observé quatre espèces différentes de plantes affectées par la corruption : la fleur-de-chant, la fleur-de-vent, la navetille et le dragon nocturne. La fleur-de-chant et la fleur-de-vent sont communes dans Gangrebois, les deux autres sont plus rares. Toutes sont vitales pour l\'écosystème de Gangrebois.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3046;
