UPDATE locales_npc_text SET entry = 3074, Text0_0_loc2 = 'Très bien *hic*...Dites, vous avez l\'air familier *hic*. Je vous ai déjà raconté quand j\'étais avec Windsor et qu\'on a combattu tren... heu, mil... heu, un million d\'orcs ?$B$BOh, préparez-vous, c\'est une excellente histoire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3074;
UPDATE locales_npc_text SET entry = 3075, Text0_0_loc2 = 'L\'hôtel des ventes d\'Orgrimmar se trouve dans la vallée de la Force. Cherchez-le à du côté est, près d\'Espingoles imports.', Text0_1_loc2 = 'L\'hôtel des ventes d\'Orgrimmar se trouve dans la vallée de la Force. Cherchez-le à du côté est, près d\'Espingoles imports.', Text1_0_loc2 = 'Votre voix est rauque à force de crier, hein ? L\'hôtel des ventes d\'Orgrimmar vous attend dans la vallée de la Force.', Text1_1_loc2 = 'Votre voix est rauque à force de crier, hein ? L\'hôtel des ventes d\'Orgrimmar vous attend dans la vallée de la Force.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3075;
UPDATE locales_npc_text SET entry = 3076, Text0_0_loc2 = 'Bienvenue dans la banque de Darnassus. Nous proposons des portefeuilles financiers et des coffres de dépôt pour les objets précieux. Avez-vous déjà un compte chez nous, $g monseigneur : madame; ?', Text0_1_loc2 = 'Bienvenue dans la banque de Darnassus. Nous proposons des portefeuilles financiers et des coffres de dépôt pour les objets précieux. Avez-vous déjà un compte chez nous, $g monseigneur : madame; ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3076;
UPDATE locales_npc_text SET entry = 3077, Text0_0_loc2 = 'Incroyables, n\'est-ce pas ? Toutes ces reliques, tous ces restes.$B$BJe ne sais pas ce qui m\'impressionne le plus, leur taille ou les histoires qui les accompagnent. Il y a tant à étudier et à apprendre. Et les liens que nous pouvons établir...$B$BSelon la rumeur, bien des races ont le même héritage que les elfes de la nuit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3077;
UPDATE locales_npc_text SET entry = 3093, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour, mon nom est Petra. À ma droite, mon kodo, Dadanga. Nous sommes avec les Expéditions Marshal, depuis quelques années déjà, à vrai dire depuis que Dadanga est tout bébé.$B$BSi vous avez des torches, ou n\'importe quoi qui produit des flammes, merci de les garder à distance. Dadanga n\'aime pas la fumée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3093;
UPDATE locales_npc_text SET entry = 3094, Text0_0_loc2 = 'Pour l\'amour de tout ce qui est sacré et chéri, dites-moi que vous êtes $g venu : venue; nous délivrer de ces terres maudites. Bien que je sois avec les Expéditions Marshal, je vous donnerais mes propres dents en or pour grimper dans votre sac à dos et quitter cet endroit.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3094;
UPDATE locales_npc_text SET entry = 3095, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, bien le bonjour ! Je suis Hol\'anyee, la scientifique et biologiste en charge des Expéditions Marshal. Mon frère et moi, ainsi que nos compagnons, traversons Azeroth en explorant sa beauté sauvage et encore vierge. C\'est si passionnant... Mais je crains que nous ne nous soyons montrés trop passionnés cette fois...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3095;
UPDATE locales_npc_text SET entry = 3096, Text0_0_loc2 = 'Salut, $gétranger:étrangère; ! Bienvenue au majestueux et sauvage Cratère d\'Un\'Goro !$B$BJe me nomme Williden Marshal. Ma sœur, mes compagnons et moi avons créé les Expéditions Marshal : des scientifiques et des explorateurs s\'aventurant dans les grandes régions inconnues d\'Azeroth.$B$BNous avons rencontré quelques déconvenues ici, et votre aide sera la bienvenue, si vous en avez le temps...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3096;
UPDATE locales_npc_text SET entry = 3098, Text0_0_loc2 = 'Regardez ces échantillons ! Qu\'est-ce que ça veut dire ? Je vais avoir besoin de temps pour déchiffrer toutes ces informations. Et il me faudra d\'autres informations. Des informations que je ne puis obtenir... Enfer ! Réfléchis un peu, Laris !$B$BQuest-ce que tu vas faire ? Quoi ?!$B$BIl doit bien y avoir une solution !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3098;
UPDATE locales_npc_text SET entry = 3100, Text0_0_loc2 = 'Non, pas que je sache. C\'est essentiellement un problème de région. Certaines races les appellent gelées, d\'autres limons. Question de préférence.$B$BÀ chaque fois que j\'en ai rencontré, il s\'agissait en fait des mêmes créatures. Les différences semblent relever de l\'environnement, c\'est pourquoi nous avons besoin d\'un échantillon pur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3100;
UPDATE locales_npc_text SET entry = 3101, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3101;
UPDATE locales_npc_text SET entry = 3102, Text0_0_loc2 = 'Nulle part dans les Royaumes de l\'est, c\'est certain.$B$BCe serait quelque part à Kalimdor, mais assez loin de la Horde et des autres races. Un endroit qui n\'aurait pas été affecté par la Fracture, c\'est à dire pas à portée non plus des elfes de la nuit.$B$BUn endroit protégé, je pense, une vallée, un cratère. Je fais des suppositions, rien de plus. Désolé de ne pas pouvoir mieux vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3102;
UPDATE locales_npc_text SET entry = 3133, Text0_0_loc2 = 'Pourquoi quitter ces rivages ensoleillés et ces eaux bleues cristallines ? Je me le demande. C\'est un vrai paradis ici. Mais ne le dites pas à mon frère Zapetta, il est déjà assez amer d\'être coincé à Tirisfal. Ce zeppelin va à Orgrimmar, vous n\'aurez donc pas à l\'écouter pleurnicher. Mais si vous allez à Tirisfal un jour prochain, souvenez-vous de mes conseils et ne l\'encouragez pas à vous parler.', Text0_1_loc2 = 'Pourquoi quitter ces rivages ensoleillés et ces eaux bleues cristallines ? Je me le demande. C\'est un vrai paradis ici. Mais ne le dites pas à mon frère Zapetta, il est déjà assez amer d\'être coincé à Tirisfal. Ce zeppelin va à Orgrimmar, vous n\'aurez donc pas à l\'écouter pleurnicher. Mais si vous allez à Tirisfal un jour prochain, souvenez-vous de mes conseils et ne l\'encouragez pas à vous parler.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3133;
UPDATE locales_npc_text SET entry = 3153, Text0_0_loc2 = 'Cherchez l\'aubergiste Pala dans le bâtiment juste au sud de la banque des Pitons du Tonnerre sur la Côte inférieure.', Text0_1_loc2 = 'Cherchez l\'aubergiste Pala dans le bâtiment juste au sud de la banque des Pitons du Tonnerre sur la Côte inférieure.', Text1_0_loc2 = 'L\'aubergiste Pala vous aidera à reposer votre corps et votre esprit. Vous la trouverez dans le bâtiment au sud de la banque, sur la Côte inférieure.', Text1_1_loc2 = 'L\'aubergiste Pala vous aidera à reposer votre corps et votre esprit. Vous la trouverez dans le bâtiment au sud de la banque, sur la Côte inférieure.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3153;
UPDATE locales_npc_text SET entry = 3154, Text0_0_loc2 = 'Vous pouvez trouver une boîte aux lettres juste à l\'extérieur de la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text0_1_loc2 = 'Vous pouvez trouver une boîte aux lettres juste à l\'extérieur de la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text1_0_loc2 = 'Il me semble que du courrier a été apporté il n\'y a pas longtemps. Vous trouverez la boîte aux lettres à l\'extérieur de la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text1_1_loc2 = 'Il me semble que du courrier a été apporté il n\'y a pas longtemps. Vous trouverez la boîte aux lettres à l\'extérieur de la banque des Pitons du Tonnerre, sur la Côte inférieure.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3154;
UPDATE locales_npc_text SET entry = 3155, Text0_0_loc2 = 'Vous le trouverez dans la partie basse des Pitons du Tonnerre. Il n’est pas très loin de la banque.', Text0_1_loc2 = 'Vous le trouverez dans la partie basse des Pitons du Tonnerre. Il n’est pas très loin de la banque.', Text1_0_loc2 = 'L’hôtel des ventes se trouve sur le plateau central des Pitons du Tonnerre. Vous le trouverez près de la base de la tour des coursiers du vent. Que les ancêtres vous gardent !', Text1_1_loc2 = 'L’hôtel des ventes se trouve sur le plateau central des Pitons du Tonnerre. Vous le trouverez près de la base de la tour des coursiers du vent. Que les ancêtres vous gardent !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3155;
UPDATE locales_npc_text SET entry = 3156, Text0_0_loc2 = 'Cela va peut-être vous choquer, mais les affaires ne vont pas bien ces temps-ci...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3156;
UPDATE locales_npc_text SET entry = 3157, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous souhaite la bienvenue à Theramore, $N.$B$BJe m\'excuse de me montrer abrupte et de ne pas prendre le temps d\'échanger des politesses, mais la cité passe avant tout. Trop de choses menacent notre mode de vie et la sécurité de l\'Alliance. Je dois rester vigilante contre des forces que nous ne contrôlons pas si je veux protéger mon peuple.$B$BNous aurons peut-être le temps de parler plus tard.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3157;
UPDATE locales_npc_text SET entry = 3158, Text0_0_loc2 = null, Text0_1_loc2 = 'Hendel est à nous désormais, $N, grâce à vous.$B$BL\'interrogatoire se déroule lentement, mais je vous assure qu\'il nous dira tout ce qu\'il sait.$B$BPour l\'heure, patience. Je vous contacterai dès que nous aurons besoin de votre assistance.$B$BEn attendant, profitez de Theramore. Je vous invite à rester aussi longtemps que vous le voudrez dans les murs de la ville, avec l\'amitié de l\'Alliance.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3158;
UPDATE locales_npc_text SET entry = 3173, Text0_0_loc2 = 'Il y a un panneau à la sortie de la porte principale de la ville. Il vous indiquera la tour des zeppelins ouest.', Text0_1_loc2 = 'Il y a un panneau à la sortie de la porte principale de la ville. Il vous indiquera la tour des zeppelins ouest.', Text1_0_loc2 = 'Cherchez la tour d\'atterrissage des zeppelins à l\'extérieur de la ville, vers le sud-est.', Text1_1_loc2 = 'Cherchez la tour d\'atterrissage des zeppelins à l\'extérieur de la ville, vers le sud-est.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3173;
UPDATE locales_npc_text SET entry = 3193, Text0_0_loc2 = 'Mon frère et moi avons voué notre existence à Sylvanas. Elle est notre fanal au cœur des heures sombres. Nous nous sacrifions jour après jour pour échapper à notre destin.$B$BNous ne resterons pas faibles. Nous prendrons ce qui nous revient. Nous rendrons au centuple le mal qui nous a été fait. Et nous le ferons le sourire aux lèvres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3193;
UPDATE locales_npc_text SET entry = 3194, Text0_0_loc2 = 'Dame Sylvanas a ordonné que des recherches soient faites sur certains phénomènes naturels d\'Azeroth. Elle pense qu\'il y a beaucoup à apprendre de son passé enfoui.$B$BJ\'ignore comment elle le sait, mais je lui fais confiance et je ferai de mon mieux.$B$BJe mène actuellement l\'enquête sur les limons, les gelées, etc. Ces créatures ont toutes l\'air semblables dans leur constitution, mais elles sont grandement affectées par leur environnement.$B$BVotre aide me serait précieuse pour récupérer des échantillons de ces créatures.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3194;
UPDATE locales_npc_text SET entry = 3213, Text0_0_loc2 = '<Volcor ne vous regarde pas tandis que vous vous approchez. Il semble se concentrer sur ses bandages, qui ne tiennent guère. Il finit par vous remarquer, un peu surpris.>$B$BOh, mille pardons, $c. Ce n\'est pas le bon moment. J\'espère que vous comprendrez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3213;
UPDATE locales_npc_text SET entry = 3214, Text0_0_loc2 = 'Tel que je vois les choses, $N, nous avons deux options pour quitter cette grotte.$B$BNous pouvons nous éclipser discrètement, j\'utilise la cape que vous m\'avez donnée et vous vous débrouillez. Ou alors, et ça risque d\'être plus dur vu mes blessures, on se bat dos à dos.$B$BJe vous laisse décider, vous connaissez vos forces mieux que moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3214;
UPDATE locales_npc_text SET entry = 3218, Text0_0_loc2 = 'Salut, $g mon garçon : jeune fille;, si vous cherchez les meilleures affaires en dehors de Forgefer, c\'est ici que ça se passe !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3218;
UPDATE locales_npc_text SET entry = 3233, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous vu les élémentaires d\'eau souillés dans notre lac, $N ? C\'est pénible à voir, mais j\'ignore d\'où ils viennent.$B$BVotre nom a été mentionné dans des conversations passées, peut-être pourrez-vous m\'aider à régler ce problème.$B$BNous devons d\'abord apprendre qui contrôle ces élémentaires.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3233;
UPDATE locales_npc_text SET entry = 3234, Text0_0_loc2 = 'Salut ! Je suis Gubber Blump.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3234;
UPDATE locales_npc_text SET entry = 3253, Text0_0_loc2 = '… à la poêle, à la friture, ou sautés...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3253;
UPDATE locales_npc_text SET entry = 3273, Text0_0_loc2 = 'Les liquides et mixtures semblent entrer en ébullition tandis que vous approchez. À côté des équipements traîne une pince qui paraît convenir pour la manipulation d\'échantillons de Limon.$B$BIl y a aussi un équipement de protection, mais il paraît poussiéreux et peu utilisé.$B$BToucher quoi que ce soit dans ce lieu ne paraît pas particulièrement avisé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3273;
UPDATE locales_npc_text SET entry = 3293, Text0_0_loc2 = 'Bonjour, $N. Bienvenue au refuge de l\'Ornière.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3293;
UPDATE locales_npc_text SET entry = 3294, Text0_0_loc2 = 'Veuillez excuser Skuerto, $N. Il ne voulait pas être grossier, mais il est très occupé. Attendons qu\'il ait fini ses explorations et travaillons en attendant sur quelque chose de productif, d\'accord ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3294;
UPDATE locales_npc_text SET entry = 3295, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut $N !$B$BJe dois travailler à mes mécanismes, mais au bout du compte... Je vais avoir besoin de quelqu\'un pour me rapporter des œufs de dragon !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3295;
UPDATE locales_npc_text SET entry = 3296, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Steppes ardentes sont un endroit dangereux, mais les érudits dans mon genre feraient n\'importe quoi pour augmenter leurs connaissances.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3296;
UPDATE locales_npc_text SET entry = 3297, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3297;
UPDATE locales_npc_text SET entry = 3298, Text0_0_loc2 = null, Text0_1_loc2 = 'Kar Aman, $N. Vous avez l\'air d\'$gun:une:c; solide $c. Mais faites attention dans cette zone. Toucher des choses dont vous ne savez rien, montrer de l\'irrespect à vos anciens, cela peut être sévèrement sanctionné.$B$BVous pouvez ne pas nous respecter, mes compagnons et moi, mais vous apprendrez à respecter notre puissance si vous vous conduisez mal.$B$BÀ présent, assez de politesses, puis-je faire quelque chose pour vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3298;
UPDATE locales_npc_text SET entry = 3299, Text0_0_loc2 = null, Text0_1_loc2 = 'Ah, un collègue démoniste qui a prouvé sa valeur, au moins jusqu\'à un certain point.$B$BC\'est un bonheur de faire votre connaissance. Mon nom est Menara Taillevide... Ça me va plutôt bien.$B$BVous semblez avoir assez de puissance et d\'éducation pour que d\'autres outils... des outils très puissants... puissent vous être de quelque utilité. Si tel est le cas, n\'hésitez pas à m\'en faire part. Sinon, le chemin des docks et de l\'océan vous est connu je crois ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3299;
UPDATE locales_npc_text SET entry = 3301, Text0_0_loc2 = 'Au cours des décennies passées, lors des guerres anciennes, notre espèce a presque entièrement disparu. Nous avons beaucoup souffert, $R.$B$BLe massacre de la Légion ardente. L\'Âme du démon. Les Aspects divisés. L\'ascension de l\'Aile de mort. L\'emprisonnement de notre reine Alexstrasza par les Gueules-de-dragon.$B$BTout ce temps, nous sommes demeurés la volonté inexpugnable des dragons, la force inextinguible qui a combattu pour assurer notre subsistance dans le monde.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3301;
