UPDATE locales_npc_text SET entry = 3311, Text0_0_loc2 = 'Ne l\'approchez pas davantage. Je sens d\'où je suis le mal courir dans le sceau.$B$BDonnez-moi un moment pour inspecter l\'objet. Tenez-le fermement.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3311;
UPDATE locales_npc_text SET entry = 3321, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3321;
UPDATE locales_npc_text SET entry = 3334, Text0_0_loc2 = '$N, je suis venu pour évaluer les insidieux effets des gangremousses, qui se ressentent dans tout Sombrivage. La corruption de Teldrassil et des terres avoisinantes inquiète beaucoup les druides du Cercle cénarien et tous les elfes de la nuit.$B$BMon but est de comprendre pourquoi cela a eu lieu et d\'y mettre fin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3334;
UPDATE locales_npc_text SET entry = 3337, Text0_0_loc2 = 'Une lame n\'est jamais trop aiguisée et une armure jamais trop solide, c\'est pour cela qu\'on aura toujours besoin d\'enchanteurs talentueux. Comme Taladan, par exemple, qui peut vous montrer comment enchanter correctement votre arme et votre armure pour les améliorer. Cherchez-le à la Terrasse des artisans.', Text0_1_loc2 = 'Une lame n\'est jamais trop aiguisée et une armure jamais trop solide, c\'est pour cela qu\'on aura toujours besoin d\'enchanteurs talentueux. Comme Taladan, par exemple, qui peut vous montrer comment enchanter correctement votre arme et votre armure pour les améliorer. Cherchez-le à la Terrasse des artisans.', Text1_0_loc2 = 'Merci pour la flatterie, mais ça ne m\'intéresse pas. Quoi ? Vous voulez savoir où apprendre les enchantements ? Oh, c\'est très embarrassant. Ma foi, vous pourriez... Vous pouvez sûrement... Heu... Taladan ! C\'est ça, voyez Taladan à la Terrasse des artisans !', Text1_1_loc2 = 'Merci pour la flatterie, mais ça ne m\'intéresse pas. Quoi ? Vous voulez savoir où apprendre les enchantements ? Oh, c\'est très embarrassant. Ma foi, vous pourriez... Vous pouvez sûrement... Heu... Taladan ! C\'est ça, voyez Taladan à la Terrasse des artisans !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3337;
UPDATE locales_npc_text SET entry = 3357, Text0_0_loc2 = 'Bienvenue dans mon échoppe, $c. Qu\'est-ce que les frères Beaupiqué peuvent faire pour vous ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3357;
UPDATE locales_npc_text SET entry = 3358, Text0_0_loc2 = 'Ah, un de mes clients préférés ! Vous avez l\'air d\'un démoniste de quelque puissance, $N. C\'est un plaisir de vous rencontrer. Si je puis quoi que ce soit, veuillez me le dire.$B$BMon frère et moi pensons que c\'est grâce aux gens comme vous que nous connaîtrons une nouvelle grande époque. Une telle puissance... Un tel contrôle... Ce n\'est qu\'une question de temps avant que la Légion elle-même ne s\'incline devant vous.$B$BJe suis un peu jaloux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3358;
UPDATE locales_npc_text SET entry = 3375, Text0_0_loc2 = null, Text0_1_loc2 = 'Ah, $r... J\'ai une vaste connaissance du monde et je sais créer des charmes d\'une grande puissance.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3375;
UPDATE locales_npc_text SET entry = 3377, Text0_0_loc2 = null, Text0_1_loc2 = 'Mau\'ari sait pourquoi vous la cherchez...$B$BApportez-moi l\'E\'ko que vous avez trouvé et je le transformerai pour vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3377;
UPDATE locales_npc_text SET entry = 3380, Text0_0_loc2 = 'Oui, oui, je sais, Dadanga n\'aime pas la fumée. Cela dit, au nez, Dadanga n\'aime pas les bains non plus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3380;
UPDATE locales_npc_text SET entry = 3382, Text0_0_loc2 = null, Text0_1_loc2 = 'La Cache de Mau\'ari détient de grands secrets... Les éclats utilisés dans la fabrication ont des propriétés réflexives et vous pouvez utiliser sa surface lumineuse pour siphonner la puissance d\'autres choses et les réfléchir sur vous-même.$B$BGardez le charme dans votre inventaire pour collecter l\'e\'ko, la puissance spirituelle des bêtes que vous combattez. Quand vous trouverez de l\'e\'ko, rapportez-en à Mau\'ari.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3382;
UPDATE locales_npc_text SET entry = 3383, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'E\'ko est le pouvoir que détient chaque créature. Le charme que je vous ai fait vous permet de capturer cette essence en une manifestation physique.$B$BJe connais une incantation qui peut transformer l\'e\'ko que vous trouvez et en faire du juju.$B$BVous pouvez ensuite apporter la Cache de Mau\'ari et l\'utiliser pour réfléchir la puissance du juju sur vous-même.$B$BAllez, essayez ! Apportez l\'e\'ko à Mau\'ari et on va bien s\'amuser.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3383;
UPDATE locales_npc_text SET entry = 3395, Text0_0_loc2 = 'Bien le bonjour jeune $g homme:fille;. En quoi puis-je vous être utile ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3395;
