UPDATE locales_npc_text SET entry = 3412, Text0_0_loc2 = 'Grond dit que je suis un peu simple parce que je ne suis pas rapide. Je veux juste faire le boulot correctement. Ça prend du temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3412;
UPDATE locales_npc_text SET entry = 3413, Text0_0_loc2 = 'Je peux vous montrer ce que vous voulez savoir, mais il faut de la patience. Prenez le temps quand vous travaillez l\'acier de sentir comment il prend forme sous votre marteau. Faites un avec l\'acier, et alors vous comprendrez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3413;
UPDATE locales_npc_text SET entry = 3414, Text0_0_loc2 = 'Vous apprenez vite, c\'est une bonne chose. Faites le travail correctement, mettez-y le temps qu\'il faut. Mais je crains de devoir vous envoyer à Grond si vous voulez progresser. Apprenez ce qu\'il a à enseigner mais n\'écoutez pas ses grommellements. Il est comme ça quand les filles de Saru se moquent de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3414;
UPDATE locales_npc_text SET entry = 3415, Text0_0_loc2 = 'J\'écoute, j\'écoute encore, mais je n\'entends pas les esprits dont parle Karn.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3415;
