UPDATE locales_npc_text SET entry = 3424, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'E\'ko est la forme non focalisée du pouvoir surnaturel, Juju est son essence concentrée. Grâce à une intense incantation, je peux transformer l\'E\'ko en Juju. Une fois qu\'il est Juju, vous pouvez utiliser la Cache de Mau\'ari pour réfléchir sur vous ses pouvoirs.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3424;
UPDATE locales_npc_text SET entry = 3425, Text0_0_loc2 = null, Text0_1_loc2 = 'Quelle surprise que quelqu\'un d\'aussi peu malin que vous ait pu aller si loin...$B$BOui, vous aurez un autre charme, mais Mau\'ari n\'apprécie pas beaucoup vos agissements !$B$BPartez, maintenant.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3425;
