UPDATE locales_npc_text SET entry = 3454, Text0_0_loc2 = 'Je pensais que vous aviez du sang de forgeron, et j\'avais raison. Allez à Forgefer, à la Grande Forge, et trouvez donc un nain du nom de Bengus Forge-profonde. Mon professeur et l\'un des plus grands forgerons que j\'aie jamais vu !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3454;
UPDATE locales_npc_text SET entry = 3456, Text0_0_loc2 = 'La vie d\'un forgeron n\'est pas simple, mais ça met de l\'acier dans les os et du feu dans le cœur ! Mon père était forgeron, et mon grand-père avant lui, dans les forges sous Grim Batol. Et maintenant mon fils suit mes traces, je suis bien fier de lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3456;
