UPDATE locales_npc_text SET entry = 3460, Text0_0_loc2 = 'On apprend vite, à ce que je vois, et vous avez quelques bases comme forgeron. Je vous embauche quand vous voulez. Mais je vois que vous voulez encore en apprendre plus. Allez parler à Bengus Forge-profonde, ce n\'est pas quelqu\'un de facile mais c\'est l\'un des meilleurs maîtres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3460;
UPDATE locales_npc_text SET entry = 3461, Text0_0_loc2 = null, Text0_1_loc2 = 'Ne jaillissez pas des ombres comme ça ! La prochaine fois, prévenez les jeunes filles au lieu de les terrifier !', Text1_0_loc2 = null, Text1_1_loc2 = 'Depuis que la pénombre est tombée sur le bois, je ne dors plus. Les sons fantomatiques de hurlements dans le vent m\'en empêchent. On dirait presque des agneaux qui crient, ils ne s\'arrêtent jamais.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3461;
