UPDATE locales_npc_text SET entry = 3466, Text0_0_loc2 = 'Si je montre ma valeur à Saru il n\'aura pas d\'autre choix que de me laisser combattre pour une des ses filles. Et rira bien qui rira le dernier.', Text0_1_loc2 = null, Text1_0_loc2 = 'Restez à l\'écart de Sumi et Tumi. Elles... ne sont pas libres.', Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3466;
UPDATE locales_npc_text SET entry = 3467, Text0_0_loc2 = 'Revenez me montrer un travail aussi lamentable et je vous renverrai chez vous dans une petite boîte en fer trempé à l\'urine de chèvre. Allez voir cet idiot d\'Ug\'thok, maintenant, il vous conviendra mieux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3467;
UPDATE locales_npc_text SET entry = 3468, Text0_0_loc2 = 'Alors vous pensez mériter mon enseignement ? L\'enseignement du grand Grond ? Vous n\'êtes qu\'un ver rampant sous la semelle de ma botte. Mais il est possible que m\'occuper d\'un vermisseau me rendrait plus attirant auprès de Sumi et Tumi. Voyons quelles maigres compétences vous possédez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3468;
UPDATE locales_npc_text SET entry = 3470, Text0_0_loc2 = 'Je n\'ai plus rien à vous apprendre. Allez-vous-en ! Et n\'approchez pas de Saru, quelqu\'un d\'aussi médiocre que vous lui servirait au mieux de réservoir de sang pour tremper son acier.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3470;
