UPDATE locales_npc_text SET entry = 3484, Text0_0_loc2 = 'Je n\'ai pas de mot assez fort pour décrire votre travail, vous savez faire des choses extraordinaires avec un marteau et une enclume ! Maintenant, ne dites à personne que je vous ai suggéré de prendre un gobelin comme maître, mais c\'est exactement ce que je vais faire. Il s\'appelle Brikk Keencraft, et la dernière fois que j\'ai entendu parler de lui, il tenait une échoppe à Baie-du-Butin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3484;
UPDATE locales_npc_text SET entry = 3485, Text0_0_loc2 = 'Ce n\'est pas un jeu ! Allez parler à Grond, dehors. Il est peut-être un serpent dans ma maison, mais au moins il sait se servir d\'une enclume.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3485;
UPDATE locales_npc_text SET entry = 3487, Text0_0_loc2 = 'Je vois que vous maîtrisez mieux que moi les secrets de l\'acier. C\'est bien, car ces secrets sont à prendre. Maintenant vous devez vous diriger vers la jungle au sud des terres humaines, jusqu\'à la ville gobeline de Baie-du-Butin. Vous y trouverez un gobelin nommé Brikk Keencraft. Je n\'aime pas les gobelins mais sa connaissance de l\'acier n\'a pas son pareil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3487;
UPDATE locales_npc_text SET entry = 3488, Text0_0_loc2 = 'Ouais ? Bien sûr que je suis forgeron. Vous pensez que seuls les nains savent se servir d\'une enclume ? Comme vous voulez ! Allez chercher un nain pour vous aider !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3488;
