UPDATE locales_npc_text SET entry = 3490, Text0_0_loc2 = 'Vous avez l\'air de croire que c\'est le plus beau bout de métal que j\'aie jamais vu. C\'est correct, mais je vois des trucs corrects tous les jours. J\'vais vous dire, si vous réussissez à m\'épater, je vous enseignerai des trucs qui font sourire les nains dans leur sommeil.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3490;
UPDATE locales_npc_text SET entry = 3493, Text0_0_loc2 = 'Je rechigne à l\'admettre, mais l\'ours a pris le dessus. Par tous les diables, quel ours, mes amis !$b$bSi vous pouvez l\'abattre, vous aurez le droit de vous féliciter ! Il a donné du fil à retordre à beaucoup de nos meilleurs chasseurs.$b$b<Il vous donne une tape dans le dos.>$b$bTriomphez de l\'ours et nous vous prendrons comme membre à part entière du pavillon des Pérégrins.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3493;
UPDATE locales_npc_text SET entry = 3494, Text0_0_loc2 = 'Ici, au pavillon des Pérégrins, nous apprécions surtout les compétences de chasseur. Vous voulez ma protection ? Il faut la mériter !$B$BVous avez l\'air à la hauteur de la tâche, $g mon gars:ma petite;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3494;
UPDATE locales_npc_text SET entry = 3495, Text0_0_loc2 = 'Vous êtes un chasseur de quelque talent, $N ! Vous avez prouvé vos mérites, vous avez également battu Daryl, ce qui n\'est pas rien selon moi !$B$BRevenez quand vous voulez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3495;
