UPDATE locales_npc_text SET entry = 3513, Text0_0_loc2 = 'Ils sont au Quartier de la magie. Cherchez-les dans le bâtiment effrayant en forme de crâne.', Text0_1_loc2 = 'Ils sont au Quartier de la magie. Cherchez-les dans le bâtiment effrayant en forme de crâne.', Text1_0_loc2 = 'On sait où ils sont ! Le bâtiment en forme de crâne, dans le Quartier de la magie. À l\'extérieur de la ville.', Text1_1_loc2 = 'On sait où ils sont ! Le bâtiment en forme de crâne, dans le Quartier de la magie. À l\'extérieur de la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3513;
UPDATE locales_npc_text SET entry = 3514, Text0_0_loc2 = 'Banque ? Dans tour centrale du Quartier des marchands. Pouvez pas la rater.', Text0_1_loc2 = 'Banque ? Dans tour centrale du Quartier des marchands. Pouvez pas la rater.', Text1_0_loc2 = 'Nous n\'avons qu\'un oeil et nous voyons mieux que vous ! La banque est au centre de Fossoyeuse !', Text1_1_loc2 = 'Nous n\'avons qu\'un oeil et nous voyons mieux que vous ! La banque est au centre de Fossoyeuse !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3514;
UPDATE locales_npc_text SET entry = 3515, Text0_0_loc2 = 'Michael Garrett sur anneau supérieur du Quartier des marchands.', Text0_1_loc2 = 'Michael Garrett sur anneau supérieur du Quartier des marchands.', Text1_0_loc2 = 'L\'homme chauve-souris sur niveau supérieur du Quartier des marchands.', Text1_1_loc2 = 'L\'homme chauve-souris sur niveau supérieur du Quartier des marchands.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3515;
UPDATE locales_npc_text SET entry = 3516, Text0_0_loc2 = 'Oui, vous chercher Christopher Drakul dans Quartier des marchands. Lui sur plate-forme est.', Text0_1_loc2 = 'Oui, vous chercher Christopher Drakul dans Quartier des marchands. Lui sur plate-forme est.', Text1_0_loc2 = 'Nous rejoindre la guilde ? Non ? D’accord, vous trouver maître des guildes sur plateforme est, dans Quartier des marchands.', Text1_1_loc2 = 'Nous rejoindre la guilde ? Non ? D’accord, vous trouver maître des guildes sur plateforme est, dans Quartier des marchands.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3516;
UPDATE locales_npc_text SET entry = 3517, Text0_0_loc2 = 'Aubergiste Norman sur cercle supérieur du Quartier des marchands, parlez-lui.', Text0_1_loc2 = 'Aubergiste Norman sur cercle supérieur du Quartier des marchands, parlez-lui.', Text1_0_loc2 = 'Pas de pitié pour les vilains, mais vous avoir l\'air de quelqu\'un de bien. Vous trouver aubergiste Norman sur anneau supérieur du Quartier des marchands.', Text1_1_loc2 = 'Pas de pitié pour les vilains, mais vous avoir l\'air de quelqu\'un de bien. Vous trouver aubergiste Norman sur anneau supérieur du Quartier des marchands.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3517;
UPDATE locales_npc_text SET entry = 3518, Text0_0_loc2 = 'Boîte aux lettres au centre de la ville, Quartier des marchands, anneau supérieur... À côté de l\'auberge.', Text0_1_loc2 = 'Boîte aux lettres au centre de la ville, Quartier des marchands, anneau supérieur... À côté de l\'auberge.', Text1_0_loc2 = 'Nous vouloir savoir si notre Trépa... Trépan... Trépaneuse a été vendue aux enchères. Vous trouver boîte aux lettres à côté auberge, sur anneau supérieur du Quartier des marchands.', Text1_1_loc2 = 'Nous vouloir savoir si notre Trépa... Trépan... Trépaneuse a été vendue aux enchères. Vous trouver boîte aux lettres à côté auberge, sur anneau supérieur du Quartier des marchands.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3518;
UPDATE locales_npc_text SET entry = 3519, Text0_0_loc2 = 'Nous avons hôtel des ventes dans anneau central.', Text0_1_loc2 = 'Nous avons hôtel des ventes dans anneau central.', Text1_0_loc2 = 'Anneau central. Circulez !', Text1_1_loc2 = 'Anneau central. Circulez !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3519;
UPDATE locales_npc_text SET entry = 3520, Text0_0_loc2 = 'Le petit gobelin au sommet du bâtiment, au nord des Ruines de Lordaeron. Quittez Fossoyeuse et allez-y.', Text0_1_loc2 = 'Le petit gobelin au sommet du bâtiment, au nord des Ruines de Lordaeron. Quittez Fossoyeuse et allez-y.', Text1_0_loc2 = 'On voit le ballon au nord des ruines. Il vous emporte vers des régions lointaines.', Text1_1_loc2 = 'On voit le ballon au nord des ruines. Il vous emporte vers des régions lointaines.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3520;
