UPDATE locales_npc_text SET entry = 3522, Text0_0_loc2 = null, Text0_1_loc2 = 'Récupérez cette tête, vous ne manquerez pas de flanquer une bonne trouille à Daryl. Ce n\'est pas qu\'il ne le mérite pas, ce bâtard arrogant. Mais ah, j\'ai hâte de voir sa tête quand vous lui montrerez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3522;
