UPDATE locales_npc_text SET entry = 3524, Text0_0_loc2 = 'Les voleurs sont dans le bâtiment à la périphérie du Quartier des voleurs.', Text0_1_loc2 = 'Les voleurs sont dans le bâtiment à la périphérie du Quartier des voleurs.', Text1_0_loc2 = 'Ils m\'ont bien appris à me faufiler. Vous les trouver dans le Quartier des voleurs, à la périphérie d\'Fossoyeuse.', Text1_1_loc2 = 'Ils m\'ont bien appris à me faufiler. Vous les trouver dans le Quartier des voleurs, à la périphérie d\'Fossoyeuse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3524;
UPDATE locales_npc_text SET entry = 3525, Text0_0_loc2 = null, Text0_1_loc2 = 'Un plaisir de vous revoir, $N. Merci pour cette partie de rigolade, et ne vous en faites pas pour Daryl, ça lui pendait au nez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3525;
UPDATE locales_npc_text SET entry = 3526, Text0_0_loc2 = 'Maître des démonistes ? Dans le Quartier de la magie, dans le bâtiment avec les mages, à la périphérie.', Text0_1_loc2 = 'Maître des démonistes ? Dans le Quartier de la magie, dans le bâtiment avec les mages, à la périphérie.', Text1_0_loc2 = 'Nous vouloir un gangrechien comme familier. On le nourrirait, on le traierait bien, on jouerait à aller chercher le bâton. Les démonistes sont dans le Quartier de la magie, sur l\'anneau extérieur.', Text1_1_loc2 = 'Nous vouloir un gangrechien comme familier. On le nourrirait, on le traierait bien, on jouerait à aller chercher le bâton. Les démonistes sont dans le Quartier de la magie, sur l\'anneau extérieur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3526;
