UPDATE locales_npc_text SET entry = 3532, Text0_0_loc2 = 'Franklin Lloyd dans Quartier des voleurs, périphérie de Fossoyeuse.', Text0_1_loc2 = 'Franklin Lloyd dans Quartier des voleurs, périphérie de Fossoyeuse.', Text1_0_loc2 = 'Eh, les bombes, on aime les bombes. Graham Van Talen montre comment les faire. Il est à la périphérie du Quartier des voleurs.', Text1_1_loc2 = 'Eh, les bombes, on aime les bombes. Graham Van Talen montre comment les faire. Il est à la périphérie du Quartier des voleurs.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3532;
