UPDATE locales_npc_text SET entry = 3536, Text0_0_loc2 = 'Il est au centre du Quartier des voleurs.', Text0_1_loc2 = 'Il est au centre du Quartier des voleurs.', Text1_0_loc2 = 'Arthur Moore est au centre, au Quartier des voleurs. C\'est lui qui apprend le travail du cuir.', Text1_1_loc2 = 'Arthur Moore est au centre, au Quartier des voleurs. C\'est lui qui apprend le travail du cuir.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3536;
