UPDATE locales_npc_text SET entry = 3539, Text0_0_loc2 = 'Vous cherchez Josef Gregorian. Il est dans le Quartier de la magie, au centre, du côté du canal vert.', Text0_1_loc2 = 'Vous cherchez Josef Gregorian. Il est dans le Quartier de la magie, au centre, du côté du canal vert.', Text1_0_loc2 = 'Hmmm, c\'est une bonne idée. On pourrait peut-être y aller pour profiter d\'un peu de raccommodage ? Il coud bien. Vous trouverez Josef Gregorian au centre du Quartier de la magie.', Text1_1_loc2 = 'Hmmm, c\'est une bonne idée. On pourrait peut-être y aller pour profiter d\'un peu de raccommodage ? Il coud bien. Vous trouverez Josef Gregorian au centre du Quartier de la magie.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3539;
UPDATE locales_npc_text SET entry = 3540, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, OK ! Vous voici donc, $N. C\'est le moins que je puisse faire pour vous remercier de votre aide !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3540;
