UPDATE locales_npc_text SET entry = 3543, Text0_0_loc2 = 'Qu\'est-ce que vous cherchez ?', Text0_1_loc2 = 'Qu\'est-ce que vous cherchez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3543;
UPDATE locales_npc_text SET entry = 3545, Text0_0_loc2 = 'Écoutez les histoires d\'un ancien. $N. J\'en ai beaucoup à raconter…', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3545;
UPDATE locales_npc_text SET entry = 3546, Text0_0_loc2 = null, Text0_1_loc2 = 'Bienvenue au refuge, $N. Certains viennent ici avec un but, d\'autres par accident... Mais tout le monde semble avoir une histoire unique à raconter.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3546;
UPDATE locales_npc_text SET entry = 3550, Text0_0_loc2 = 'Grrr...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3550;
UPDATE locales_npc_text SET entry = 3553, Text0_0_loc2 = 'Je viens du sud pour chercher de l\'aide à la Croisée, $c, mais je n\'y trouve que des lâches qui me tournent le dos. Même le puissant Thork m\'insulte de cette manière. C\'est un bon chef, mais son rejet de ma requête me cause une grande colère.$B$BIl prétend que ma rage me plonge dans la précipitation, qu\'elle obscurcit mon jugement, mais je ne connaîtrai pas le repos avant d\'avoir exercé ma vengeance !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3553;
UPDATE locales_npc_text SET entry = 3554, Text0_0_loc2 = 'Je vous remercie, $N. Votre dévouement procure le repos à l\'âme de ma défunte femme et pour cela je ne pourrai jamais vous exprimer ma reconnaissance.$B$BJe ne crois pas que ma haine pour les hurans cessera jamais, mais pour l\'instant elle est apaisée.$B$BJe dois maintenant reconstruire ma vie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3554;
UPDATE locales_npc_text SET entry = 3557, Text0_0_loc2 = null, Text0_1_loc2 = 'Le corps est celui d\'une guerrière orque.$B$BElle est visiblement morte au combat, sans doute contre les hurans de la région.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3557;
UPDATE locales_npc_text SET entry = 3558, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous retournez le corps et voyez des traces de coups d\'épée et de sorts offensifs sur l\'orque morte. Son armure est détruite, ses cheveux emmêlés lui font comme une mantille.$B$BD\'après la description de Mankrik, c\'était à l\'évidence sa femme.$B$BIl sera bouleversé par cette nouvelle, mais vous pensez qu\'il faut lui dire la vérité.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3558;
UPDATE locales_npc_text SET entry = 3559, Text0_0_loc2 = 'Bienvenue à la Croisée, $c. Mon nom est Thork. Je dirige les défenses locales au nom du Chef de guerre.$B$BConsidérez-vous ici comme chez vous. Aussi longtemps que vous vous occupez de vos affaires, personne ne verra à mal que vous soyez ici.$B$BLes menaces contre notre sécurité sont nombreuses ici. Si vous connaissez déjà la disposition du terrain, allez parler aux gens autour de vous. Ils ont peut-être des choses à vous faire faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3559;
UPDATE locales_npc_text SET entry = 3560, Text0_0_loc2 = '<Sangredent crache.>$B$BJe suis un guerrier de la Horde, $N. Cela me remplit de rage de savoir que nos ennemis sont calfeutrés dans une forteresse à moins d\'une journée de cheval de Kargath et complotent contre nous.$B$BJe rêve d\'être libéré de mes devoirs et de pouvoir aller les détruire au nom du Chef.$B$BMais nous avons des rôles à tenir, hélas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3560;
UPDATE locales_npc_text SET entry = 3561, Text0_0_loc2 = 'Je vous envie vraiment : vous battre au nom de la Horde, au cœur du conflit. Moi, je dois commander.$B$BDans ma jeunesse, bien sûr, j\'ai vécu ces rêves qui rendent mon vieil âge supportable. Je me délecte à présent de la gloire de la victoire et du goût amer de la défaite au travers des soldats que je commande.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3561;
UPDATE locales_npc_text SET entry = 3562, Text0_0_loc2 = 'Je rêve, $N... oh oui, je rêve. Dans mes rêves je meurs mille fois de mille manières différentes. Certains appelleraient ces rêves des cauchemars. Pas moi ! Dans mes rêves, je meurs dans la fumée des combats. Pour chacune de mes morts, dix mille ennemis de la Horde tombent à mes pieds. Quelle gloire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3562;
UPDATE locales_npc_text SET entry = 3563, Text0_0_loc2 = 'Je le savais rien qu\'à vous regarder, $N. Vous me rappelez ce que j\'étais à mes débuts, quand j\'étais jeune guerrier de la Horde.$B$BVous irez loin dans la vie, et plus loin encore dans la mort.$B$B<Sangredent vous salue.>', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3563;
UPDATE locales_npc_text SET entry = 3564, Text0_0_loc2 = 'Et modeste avec ça... Votre heure est peut-être venue, $N.$B$BCroyez-vous pouvoir entrer dans la forteresse des orcs Rochenoire ? Y porter le chaos ? Ébranler les fondations de la citadelle des montagnes ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3564;
UPDATE locales_npc_text SET entry = 3565, Text0_0_loc2 = 'Je n\'ai pas douté de vous une seconde, $N.$B$BVoici les ordres, $N. Je sais que vous ne me laisserez pas tomber.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3565;
UPDATE locales_npc_text SET entry = 3566, Text0_0_loc2 = 'Ah, un visiteur ! Sans doute en quête d’aventures. Nous ne voyons pas beaucoup de touristes marcher sur ce chemin. Attrapez une chaise et installez-vous confortablement. Vous avez tout l’air d’avoir de la chair sur les os ; ne seriez-vous pas là pour participer aux compétitions de combat en cage ?', Text0_1_loc2 = 'Ah, un visiteur ! Sans doute en quête d’aventures. Nous ne voyons pas beaucoup de touristes marcher sur ce chemin. Attrapez une chaise et installez-vous confortablement. Vous avez tout l’air d’avoir de la chair sur les os ; ne seriez-vous pas là pour participer aux compétitions de combat en cage ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3566;
UPDATE locales_npc_text SET entry = 3573, Text0_0_loc2 = 'Salutations, jeune $c.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3573;
UPDATE locales_npc_text SET entry = 3574, Text0_0_loc2 = 'Rend, fils de Main-noire : chef impuissant du clan Rochenoire. Il n\'est pas d\'ordure plus bornée.$B$BÀ la fin de la Deuxième Guerre, Rend et son frère Maim s\'enfuirent pour trouver refuge dans la forteresse montagnarde du pic Rochenoire. C\'est là que Rend se proclama Chef de guerre d\'un clan Rochenoire affaibli.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3574;
UPDATE locales_npc_text SET entry = 3575, Text0_0_loc2 = 'Oui, c\'est certain, mais inutile de tenter de me convaincre, le Chef de guerre et moi avons une longue histoire commune. Mais je note votre dévouement.$B$BComme je disais, Rend avait trouvé un nouveau foyer pour son peuple et j\'étais parmi ceux qui le suivaient avec lassitude.$B$BChaque conflit en entraînait d\'autres... innombrables... C\'est au cours de ces combats du pic Rochenoire entre le clan Rochenoire et les nains Sombrefer que mes fils furent tous deux tués. Je réalisai alors que mon temps au sein du clan Rochenoire était achevé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3575;
UPDATE locales_npc_text SET entry = 3576, Text0_0_loc2 = 'Je suis parti. Je ne voulais pas rester avec le clan Rochenoire. Non, la mort de mes enfants n\'allait pas être inutile. Je n\'allais pas continuer à servir un maniaque incompétent et avide de pouvoir comme Rend.$B$BJe me suis abrité dans les bois de Lordaeron, vivant en ermite près d’Âtreval.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3576;
UPDATE locales_npc_text SET entry = 3577, Text0_0_loc2 = '<Eitrigg rit.>$B$BMon enfant, le monde n\'a pas toujours été plongé dans un tel chaos. Il fut un temps, avant le Fléau, où cette zone de Lordaeron n\'était pas la terre désolée nommée Maleterres.$B$BC\'est là que Thrall me sauva d\'une mort certaine et que je rejoignis la nouvelle Horde... Mais je m\'égare. Cette histoire sera pour une autre fois. Oui, une autre fois... Peut-être...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3577;
UPDATE locales_npc_text SET entry = 3578, Text0_0_loc2 = 'J\'en suis certain. Un jour peut-être pourrez-vous porter un message à un de mes vieux amis. Il paraît qu\'il vit toujours dans ces bois.$B$B<Eitrigg courbe la tête un moment, comme en oraison.>$B$BAlors Rend, oui... Rend et son frère Maim régnaient sur le Pic, en conflit permanent avec les nains Sombrefer. Ils parvinrent à repousser les nains dans la zone supérieure de la cité, mais seulement après la venue des dragons et la mort de Maim.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3578;
UPDATE locales_npc_text SET entry = 3579, Text0_0_loc2 = 'Oui. Nefarian, descendant de l\'Aile de mort, dirige désormais le Pic. Rend est sous le contrôle du dragon.$B$BSi seulement vous parveniez à passer par les Halls de l\'Ascension.$B$B<Eitrigg soupire.>$B$BHélas, seuls ceux qui sont jugés dignes par les dragons noirs gagnent le droit d\'y monter.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3579;
UPDATE locales_npc_text SET entry = 3580, Text0_0_loc2 = 'Peut-être. Allez maintenant, mon enfant. Cherchez conseil auprès du Chef. Il voudra sûrement en savoir plus sur votre découverte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3580;
UPDATE locales_npc_text SET entry = 3583, Text0_0_loc2 = 'La Vallée des épreuves transformera même le plus timoré en un guerrier susceptible de rejoindre la Horde. Ceux qui ne peuvent affronter les épreuves verront leurs os blanchir sur les pistes du désert.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3583;
UPDATE locales_npc_text SET entry = 3584, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $N. Mon nom est Chronormu, mais vous pouvez m\'appeler Chromie. Ces familiarités ne me dérangent pas.$B$BComment ai-je appris votre nom ? Étant l\'un des dragons de bronze, je suis liée au fleuve du temps, et en quelque sorte nous nous sommes déjà rencontrés. Nous nous rencontrerons encore.$B$BJ\'espère que le choix de mon apparence physique ne vous perturbe pas. Gardien des lieux où la préservation du cours du temps est le plus important, j\'ai tout de même choisi de ne pas m\'immerger dans les douleurs de l\'endroit.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3584;
UPDATE locales_npc_text SET entry = 3585, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous salue à nouveau, $N. Je sens que le fleuve du temps reste menacé par des forces extrêmement puissantes. Votre aide permanente est toujours la bienvenue pour les dragons de bronze.$B$BLe passé est inébranlable, mais le futur fluctue en permanence. Les décisions que vous prenez ici et maintenant aident à guider ces changements.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3585;
UPDATE locales_npc_text SET entry = 3586, Text0_0_loc2 = null, Text0_1_loc2 = 'Écoutez vos anciens, $N. Si vous avez assez de force et de persévérance, il n\'est pas de pouvoir que vous ne puissiez contrôler.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3586;
UPDATE locales_npc_text SET entry = 3587, Text0_0_loc2 = null, Text0_1_loc2 = 'Tous les Réprouvés ne sont pas dirigés par la volonté de Sylvanas, $N. Certains d\'entre nous sont conduits par la vraie puissance, une puissance que nous avons gagnée par nous-mêmes.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3587;
UPDATE locales_npc_text SET entry = 3589, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3589;
UPDATE locales_npc_text SET entry = 3590, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'affaire qui vous amène doit avoir grande importance pour vous, $c, pour vous conduire si loin dans le marais...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3590;
UPDATE locales_npc_text SET entry = 3591, Text0_0_loc2 = null, Text0_1_loc2 = 'L\'infernal est l\'un des plus puissants démons qu\'un démoniste puisse rêver d\'avoir à ses côtés au combat. Cette créature est forte, bien protégée, et assène des coups dévastateurs à n\'importe quel ennemi. Si vous décidez de placer un esprit d\'infernal dans votre arme, vous bénéficierez d\'une partie de ces avantages : meilleure santé, régénération, et tout le reste.$B$BSi vous voulez en savoir plus sur les gangrechiens, parlez à Wytula qui se trouve en face de moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3591;
UPDATE locales_npc_text SET entry = 3592, Text0_0_loc2 = null, Text0_1_loc2 = 'Le gangrechien est vraiment une créature vicieuse. Physiquement, il n\'est pas aussi solide qu\'un infernal, mais un démoniste sage sait que la force brute n\'est pas toujours la réponse. Sinon, vous auriez choisi la voie du guerrier, n\'est-ce pas ?$B$BLe gangrechien draine le mana de ses adversaires, c\'est cela, ainsi que sa rapidité, qui lui permet de les abattre. Sa force provient des arcanes et de sa manière de les utiliser à ses fins.$B$BSi vous voulez en savoir plus sur les infernaux, parlez à Magaz qui se trouve en face de moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3592;
UPDATE locales_npc_text SET entry = 3594, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous parlé à l\'un de mes acolytes dans la tour, $N ? Nous ne pouvons achever votre Orbe jusqu\'à ce que vous ayez accompli leurs tâches. Parlez-leur maintenant, si vous vous sentez d\'attaque.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3594;
UPDATE locales_npc_text SET entry = 3633, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3633;
UPDATE locales_npc_text SET entry = 3634, Text0_0_loc2 = 'Oui, je parle un langage que vous comprenez, $r. Nous ne sommes pas des animaux, mais votre espèce a tenté de nous réduire en esclavage comme des bêtes. Cela dit, nos différends ne sont rien face au drame que vit cette terre. Je vous en prie, oubliez les conflits qui vous opposent aux miens et écoutez ma dernière volonté d\'agonisant.$B$BJe suis venu ici pour faire le bien dont j’étais capable, et cela n\'aura servi à rien si vous ne m\'aidez pas. Je vous demande votre aide dans mon dernier souffle de vie.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3634;
UPDATE locales_npc_text SET entry = 3635, Text0_0_loc2 = '…$B$BLe corps du druide tauren ne bouge plus. Il semble avoir finalement quitté cette terre vers le grand au-delà promis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3635;
UPDATE locales_npc_text SET entry = 3653, Text0_0_loc2 = 'Je suis un officiel de la ville, je travaille pour le cens de Fossoyeuse. Depuis que nous sommes libérés du Fléau, nous nous efforçons de recenser la population avec exactitude. Par les méthodes des arcanes et une grande discipline mentale, je peux accéder à ces archives. Si vous devez faire affaire avec moi, continuez. Vous devez le savoir, je suppose.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3653;
UPDATE locales_npc_text SET entry = 3654, Text0_0_loc2 = 'Le Chef de guerre m\'a ordonné d\'étudier toutes les sortes d\'armes et d\'armures. Il m\'a envoyé ici, au creuset des cultures tauren et orc, dans les Tarides, pour y rencontrer autant de voyageurs que possible et pour découvrir les cultures des Tarides.$B$BIl pense qu\'il y a quelque chose à apprendre même des cultures les plus pathétiques... comme celle des hurans ou des centaures.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3654;
UPDATE locales_npc_text SET entry = 3656, Text0_0_loc2 = 'Vous ! Venez ici, $r ! <grogne>$B$BLe temps passe vite et ma fin <grogne> est proche. Mutiledent vaincra, vous verrez. <grogne>$B$BMa capture peut toujours aider <grogne> mon peuple. La tribu Tranchecrin est plus puissante que celle des Dos-hirsute !$B$BLa Horde veut découvrir qui mène les raids contre son peuple. Vous <grogne> aiderez Mutiledent en paiement des informations que je suis seul à connaître. Mutiledent peut vous aider ! <grogne> Mais vous devez aider Mutiledent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3656;
UPDATE locales_npc_text SET entry = 3657, Text0_0_loc2 = 'Bienvenue à Hurlevent ! Je suis un officiel de la ville, j\'aide les citoyens depuis le bureau de cens de la ville. Si vous avez besoin de trouver quelque chose de spécifique, je peux peut-être vous aider. Par les méthodes des arcanes et une grande discipline mentale, je peux accéder à ces archives.$B$BSi vous devez faire affaire avec moi, continuez.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3657;
UPDATE locales_npc_text SET entry = 3658, Text0_0_loc2 = 'Ma haine est puissante <grogne>, mais il est temps de vous raconter quelque chose, $r.$B$BIl y a longtemps, une bataille eut lieu entre les dieux. Agammagan, notre grand dieu sanglier, s\'effondra sur la terre après avoir été vaincu au cours de la Guerre des Anciens ; son sang tomba en pluie et ses os furent réduits en pièces. <grogne>$B$BDe grandes épines sortirent de la terre où son sang avait touché le sol et son crâne orne une des cavernes du sud.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3658;
UPDATE locales_npc_text SET entry = 3659, Text0_0_loc2 = 'Ouh... J\'ai besoin d\'une bouffarde et d\'un contact avec une jolie femelle gnome, pas forcément dans cet ordre !$B$BÇa fait des mois que je suis coincé dans le ventre de cette chose. On est quel jour d\'ailleurs ? Ah, peu importe, ce qui compte c\'est que je sois enfin dehors.$B$BYOUHOU !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3659;
UPDATE locales_npc_text SET entry = 3660, Text0_0_loc2 = 'Ma foi, c\'est une longue histoire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3660;
UPDATE locales_npc_text SET entry = 3661, Text0_0_loc2 = 'Très bien ! J\'étais donc dans les puits de la Travée de lave, je testais ma tenue de protection. Tout se passait bien. J\'ai même pu nager dans ce truc sans la moindre blessure !$B$BAlors me voilà, m\'occupant tranquillement de mes affaires, en dos crawlé, quand soudain ce truc m\'avale d\'un coup ! Sans ma tenue de protection et sa poche de nourriture intégrée, c\'en était fini de moi !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3661;
UPDATE locales_npc_text SET entry = 3662, Text0_0_loc2 = 'Je crois sentir une sorte de scepticisme dans votre voix. Mais oui, c\'est ce qui s\'est passé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3662;
UPDATE locales_npc_text SET entry = 3663, Text0_0_loc2 = 'Je ne suis pas hors de cet estomac depuis deux minutes et on me parle déjà d\'hygiène corporelle ? $g Mon gars:Ma fille;, je vous aurais claqué le beignet si ce n\'était pas vous qui m\'aviez secouru !$B$BEnfin, merci pour le coup de main. Je peux peut-être vous payer de retour ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3663;
UPDATE locales_npc_text SET entry = 3664, Text0_0_loc2 = 'Enfin, peut-être que "payer" n\'est pas le meilleur mot. Vous voyez, quand j\'étais dans le ventre de cette chose, j\'ai réfléchi :$B$BCette créature vit dans du magma en fusion et ne subit pas le moindre dégât !$B$BSi seulement je pouvais récupérer une peau en parfait état sur l\'une de ces bestioles... Je pourrais l\'apporter à mon cher ami Malyfous Darkhammer, à Long-guet. Si quelqu\'un pouvait faire quelque chose avec la peau d\'une bête pareille, ce serait bien lui !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3664;
UPDATE locales_npc_text SET entry = 3665, Text0_0_loc2 = 'Que penseriez-vous de porter un message pour moi ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3665;
UPDATE locales_npc_text SET entry = 3666, Text0_0_loc2 = 'Vous voulez acheter un cafard ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3666;
UPDATE locales_npc_text SET entry = 3668, Text0_0_loc2 = null, Text0_1_loc2 = 'Le temps m’est compté, comme à nous tous !', Text1_0_loc2 = null, Text1_1_loc2 = 'J’ai tant à faire, tant à faire avant que la peste n’arrive ici…', Text2_0_loc2 = null, Text2_1_loc2 = 'John, chéri, où êtes-vous ?', Text3_0_loc2 = null, Text3_1_loc2 = 'Qu’arrive-t-il à tout le monde ? J’ai… froid…', Text4_0_loc2 = null, Text4_1_loc2 = 'Chut... Vous ne sentez rien ?', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3668;
