UPDATE locales_npc_text SET entry = 3673, Text0_0_loc2 = 'La Confrérie du Thorium ne perd pas son temps avec des fourmis. Partez !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3673;
UPDATE locales_npc_text SET entry = 3674, Text0_0_loc2 = 'C\'est très simple pour un artisan de mon calibre.$B$BLe plus difficile, dans le traitement des peaux et des carapaces, c\'est de les trouver. Si vous pouviez m\'apporter une dépouille de cette bête en parfait état, je pourrais faire plusieurs puissants objets avec sa chair.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3674;
UPDATE locales_npc_text SET entry = 3675, Text0_0_loc2 = 'Je dois dire que les chances de trouver une telle pièce sont d\'une sur un million ! La chance d\'une vie ! Même l\'artisan le plus discipliné échouerait sans doute plusieurs centaines de fois en dépeçant la bête avant d\'obtenir un échantillon en parfait état.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3675;
UPDATE locales_npc_text SET entry = 3676, Text0_0_loc2 = 'Si vous m\'apportiez la peau de cette bête ? Ma foi... Laissez-moi vous montrer.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3676;
UPDATE locales_npc_text SET entry = 3677, Text0_0_loc2 = 'Catalogue de la Confrérie du Thorium :$B$BVous trouverez à l\'intérieur les meilleures affaires pour préparer vos aventures ! Nous vous montrerons même comment dénicher ces composants difficiles à trouver.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3677;
UPDATE locales_npc_text SET entry = 3693, Text0_0_loc2 = 'Le titre de ce livre apparaît en lettres ornées : Journal de Maureen Dalson.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3693;
UPDATE locales_npc_text SET entry = 3694, Text0_0_loc2 = 'Un journal aux pages jaunies est ouvert à sa dernière page :$B$B"Je ne sais pas ce qui va se passer maintenant que Harold a changé. J\'ai dû faire de gros efforts pour le mettre dans le seul endroit où je savais qu\'il serait enfermé pour de bon. J\'ai caché la clé sur moi, et je prie pour que cela fonctionne...$B$BJ\'espère pouvoir sortir d\'ici, mais il me faudrait de l\'argent pour cela... Nos affaires sont dans le cabinet, je pense... Et maintenant que j\'y pense, Harold en a la clé sur lui ?$B$BJe vais me reposer, je suis morte de fatigue..."', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3694;
UPDATE locales_npc_text SET entry = 3734, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3734;
UPDATE locales_npc_text SET entry = 3753, Text0_0_loc2 = 'Bienvenue sur le front, $c. Il est possible que les politiciens aiment à élaborer des plans secrets entre eux, mais ils ne pourront guère pratiquer ici ce genre de loisirs. Nous sommes sur la Barricade, et nous luttons chaque jour pour survivre.$B$BSi vous venez ici pour prouver votre valeur de héros, vous en aurez l\'occasion, surtout quand nous nous mettrons en route vers Andorhal et les forces maléfiques qui y contrôlent le Fléau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3753;
UPDATE locales_npc_text SET entry = 3754, Text0_0_loc2 = 'Bienvenue sur le front, $c. Le royaume de Hurlevent, avec l\'aide de ses alliés, m\'a envoyé ici pour contrer la menace croissante du Fléau. Ici, au camp du Noroît, nous luttons pour notre survie chaque jour.$B$BSi vous êtes $gvenu:venue; pour prouver votre valeur de héros, ce ne sont pas les occasions qui manqueront. Surtout lorsque nous nous mettrons en route vers Andorhal et les forces maléfiques qui y contrôlent le Fléau.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3754;
UPDATE locales_npc_text SET entry = 3755, Text0_0_loc2 = 'C\'est bon d\'en voir d\'autres qui combattent le Fléau à Stratholme, car c\'est là qu\'il est le plus fort.$B$BBonjour, je me nomme Aurius. J\'étais naguère un paladin de la Main d\'argent, mais comme d\'autres dont la foi a vacillé, j\'ai perdu la grâce. Le Fléau m\'a séduit, et m\'a presque transformé en chevalier de la mort !$B$BJ\'ai enfin senti le péril et j\'ai fui jusqu\'à cette chapelle. C\'est un lieu sacré où le premier paladin, Uther le Porteur de Lumière, fut adoubé dans l\'ordre de la Main d\'argent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3755;
UPDATE locales_npc_text SET entry = 3756, Text0_0_loc2 = 'Il ne me reste guère de temps dans ce monde, mais si la Lumière le veut, cela suffira pour que j\'accomplisse ma destinée. Lorsque vous affronterez le baron Rivendare, je serai avec vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3756;
UPDATE locales_npc_text SET entry = 3757, Text0_0_loc2 = 'Laissez-moi. Mes blessures sont profondes et je pense que je vais y rester. Merci, $N. Je vais mourir, mais grâce à vous... je vais mourir en paix.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3757;
UPDATE locales_npc_text SET entry = 3758, Text0_0_loc2 = 'Ne mettez pas ma patience à l\'épreuve, $g mortel : mortelle ;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3758;
