UPDATE locales_npc_text SET entry = 3793, Text0_0_loc2 = null, Text0_1_loc2 = 'Depuis que nous sommes dans le cratère, Dadanga semble apprécier tout particulièrement les grandes pousses de Pétales-de-sang qui foisonnent dans les alentours.$B$BJe crois qu’elle serait très contente si vous pouviez lui en apporter quelques-unes...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3793;
UPDATE locales_npc_text SET entry = 3794, Text0_0_loc2 = 'Salut ! Allez parler au maître Gadrin, juste là. Occupez-vous de Zalazane pour lui et je me chargerai de vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3794;
