UPDATE locales_npc_text SET entry = 3797, Text0_0_loc2 = 'Je suis... grièvement blessé...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3797;
UPDATE locales_npc_text SET entry = 3798, Text0_0_loc2 = 'Écoutez, $R. Écoutez bien... $B$BLes chevaucheurs de dragons viennent la nuit, montés sur les dragons noirs. Ils frappent vite, s\'emparent de jeunes dragonnets, comme moi, et s\'échappent sous le couvert de l\'obscurité.$B$BJe les ai vus, pétrifié d\'horreur, utiliser mes frères et mes sœurs dans leurs expériences. C\'est à partir de notre essence qu\'ils ont créé les dragons chromatiques.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3798;
UPDATE locales_npc_text SET entry = 3799, Text0_0_loc2 = 'Nefarian avait ordonné que les sujets inexploitables soient détruits immédiatement, mais ces ignobles bêtes ont obéi aux ordres avec un amour immodéré pour la torture.$B$BUne fois brisés, les pauvres, ou ce qu\'il en restait, étaient jetés à ces viles créatures. Ils étaient utilisés comme des objets dans des jeux morbides, jetés dans la lave, livrés aux légions sanguinaires, au plus profond de la terre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3799;
UPDATE locales_npc_text SET entry = 3800, Text0_0_loc2 = 'Vous avez agi juste au moment où j\'allais être jeté en pâture à ces bêtes. Je vous en remercie. Peut-être nous autres, les dragons bleus, pourrons-nous vous aider de quelque manière ?$B$BVous avez beaucoup marché, $R, vous avez beaucoup risqué. Irez-vous plus loin pour combattre Nefarian et sa légion de Rochenoire ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3800;
UPDATE locales_npc_text SET entry = 3801, Text0_0_loc2 = 'Si vous êtes ici pour tuer des dragons, faites comme bon vous semble ! À qui dois-je envoyer mes condoléances ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3801;
UPDATE locales_npc_text SET entry = 3802, Text0_0_loc2 = 'Votre puanteur va attirer des dragons à des lieues à la ronde. Dites-moi ce que vous voulez, et vite.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3802;
UPDATE locales_npc_text SET entry = 3803, Text0_0_loc2 = 'Les esprits m\'ont dit que vous viendriez, que vous me chercheriez pour quelque raison, mais que peut-être vous-même ignoreriez cette raison. Alors, savez-vous pourquoi vous êtes ici ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3803;
UPDATE locales_npc_text SET entry = 3804, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, salut, je m’appelle Sarah. Que puis-je faire pour vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3804;
UPDATE locales_npc_text SET entry = 3805, Text0_0_loc2 = null, Text0_1_loc2 = 'Les forêts m’ont annoncé votre présence, mais elles ne m’ont pas dit pourquoi vous étiez là. Parlez et éclairez-moi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3805;
UPDATE locales_npc_text SET entry = 3806, Text0_0_loc2 = 'Comment ça va ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3806;
UPDATE locales_npc_text SET entry = 3807, Text0_0_loc2 = null, Text0_1_loc2 = 'Je vous salue, $N, et le Cercle d\'émeraude vous souhaite la bienvenue.$B$BJ’espère qu’ensemble nous pourrons protéger cette forêt autrefois si majestueuse, cette forêt que tant de forces hostiles veulent détruire.$B$BNe vous laissez pas abuser par la force qui règne sur ces bois, elle n’a comme seul dessein que de vous corrompre, comme elle l’a fait avec tout le pays !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3807;
UPDATE locales_npc_text SET entry = 3813, Text0_0_loc2 = 'Vous n\'avez pas assez de cran pour monter à dos de griffon, hein ? C\'est une chance, on a quelque chose qui vous permettrait de rester sur le plancher des kodos. Enfin, plus ou moins. C\'est le transport en commun des gnomes, le Tram des profondeurs. Il est situé au fond du quartier des nains.', Text0_1_loc2 = 'Vous n\'avez pas assez de cran pour monter à dos de griffon, hein ? C\'est une chance, on a quelque chose qui vous permettrait de rester sur le plancher des kodos. Enfin, plus ou moins. C\'est le transport en commun des gnomes, le Tram des profondeurs. Il est situé au fond du quartier des nains.', Text1_0_loc2 = 'Vous avez entendu parler du tram, non ? C\'est quelque chose de fabuleux ! Vous le trouverez dans le quartier des nains, au fond. Oh, et ne sortez pas vos bras ou vos jambes quand il se déplace.', Text1_1_loc2 = 'Vous avez entendu parler du tram, non ? C\'est quelque chose de fabuleux ! Vous le trouverez dans le quartier des nains, au fond. Oh, et ne sortez pas vos bras ou vos jambes quand il se déplace.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3813;
UPDATE locales_npc_text SET entry = 3814, Text0_0_loc2 = 'Oui, tout le monde veut prendre le tram. Ma foi, si vous voulez le trouver, il est à Brikabrok, à l\'est des portes de Forgefer.', Text0_1_loc2 = 'Oui, tout le monde veut prendre le tram. Ma foi, si vous voulez le trouver, il est à Brikabrok, à l\'est des portes de Forgefer.', Text1_0_loc2 = 'Oh, le tram, le tram... Tout le monde veut le tram. C\'en est donc fini des bons vieux vols à dos de griffon, quand on s\'agrippe à la selle pour ne pas tomber du haut des cieux ?
Bah ! Allez prendre le tram si ça vous chante... C\'est à Brikabrok, avec le reste des bidules gnomes.', Text1_1_loc2 = 'Oh, le tram, le tram... Tout le monde veut le tram. C\'en est donc fini des bons vieux vols à dos de griffon, quand on s\'agrippe à la selle pour ne pas tomber du haut des cieux ?
Bah ! Allez prendre le tram si ça vous chante... C\'est à Brikabrok, avec le reste des bidules gnomes.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3814;
UPDATE locales_npc_text SET entry = 3815, Text0_0_loc2 = 'Le livre parle de dragons qui s\'unissent contre un autre dragon plus puissant, l\'Aile de mort. Apparemment, les dragons chromatiques avaient presque été lâchés sur le monde. Les Aspects étaient préparés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3815;
UPDATE locales_npc_text SET entry = 3833, Text0_0_loc2 = 'Un hôtel des ventes vient d’ouvrir sur la Terrasse des marchands. Allez à la banque, prenez le sentier qui part vers le sud-est, vous ne pouvez pas le rater.', Text0_1_loc2 = 'Un hôtel des ventes vient d’ouvrir sur la Terrasse des marchands. Allez à la banque, prenez le sentier qui part vers le sud-est, vous ne pouvez pas le rater.', Text1_0_loc2 = 'Oh, l\'éternelle obsession des biens matériels. Ma foi, la fortune vous sourit, car vous trouverez un hôtel des ventes sur la Terrasse des marchands.', Text1_1_loc2 = 'Oh, l\'éternelle obsession des biens matériels. Ma foi, la fortune vous sourit, car vous trouverez un hôtel des ventes sur la Terrasse des marchands.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3833;
UPDATE locales_npc_text SET entry = 3834, Text0_0_loc2 = 'Il se trouve dans le Quartier commerçant, $gcitoyen:citoyenne;. Presque en face de l’entrée de Hurlevent.', Text0_1_loc2 = 'Il se trouve dans le Quartier commerçant, $gcitoyen:citoyenne;. Presque en face de l’entrée de Hurlevent.', Text1_0_loc2 = 'Un autre entrepeneur, hein ? Eh bien, l’hôtel des ventes se trouve dans le Quartier commerçant, pas très loin de la banque.', Text1_1_loc2 = 'Un autre entrepeneur, hein ? Eh bien, l’hôtel des ventes se trouve dans le Quartier commerçant, pas très loin de la banque.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3834;
UPDATE locales_npc_text SET entry = 3854, Text0_0_loc2 = 'Bonjour. Rares sont ceux qui me trouvent.$B$BMon nom est Rivern, et je dresse des dents de sabre du Berceau-de-l\'Hiver pour en faire des montures.$B$BCes félins sont les plus difficiles à soumettre.$B$BJe ne vendrai pas une telle monture à n\'importe qui. Seuls ceux qui sont exaltés peuvent tenter de les monter. Vous devrez travailler pour gagner ma confiance et celle de l\'animal.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3854;
