UPDATE locales_npc_text SET entry = 3859, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3859;
UPDATE locales_npc_text SET entry = 3860, Text0_0_loc2 = 'On peut boire un verre dans de nombreux établissements de Hurlevent, mais il n\'en est qu\'un où on peut passer la nuit. Dirigez-vous vers la Rose dorée dans le Quartier commerçant. Il paraît que le coût d\'une chambre est raisonnable et que les draps sont propres.', Text0_1_loc2 = 'On peut boire un verre dans de nombreux établissements de Hurlevent, mais il n\'en est qu\'un où on peut passer la nuit. Dirigez-vous vers la Rose dorée dans le Quartier commerçant. Il paraît que le coût d\'une chambre est raisonnable et que les draps sont propres.', Text1_0_loc2 = 'Ah, la Rose dorée, ses lits moelleux, ses bains chauds... Rien que d\'y penser, je bâille... Vous avez de la chance si vous vous y rendez. Vous la trouverez à l\'ouest du Quartier commerçant. Des oreillers à plumes, des couvertures bien chaudes... ::bâille::', Text1_1_loc2 = 'Ah, la Rose dorée, ses lits moelleux, ses bains chauds... Rien que d\'y penser, je bâille... Vous avez de la chance si vous vous y rendez. Vous la trouverez à l\'ouest du Quartier commerçant. Des oreillers à plumes, des couvertures bien chaudes... ::bâille::', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3860;
UPDATE locales_npc_text SET entry = 3861, Text0_0_loc2 = 'Il y a une boîte aux lettres entre la banque de Hurlevent et l\'auberge de la Rose dorée, dans le Quartier commerçant.', Text0_1_loc2 = 'Il y a une boîte aux lettres entre la banque de Hurlevent et l\'auberge de la Rose dorée, dans le Quartier commerçant.', Text1_0_loc2 = 'Je ne sais pas comment ils font... Ces boîtes aux lettres doivent être des portails magiques ou je ne sais quoi. Je vois des gens s\'amasser autour de la boîte aux lettres, à côté de la banque, toute la journée, et ils tirent des épées, des boucliers et des armures de cette petite boîte !', Text1_1_loc2 = 'Je ne sais pas comment ils font... Ces boîtes aux lettres doivent être des portails magiques ou je ne sais quoi. Je vois des gens s\'amasser autour de la boîte aux lettres, à côté de la banque, toute la journée, et ils tirent des épées, des boucliers et des armures de cette petite boîte !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3861;
UPDATE locales_npc_text SET entry = 3864, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut, $c, je suis Jessir Arquelune. Je viens de Darnassus et suis à la recherche de deux de mes amis. Ils cherchaient les signes de la présence du Conseil des ombres dans Gangrebois.$B$BIl semble en effet que les viles créatures qui ont tant tourmenté la race orc travaillent maintenant à regagner pouvoir et force.$B$BMes deux amis faisaient leurs recherches dans Jaedenar pour tenter de trouver des preuves.$B$BJe pensais les retrouver ici, mais ils ont plusieurs jours de retard.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3864;
UPDATE locales_npc_text SET entry = 3865, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai tellement froid... et j’ai si faim.$B$BMon pauvre ami, j’espère que vous avez trouvé la paix. Je ne peux supporter de penser à ce qu’ils on pu vous faire.$B$BVous ? Vous ici ! S’il vous plaît, vous devez m’aider à m’échapper. L’un d’eux a une clé, pouvez-vous la trouver ? Si vous mettiez la main dessus, vous pourriez m’aider à sortir d’ici. Trey est probablement déjà mort, mais il reste encore du temps, m’aiderez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3865;
UPDATE locales_npc_text SET entry = 3866, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut ! Je reviens d\'une exploration dans les ruines de Kel\'Theril, au sud-ouest. Maintenant, j\'ai l\'intention d\'enquêter sur les Indomptables qui grouillent de l\'autre côté de la crête, au Fourré de l\'Aile de la chouette.$B$BAvez-vous parlé à Erelas Ambreciel, au village de Rut\'theran ? Il m\'a demandé de venir ici. Il a peut-être une tâche pour vous aussi.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3866;
UPDATE locales_npc_text SET entry = 3868, Text0_0_loc2 = null, Text0_1_loc2 = 'Avez-vous vu le Gardien Backus ? Il est si courageux...$B$BSans doute est-il le plus fort de tous les Veilleurs. Je me demande s’il a seulement remarqué que je suis là…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3868;
