UPDATE locales_npc_text SET entry = 3996, Text0_0_loc2 = 'La peste du Fléau domine toujours la zone, il faudra du temps avant que la peste soit suffisamment altérée pour les affecter. Collectez les ressources nécessaires pour aider à l\'effort de guerre !', Text0_1_loc2 = 'La peste du Fléau domine toujours la zone, il faudra du temps avant que la peste soit suffisamment altérée pour les affecter. Collectez les ressources nécessaires pour aider à l\'effort de guerre !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 3996;
