UPDATE locales_npc_text SET entry = 4015, Text0_0_loc2 = 'Vous cherchez Tai\'jin. Vous la trouverez dans la caserne, à l\'est de la ville.', Text0_1_loc2 = 'Vous cherchez Tai\'jin. Vous la trouverez dans la caserne, à l\'est de la ville.', Text1_0_loc2 = 'Tai\'jin est son nom, vous la trouverez dans la caserne à l\'est de Tranchecolline. Elle vous apprendra à respecter les dieux. Montrez-lui le respect qu\'elle mérite en retour !', Text1_1_loc2 = 'Tai\'jin est son nom, vous la trouverez dans la caserne à l\'est de Tranchecolline. Elle vous apprendra à respecter les dieux. Montrez-lui le respect qu\'elle mérite en retour !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4015;
UPDATE locales_npc_text SET entry = 4016, Text0_0_loc2 = 'Vous trouverez Kaplak en haut du fortin, au sud-ouest de la ville.', Text0_1_loc2 = 'Vous trouverez Kaplak en haut du fortin, au sud-ouest de la ville.', Text1_0_loc2 = 'Pourquoi voulez-vous parler à Kaplak ? Peu importe. Vous le trouverez en haut du fortin, au sud-ouest de Tranchecolline. Mais gardez vos mains dans vos poches !', Text1_1_loc2 = 'Pourquoi voulez-vous parler à Kaplak ? Peu importe. Vous le trouverez en haut du fortin, au sud-ouest de Tranchecolline. Mais gardez vos mains dans vos poches !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4016;
UPDATE locales_npc_text SET entry = 4017, Text0_0_loc2 = 'Il vous faut trouver Swart. Cherchez-le dans la caserne à l\'est de Tranchcolline.', Text0_1_loc2 = 'Il vous faut trouver Swart. Cherchez-le dans la caserne à l\'est de Tranchcolline.', Text1_0_loc2 = 'Le chef apporte un souffle nouveau qui donnera la victoire à la Horde ! Cherchez Swart dans la caserne à l\'est de la ville.', Text1_1_loc2 = 'Le chef apporte un souffle nouveau qui donnera la victoire à la Horde ! Cherchez Swart dans la caserne à l\'est de la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4017;
