UPDATE locales_npc_text SET entry = 4021, Text0_0_loc2 = 'C\'est Dwukk que vous cherchez. Il est à côté de la forge, dans l\'angle nord-ouest de la ville.', Text0_1_loc2 = 'C\'est Dwukk que vous cherchez. Il est à côté de la forge, dans l\'angle nord-ouest de la ville.', Text1_0_loc2 = 'Si vous pensez avoir la force de fondre et de forger le métal pour en faire des armes et des armures pour la Horde, allez voir Dwukk, à la forge dans l\'angle nord-ouest de Tranchecolline.', Text1_1_loc2 = 'Si vous pensez avoir la force de fondre et de forger le métal pour en faire des armes et des armures pour la Horde, allez voir Dwukk, à la forge dans l\'angle nord-ouest de Tranchecolline.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4021;
UPDATE locales_npc_text SET entry = 4022, Text0_0_loc2 = 'Suivez la route vers le nord jusqu\'à Orgrimmar. Demandez à l\'un des grunts de faction comment trouver le maître des cuisiniers.', Text0_1_loc2 = 'Suivez la route vers le nord jusqu\'à Orgrimmar. Demandez à l\'un des grunts de faction comment trouver le maître des cuisiniers.', Text1_0_loc2 = 'Hum, manger... Ce n\'est pas très loin, mais vous devez aller vers le nord, jusqu\'à Orgrimmar. Demandez ensuite à l\'un des grunts de faction.', Text1_1_loc2 = 'Hum, manger... Ce n\'est pas très loin, mais vous devez aller vers le nord, jusqu\'à Orgrimmar. Demandez ensuite à l\'un des grunts de faction.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4022;
UPDATE locales_npc_text SET entry = 4023, Text0_0_loc2 = 'Il y en a un à Orgrimmar. Demandez à un grunt de vous indiquer la direction quand vous y serez. C\'est vers le nord, suivez la route, vous ne pouvez pas rater la ville.', Text0_1_loc2 = 'Il y en a un à Orgrimmar. Demandez à un grunt de vous indiquer la direction quand vous y serez. C\'est vers le nord, suivez la route, vous ne pouvez pas rater la ville.', Text1_0_loc2 = 'Je ne crois pas qu\'il y en ait dans le secteur. Vous devriez aller à Orgrimmar, au nord, et demander à l\'un des grunts de faction.', Text1_1_loc2 = 'Je ne crois pas qu\'il y en ait dans le secteur. Vous devriez aller à Orgrimmar, au nord, et demander à l\'un des grunts de faction.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4023;
UPDATE locales_npc_text SET entry = 4024, Text0_0_loc2 = 'Il y a un maître des ingénieurs à Tranchecolline. Il s\'appelle Mukdrak, vous le trouverez probablement à côté de la forge.', Text0_1_loc2 = 'Il y a un maître des ingénieurs à Tranchecolline. Il s\'appelle Mukdrak, vous le trouverez probablement à côté de la forge.', Text1_0_loc2 = 'Bah, c\'est un travail pour les gobelins ! Mais bon, il y a bien un orc ici à Tranchecolline qui peut vous servir de maître. Cherchez Mukdrak, du côté de la forge.', Text1_1_loc2 = 'Bah, c\'est un travail pour les gobelins ! Mais bon, il y a bien un orc ici à Tranchecolline qui peut vous servir de maître. Cherchez Mukdrak, du côté de la forge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4024;
UPDATE locales_npc_text SET entry = 4025, Text0_0_loc2 = 'Il y en a un dans les casernes à l\'est de Tranchecolline. Cherchez Rawrk.', Text0_1_loc2 = 'Il y en a un dans les casernes à l\'est de Tranchecolline. Cherchez Rawrk.', Text1_0_loc2 = 'Avec tous les combats, il est évident que nous avons un maître des secouristes à Tranchecolline. Cherchez Rawrk dans la caserne du côté est de la ville.', Text1_1_loc2 = 'Avec tous les combats, il est évident que nous avons un maître des secouristes à Tranchecolline. Cherchez Rawrk dans la caserne du côté est de la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4025;
UPDATE locales_npc_text SET entry = 4026, Text0_0_loc2 = 'J\'ai vu un troll nommé Lau\'Tiki pêcher au sud de la grève des Sombrelances. C\'est à la périphérie du village de Sen\'jin.', Text0_1_loc2 = 'J\'ai vu un troll nommé Lau\'Tiki pêcher au sud de la grève des Sombrelances. C\'est à la périphérie du village de Sen\'jin.', Text1_0_loc2 = 'Vous cherchez Lau\'Tiki. Vous le trouverez sur la grève des Sombrelances, au sud, au-delà du village de Sen\'jin.', Text1_1_loc2 = 'Vous cherchez Lau\'Tiki. Vous le trouverez sur la grève des Sombrelances, au sud, au-delà du village de Sen\'jin.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4026;
UPDATE locales_npc_text SET entry = 4027, Text0_0_loc2 = 'C\'est Mishiki que vous cherchez. Elle a une hutte au sud du village de Sen\'jin.', Text0_1_loc2 = 'C\'est Mishiki que vous cherchez. Elle a une hutte au sud du village de Sen\'jin.', Text1_0_loc2 = 'Des fleurs ? Dans ce désert ? Si quelqu\'un peut vous apprendre à en trouver, il ne peut s\'agir que de Mishiki, qui est au village de Sen\'jin, plus au sud.', Text1_1_loc2 = 'Des fleurs ? Dans ce désert ? Si quelqu\'un peut vous apprendre à en trouver, il ne peut s\'agir que de Mishiki, qui est au village de Sen\'jin, plus au sud.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4027;
UPDATE locales_npc_text SET entry = 4028, Text0_0_loc2 = 'Vous devrez vous rendre à Orgrimmar pour ça. Suivez la route qui sort de Tranchecolline vers le nord, à travers le canyon, et vous trouverez la ville. Sur place, demandez à l\'un des grunts plus d\'informations.', Text0_1_loc2 = 'Vous devrez vous rendre à Orgrimmar pour ça. Suivez la route qui sort de Tranchecolline vers le nord, à travers le canyon, et vous trouverez la ville. Sur place, demandez à l\'un des grunts plus d\'informations.', Text1_0_loc2 = 'Pour apprendre à traiter le cuir et à travailler les peaux, vous devez aller à Orgrimmar, vers le nord. Demandez à l\'un des grunts où se trouve le maître des travailleurs du cuir.', Text1_1_loc2 = 'Pour apprendre à traiter le cuir et à travailler les peaux, vous devez aller à Orgrimmar, vers le nord. Demandez à l\'un des grunts où se trouve le maître des travailleurs du cuir.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4028;
UPDATE locales_npc_text SET entry = 4029, Text0_0_loc2 = 'Bien sûr, nous avons un maître des mineurs ici. Cherchez Krunn, dans l\'angle nord-ouest, à côté de l\'auberge.', Text0_1_loc2 = 'Bien sûr, nous avons un maître des mineurs ici. Cherchez Krunn, dans l\'angle nord-ouest, à côté de l\'auberge.', Text1_0_loc2 = 'On m\'a dit qu\'il y a de beaux gisements à Durotar. D\'une telle qualité que nous avons même un maître des mineurs ici même à la Tranchecolline. Vous trouverez Krunn à côté de la forge, dans l\'angle nord-ouest de la ville.', Text1_1_loc2 = 'On m\'a dit qu\'il y a de beaux gisements à Durotar. D\'une telle qualité que nous avons même un maître des mineurs ici même à la Tranchecolline. Vous trouverez Krunn à côté de la forge, dans l\'angle nord-ouest de la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4029;
UPDATE locales_npc_text SET entry = 4030, Text0_0_loc2 = 'Il y a un maître des dépeceurs à Orgrimmar. Pour vous y rendre, suivez la route vers le nord en quittant Tranchecolline. Demandez sur place à un grunt les directions précises.', Text0_1_loc2 = 'Il y a un maître des dépeceurs à Orgrimmar. Pour vous y rendre, suivez la route vers le nord en quittant Tranchecolline. Demandez sur place à un grunt les directions précises.', Text1_0_loc2 = 'Il y en a un à Orgrimmar. Allez vers le nord et demandez à l\'un des grunts.', Text1_1_loc2 = 'Il y en a un à Orgrimmar. Allez vers le nord et demandez à l\'un des grunts.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4030;
UPDATE locales_npc_text SET entry = 4031, Text0_0_loc2 = 'Alors c\'est à Orgrimmar que vous devez aller. Suivez la route vers le nord en sortant de Tranchecolline, elle y mène directement. Parlez à l\'un des grunts une fois sur place pour avoir des directions plus précises.', Text0_1_loc2 = 'Alors c\'est à Orgrimmar que vous devez aller. Suivez la route vers le nord en sortant de Tranchecolline, elle y mène directement. Parlez à l\'un des grunts une fois sur place pour avoir des directions plus précises.', Text1_0_loc2 = 'Vous pouvez apprendre le métier de tailleur à Orgrimmar. C\'est au nord d\'ici. Demandez à l\'un des grunts de la ville de vous indiquer qui aller voir.', Text1_1_loc2 = 'Vous pouvez apprendre le métier de tailleur à Orgrimmar. C\'est au nord d\'ici. Demandez à l\'un des grunts de la ville de vous indiquer qui aller voir.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4031;
UPDATE locales_npc_text SET entry = 4032, Text0_0_loc2 = 'Hein ? Vous trouverez une banque à Orgrimmar. Suivez la route vers le nord et parlez à un des grunts de la ville pour plus de renseignements.', Text0_1_loc2 = 'Hein ? Vous trouverez une banque à Orgrimmar. Suivez la route vers le nord et parlez à un des grunts de la ville pour plus de renseignements.', Text1_0_loc2 = 'Oui, je dois aller déposer des choses à la banque moi aussi. Vous la trouverez à Orgrimmar, en suivant la route vers le nord. Demandez à l\'un des grunts sur place pour savoir où elle se trouve précisément.', Text1_1_loc2 = 'Oui, je dois aller déposer des choses à la banque moi aussi. Vous la trouverez à Orgrimmar, en suivant la route vers le nord. Demandez à l\'un des grunts sur place pour savoir où elle se trouve précisément.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4032;
UPDATE locales_npc_text SET entry = 4033, Text0_0_loc2 = 'Vous trouverez un maître des coursiers du vent à Orgrimmar. Suivez la route vers le nord et demandez à l\'un des grunts d\'Orgrimmar de vous donner plus de renseignements.', Text0_1_loc2 = 'Vous trouverez un maître des coursiers du vent à Orgrimmar. Suivez la route vers le nord et demandez à l\'un des grunts d\'Orgrimmar de vous donner plus de renseignements.', Text1_0_loc2 = 'J\'ai du travail, ça ne se voit pas ? Suivez la route vers le nord jusqu\'à Orgrimmar et demandez à l\'un des grunts. Vous ne pouvez pas rater la ville.', Text1_1_loc2 = 'J\'ai du travail, ça ne se voit pas ? Suivez la route vers le nord jusqu\'à Orgrimmar et demandez à l\'un des grunts. Vous ne pouvez pas rater la ville.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4033;
UPDATE locales_npc_text SET entry = 4034, Text0_0_loc2 = 'Je me reposerais volontiers moi aussi, une chopine à la main. Vous trouverez l\'auberge dans l\'angle nord-ouest de Tranchecolline. Passez le bonjour de ma part à l\'aubergiste Grosk.', Text0_1_loc2 = 'Je me reposerais volontiers moi aussi, une chopine à la main. Vous trouverez l\'auberge dans l\'angle nord-ouest de Tranchecolline. Passez le bonjour de ma part à l\'aubergiste Grosk.', Text1_0_loc2 = 'C\'est le grand bâtiment à Tranchecolline, mais ne le confondez pas avec la caserne. Parlez à l\'aubergiste Grosk. Vous avez tout compris ?', Text1_1_loc2 = 'C\'est le grand bâtiment à Tranchecolline, mais ne le confondez pas avec la caserne. Parlez à l\'aubergiste Grosk. Vous avez tout compris ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4034;
