UPDATE locales_npc_text SET entry = 4037, Text0_0_loc2 = 'Qu\'est-ce que vous cherchez ?', Text0_1_loc2 = 'Qu\'est-ce que vous cherchez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4037;
UPDATE locales_npc_text SET entry = 4039, Text0_0_loc2 = null, Text0_1_loc2 = 'Maintenant que nous avons réussi à percer leurs chaudrons, nous allons pouvoir introduire un contre-agent et traiter le Fléau avec ses propres méthodes !$B$BVoyez avec l’alchimiste Arbington quels sont les ingrédients dont il a besoin pour créer l’agent. Je gèrerai personnellement la synthèse des informations qui nous permettront de retourner la peste contre le Fléau !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4039;
UPDATE locales_npc_text SET entry = 4040, Text0_0_loc2 = null, Text0_1_loc2 = 'Maintenant que nous avons réussi à percer leurs chaudrons, nous allons pouvoir introduire un contre-agent et traiter le Fléau avec ses propres méthodes !$B$BVoyez avec l\'apothicaire Dithers quels sont les ingrédients dont il a besoin pour créer l\'agent. Je gèrerai personnellement la synthèse des informations qui nous permettront de retourner la peste contre le Fléau !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4040;
UPDATE locales_npc_text SET entry = 4041, Text0_0_loc2 = null, Text0_1_loc2 = 'Bonjour, jeune $ghomme:femme;. Je suis Eva Sarkhoff et voici mon mari, Lucien. C’est un plaisir que de faire votre connaissance.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4041;
UPDATE locales_npc_text SET entry = 4042, Text0_0_loc2 = null, Text0_1_loc2 = 'Certainement. Nous avons autrefois servi dans la maison Barov, qui est maintenant connue sous le nom de Scholomance.$B$BJ\'étais servante en chef et Lucien était le majordome de Maître Barov.$B$BNous avons travaillé pour les Barov pendant de longues années, jusqu\'à ce que...$B$B<Eva sanglote.>', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4042;
UPDATE locales_npc_text SET entry = 4043, Text0_0_loc2 = null, Text0_1_loc2 = 'Oh, c\'est affreux, $gmonsieur:madame;, la maison Barov était jadis pleine de vie et de splendeur. Le manoir était la résidence principale des Barov, mais leurs possessions s\'étendaient au loin.$B$BLucien et moi avons remarqué que, le temps passant, les Barov sont devenus dépressifs. Les crises de paranoïa et de fureur devenaient de plus en plus fréquentes chez nos maîtres. La nuit, nous pouvions les entendre se disputer dans leurs appartements. D\'après ce que nous avons pu comprendre, leur avidité avait gagné sur leur raison et l\'avait dévorée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4043;
UPDATE locales_npc_text SET entry = 4044, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous pensons que pour préserver leur fortune et leur pouvoir au-delà de la mort, les Barov ont conclu un marché avec un puissant mage humain, un certain Kel\'Thuzad.$B$B<Eva frissonne.>$B$BC\'est à cette occasion qu\'est apparue l\'école de nécromancie, un lieu voué au culte des damnés, qui devint leur capitale.$B$BLes semaines passant, la maison Barov fut de plus en plus corrompue.$B$BDes créatures des ténèbres s\'installèrent dans les différentes ailes de la maison, rendant l\'entretien et le nettoyage aussi irréalisables qu\'écœurants.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4044;
UPDATE locales_npc_text SET entry = 4045, Text0_0_loc2 = null, Text0_1_loc2 = 'Pour aller où ? Nous ne connaissons pas d\'autre travail et ne pouvons vivre autrement.$B$BÀ la fin, nous avons complètement perdu contact avec les Barov et avons été mis à l\'écart du ménage. Nous ne savions pas ce qui arrivait aux autres domestiques, mais nous entendions souvent des cris de détresse, des cris terribles déchirer le silence.$B$BSachant que nous n\'avions nulle part où aller, Lucien et moi avons été forcés de nous cacher dans nos anciens quartiers. Pendant la journée, mon mari se glissait à l\'extérieur et allait glaner un peu de nourriture.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4045;
UPDATE locales_npc_text SET entry = 4046, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons vécu ainsi pendant six mois, assistant à la chute de la maison Barov, spectateurs d\'évènements toujours plus horribles. Des créatures hideuses rôdaient dans le manoir en toute liberté. Des sectateurs des ténèbres avaient couvert les murs et le sol de divers objets rituels et sacrificiels. Nous savions que nous ne pourrions nous cacher indéfiniment.$B$BComme nous nous y attendions, nous avons fini par être repérés et capturés. Ils nous ont ôté tous nos vêtements et nous ont allongés au centre d\'un cercle de sang. Nous sommes demeurés ainsi, nus et tremblants, pendant plusieurs jours.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4046;
UPDATE locales_npc_text SET entry = 4047, Text0_0_loc2 = null, Text0_1_loc2 = 'Des liens ? Non… il y a des choses sur cette terre qui sont bien plus puissantes que de simples chaînes ou barreaux pour tenir quelqu\'un prisonnier, jeune $ghomme:femme;. Les morts-vivants nous tournaient autour, ne cessant de nous tourmenter par des actes abominables.$B$BFinalement, il vint. Il se présenta sous le nom de docteur Theolen Krastinov, mais nous allions apprendre à le connaître sous son autre nom : "le Boucher".', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4047;
UPDATE locales_npc_text SET entry = 4048, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous avons finalement compris d\'où venaient les cris. Le Boucher nous soumit à des affres dont nous ignorions qu\'ils pussent exister. Il pratiqua des expériences hideuses sur nous, essayant de créer une sorte de peste en se servant de nous comme de cobayes.$B$BLes jours devinrent des semaines et nous serions morts dès le premier jour si cet être infâme n\'avait utilisé des moyens magiques pour nous conserver en vie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4048;
UPDATE locales_npc_text SET entry = 4049, Text0_0_loc2 = null, Text0_1_loc2 = 'Le Boucher nous parlait sans arrêt du "sang des innocents", de son maître, "Kirtonos", et de l\'obligation qu\'il avait de le satisfaire.$B$BFinalement, l\'infâme en eut fini avec ces expériences. Nous avions été vidés de toute vie ; nos esprits étaient en miettes. Nous n\'attendions que le doux baiser de la mort pour nous libérer. Mais dans son infinie cruauté, le Boucher referma devant nous les portes de la mort.$B$BNous étions destinés à rester en vie pour nourrir ses goules voraces. Il riait à gorge déployée en regardant comme ces créatures se repaissaient de notre chair.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4049;
UPDATE locales_npc_text SET entry = 4050, Text0_0_loc2 = null, Text0_1_loc2 = 'Nous ne ressentons rien. Nos âmes restent ici, dans les limbes. Nous ne pourrons pas partir tant que nos dépouilles n\'auront pas été retrouvées pour donner la paix à nos esprits.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4050;
UPDATE locales_npc_text SET entry = 4051, Text0_0_loc2 = 'Ah, vous devez aller dans les hauteurs des Pitons du Tonnerre pour en visiter un. Suivez le chemin au nord de Sabot-de-sang. À la base des pitons, il y a un ascenseur qui vous mènera en haut. Quand vous y serez, demandez d\'autres indications aux Gardiens des Hauts.', Text0_1_loc2 = 'Ah, vous devez aller dans les hauteurs des Pitons du Tonnerre pour en visiter un. Suivez le chemin au nord de Sabot-de-sang. À la base des pitons, il y a un ascenseur qui vous mènera en haut. Quand vous y serez, demandez d\'autres indications aux Gardiens des Hauts.', Text1_0_loc2 = 'Nous n\'avons pas un endroit de cette sorte au village. Mais ce n\'est pas le cas des hauteurs des Pitons du Tonnerre. Allez au nord par la route qui quitte Sabot-de-sang jusqu\'à ce que vous parveniez au pied des pitons. Prenez alors l\'ascenseur et parlez avec un Gardien des Hauts. Que vos ancêtres vous gardent !', Text1_1_loc2 = 'Nous n\'avons pas un endroit de cette sorte au village. Mais ce n\'est pas le cas des hauteurs des Pitons du Tonnerre. Allez au nord par la route qui quitte Sabot-de-sang jusqu\'à ce que vous parveniez au pied des pitons. Prenez alors l\'ascenseur et parlez avec un Gardien des Hauts. Que vos ancêtres vous gardent !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4051;
UPDATE locales_npc_text SET entry = 4052, Text0_0_loc2 = 'Il y a un maître des coursiers du vent dans la grande ville des Pitons du Tonnerre, au nord. Prenez l\'ascenseur à la base des Pitons du Tonnerre et, en haut, demandez d\'autres instructions à un gardien des Hauts.', Text0_1_loc2 = 'Il y a un maître des coursiers du vent dans la grande ville des Pitons du Tonnerre, au nord. Prenez l\'ascenseur à la base des Pitons du Tonnerre et, en haut, demandez d\'autres instructions à un gardien des Hauts.', Text1_0_loc2 = 'Rien ne libère l\'esprit comme de planer dans les airs. C\'est possible dans notre grand centre tribal des Pitons du Tonnerre. La route au nord de Sabot-de-sang vous y mènera. Une fois que vous aurez pris l\'ascenseur jusqu\'en haut, parlez à un des Gardiens des Hauts qui vous renseignera sur la marche à suivre.', Text1_1_loc2 = 'Rien ne libère l\'esprit comme de planer dans les airs. C\'est possible dans notre grand centre tribal des Pitons du Tonnerre. La route au nord de Sabot-de-sang vous y mènera. Une fois que vous aurez pris l\'ascenseur jusqu\'en haut, parlez à un des Gardiens des Hauts qui vous renseignera sur la marche à suivre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4052;
UPDATE locales_npc_text SET entry = 4053, Text0_0_loc2 = 'L\'aubergiste Kauth prendra soin de vous. Vous le trouverez à l\'ouest du village dans une des longues maisons.', Text0_1_loc2 = 'L\'aubergiste Kauth prendra soin de vous. Vous le trouverez à l\'ouest du village dans une des longues maisons.', Text1_0_loc2 = 'Il y a un temps et un lieu pour tout, y compris la détente. À l\'ouest de Sabot-de-sang, l\'aubergiste Kauth tient une grande bâtisse pour ceux qui souhaitent se reposer. Vous l\'y trouverez sans mal.', Text1_1_loc2 = 'Il y a un temps et un lieu pour tout, y compris la détente. À l\'ouest de Sabot-de-sang, l\'aubergiste Kauth tient une grande bâtisse pour ceux qui souhaitent se reposer. Vous l\'y trouverez sans mal.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4053;
UPDATE locales_npc_text SET entry = 4054, Text0_0_loc2 = 'Vous cherchez les conseils de Gennia Totem-runique. Vous la trouverez dans la grande tente du côté est du village.', Text0_1_loc2 = 'Vous cherchez les conseils de Gennia Totem-runique. Vous la trouverez dans la grande tente du côté est du village.', Text1_0_loc2 = 'Les voies du druide sont proches de celles du chaman. Gennia Totem-runique maîtrise ces connaissances. Cherchez-la à l\'est de Sabot-de-sang.', Text1_1_loc2 = 'Les voies du druide sont proches de celles du chaman. Gennia Totem-runique maîtrise ces connaissances. Cherchez-la à l\'est de Sabot-de-sang.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4054;
UPDATE locales_npc_text SET entry = 4055, Text0_0_loc2 = 'Yaw Crin-tranchant vous attend au nord du village. Il vous enseignera la voie du chasseur.', Text0_1_loc2 = 'Yaw Crin-tranchant vous attend au nord du village. Il vous enseignera la voie du chasseur.', Text1_0_loc2 = 'Apprendre à ne faire qu\'un avec la Nature aux yeux de la Terre-mère est une question de fierté personnelle ! Si vous désirez apprendre la voie du chasseur, adressez-vous à Yaw Crin-tranchant, au nord de Sabot-de-sang.', Text1_1_loc2 = 'Apprendre à ne faire qu\'un avec la Nature aux yeux de la Terre-mère est une question de fierté personnelle ! Si vous désirez apprendre la voie du chasseur, adressez-vous à Yaw Crin-tranchant, au nord de Sabot-de-sang.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4055;
UPDATE locales_npc_text SET entry = 4056, Text0_0_loc2 = 'Il y a une grande tente à l\'est de Sabot-de-sang. Vous y trouverez Narm Chasse-le-ciel.', Text0_1_loc2 = 'Il y a une grande tente à l\'est de Sabot-de-sang. Vous y trouverez Narm Chasse-le-ciel.', Text1_0_loc2 = 'Il y a un sage dans le village dont le nom est Narm Chasse-le-ciel. Il peut vous guider plus loin sur le chemin des chamans.', Text1_1_loc2 = 'Il y a un sage dans le village dont le nom est Narm Chasse-le-ciel. Il peut vous guider plus loin sur le chemin des chamans.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4056;
UPDATE locales_npc_text SET entry = 4057, Text0_0_loc2 = 'Krang Sabot-de-pierre tient son cercle d\'apprentissage au sud-est du village.', Text0_1_loc2 = 'Krang Sabot-de-pierre tient son cercle d\'apprentissage au sud-est du village.', Text1_0_loc2 = 'Si vous cherchez à devenir un meilleur guerrier, cherchez Krang Sabot-de-pierre. Vous le trouverez au sud-est du village, dans son cercle d\'apprentissage, éduquant d\'autres disciples.', Text1_1_loc2 = 'Si vous cherchez à devenir un meilleur guerrier, cherchez Krang Sabot-de-pierre. Vous le trouverez au sud-est du village, dans son cercle d\'apprentissage, éduquant d\'autres disciples.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4057;
UPDATE locales_npc_text SET entry = 4058, Text0_0_loc2 = 'Nous n\'avons pas de maître des alchimistes à Sabot-de-sang, mais il y en a un aux Pitons du Tonnerre, au nord. Suivez la route et demandez à un Gardien des Hauts de vous conseiller, une fois que vous aurez atteint le sommet.', Text0_1_loc2 = 'Nous n\'avons pas de maître des alchimistes à Sabot-de-sang, mais il y en a un aux Pitons du Tonnerre, au nord. Suivez la route et demandez à un Gardien des Hauts de vous conseiller, une fois que vous aurez atteint le sommet.', Text1_0_loc2 = 'Utiliser les fruits de la Terre-mère pour créer des potions susceptibles de nous aider dans notre éternelle lutte est honorable. Pour en apprendre plus, vous devez aller au nord vers notre grande maison des Pitons du Tonnerre. Quand vous serez en haut, demandez l\'aide de l\'un Gardien des Hauts pour vous orienter.', Text1_1_loc2 = 'Utiliser les fruits de la Terre-mère pour créer des potions susceptibles de nous aider dans notre éternelle lutte est honorable. Pour en apprendre plus, vous devez aller au nord vers notre grande maison des Pitons du Tonnerre. Quand vous serez en haut, demandez l\'aide de l\'un Gardien des Hauts pour vous orienter.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4058;
UPDATE locales_npc_text SET entry = 4059, Text0_0_loc2 = 'Il y a un maître des forgerons dans notre grande maison des Pitons du Tonnerre, au nord. Si vous cherchez une telle connaissance, allez-y et demandez des informations à un des Gardiens des Hauts.', Text0_1_loc2 = 'Il y a un maître des forgerons dans notre grande maison des Pitons du Tonnerre, au nord. Si vous cherchez une telle connaissance, allez-y et demandez des informations à un des Gardiens des Hauts.', Text1_0_loc2 = 'Vous désirez former les os de la Terre-mère en outils susceptibles d\'aider la Horde. Bien, bien. Suivez le chemin du nord en quittant Sabot-de-sang jusqu\'à ce que vous trouviez l\'ascenseur menant aux sommets des Pitons du Tonnerre. Un Gardien des Hauts pourra vous y aider.', Text1_1_loc2 = 'Vous désirez former les os de la Terre-mère en outils susceptibles d\'aider la Horde. Bien, bien. Suivez le chemin du nord en quittant Sabot-de-sang jusqu\'à ce que vous trouviez l\'ascenseur menant aux sommets des Pitons du Tonnerre. Un Gardien des Hauts pourra vous y aider.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4059;
UPDATE locales_npc_text SET entry = 4060, Text0_0_loc2 = 'Vous trouverez Pyall Sabots-feutrés dans la grande tente au nord-ouest du village. Il vous enseignera ce que vous voulez savoir.', Text0_1_loc2 = 'Vous trouverez Pyall Sabots-feutrés dans la grande tente au nord-ouest du village. Il vous enseignera ce que vous voulez savoir.', Text1_0_loc2 = 'J\'en ai l\'eau à la bouche rien que d\'y penser. Pyall Sabots-feutrés est le meilleur cuisinier de Sabot-de-sang. Vous le trouverez dans la grande tente au nord-ouest du village.', Text1_1_loc2 = 'J\'en ai l\'eau à la bouche rien que d\'y penser. Pyall Sabots-feutrés est le meilleur cuisinier de Sabot-de-sang. Vous le trouverez dans la grande tente au nord-ouest du village.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4060;
UPDATE locales_npc_text SET entry = 4061, Text0_0_loc2 = 'Votre voyage vous mènera vers le nord, en direction des Pitons du Tonnerre. Cherchez-y la sagesse des Gardiens des Hauts qui vous orienteront vers celui que vous cherchez.', Text0_1_loc2 = 'Votre voyage vous mènera vers le nord, en direction des Pitons du Tonnerre. Cherchez-y la sagesse des Gardiens des Hauts qui vous orienteront vers celui que vous cherchez.', Text1_0_loc2 = 'Une telle sagesse est liée à un mystère que vous ne résoudrez qu\'aux Pitons du Tonnerre. Suivez le chemin du nord quittant Sabot-de-sang jusqu\'à ce que vous arriviez à un ascenseur. Montez au sommet et laissez-vous guider par un Gardien des Hauts.', Text1_1_loc2 = 'Une telle sagesse est liée à un mystère que vous ne résoudrez qu\'aux Pitons du Tonnerre. Suivez le chemin du nord quittant Sabot-de-sang jusqu\'à ce que vous arriviez à un ascenseur. Montez au sommet et laissez-vous guider par un Gardien des Hauts.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4061;
UPDATE locales_npc_text SET entry = 4062, Text0_0_loc2 = 'Vira Jeune-sabot pratique son art dans la longue maison à l\'ouest du village.', Text0_1_loc2 = 'Vira Jeune-sabot pratique son art dans la longue maison à l\'ouest du village.', Text1_0_loc2 = 'Bandages, antivenins et autres, c\'est une nécessité quand on vit dans les plaines. Vira Jeune-sabot peut vous enseigner ces connaissances. Vous la trouverez dans la grande maison à l\'ouest de Sabot-de-sang.', Text1_1_loc2 = 'Bandages, antivenins et autres, c\'est une nécessité quand on vit dans les plaines. Vira Jeune-sabot peut vous enseigner ces connaissances. Vous la trouverez dans la grande maison à l\'ouest de Sabot-de-sang.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4062;
UPDATE locales_npc_text SET entry = 4063, Text0_0_loc2 = 'Vous cherchez Uthan Eau-plate. Vous le trouverez à l\'ouest du village, pêchant dans le lac Taureau-de-pierre.', Text0_1_loc2 = 'Vous cherchez Uthan Eau-plate. Vous le trouverez à l\'ouest du village, pêchant dans le lac Taureau-de-pierre.', Text1_0_loc2 = 'Donnez un poisson à un taureau et il mangera un jour. Apprenez-lui à pêcher et il aura à manger toute sa vie. Uthan Eau-plate peut vous apprendre à pêcher. Cherchez sa sagesse à l\'ouest de Sabot-de-sang, le long des berges du lac Taureau-de-pierre.', Text1_1_loc2 = 'Donnez un poisson à un taureau et il mangera un jour. Apprenez-lui à pêcher et il aura à manger toute sa vie. Uthan Eau-plate peut vous apprendre à pêcher. Cherchez sa sagesse à l\'ouest de Sabot-de-sang, le long des berges du lac Taureau-de-pierre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4063;
UPDATE locales_npc_text SET entry = 4064, Text0_0_loc2 = 'Si vous désirez vous perfectionner en Herboristerie, vous devez aller aux Pitons du Tonnerre. Suivez la route au nord de Sabot-de-sang et prenez l\'ascenseur jusqu\'en haut. Vous y demanderez de l\'aide à un Gardien des Hauts.', Text0_1_loc2 = 'Si vous désirez vous perfectionner en Herboristerie, vous devez aller aux Pitons du Tonnerre. Suivez la route au nord de Sabot-de-sang et prenez l\'ascenseur jusqu\'en haut. Vous y demanderez de l\'aide à un Gardien des Hauts.', Text1_0_loc2 = 'Certains pourraient vous apprendre l\'Herboristerie dans la grande maison des Pitons du Tonnerre, au nord. Suivez la route qui quitte le village pour vous y rendre. À la base du pic, prenez l\'ascenseur vers le sommet. De nombreux Gardiens des Hauts pourront vous y donner de plus amples informations.', Text1_1_loc2 = 'Certains pourraient vous apprendre l\'Herboristerie dans la grande maison des Pitons du Tonnerre, au nord. Suivez la route qui quitte le village pour vous y rendre. À la base du pic, prenez l\'ascenseur vers le sommet. De nombreux Gardiens des Hauts pourront vous y donner de plus amples informations.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4064;
UPDATE locales_npc_text SET entry = 4065, Text0_0_loc2 = 'Dans la grande tente, au nord-ouest du village, demeure Chaw Cuir-solide. Il vous enseignera ce que vous devez savoir sur le travail du cuir.', Text0_1_loc2 = 'Dans la grande tente, au nord-ouest du village, demeure Chaw Cuir-solide. Il vous enseignera ce que vous devez savoir sur le travail du cuir.', Text1_0_loc2 = 'Lorsque nous utilisons les dépouilles des animaux pour nous nourrir ou nous protéger, nous honorons la Terre-mère et nous respectons l\'animal. Chaw Cuir-solide peut vous enseigner tout cela. Vous le trouverez dans la grande tente, au nord-ouest de Sabot-de-sang.', Text1_1_loc2 = 'Lorsque nous utilisons les dépouilles des animaux pour nous nourrir ou nous protéger, nous honorons la Terre-mère et nous respectons l\'animal. Chaw Cuir-solide peut vous enseigner tout cela. Vous le trouverez dans la grande tente, au nord-ouest de Sabot-de-sang.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4065;
UPDATE locales_npc_text SET entry = 4066, Text0_0_loc2 = 'Hmmm, je pense que vous devrez aller aux Pitons du Tonnerre pour apprendre cela. Allez au nord en quittant Sabot-de-sang. Marchez jusqu\'à la base des Pitons du Tonnerre. Prenez l\'ascenseur vers les sommets et demandez votre chemin à un Gardien des Hauts.', Text0_1_loc2 = 'Hmmm, je pense que vous devrez aller aux Pitons du Tonnerre pour apprendre cela. Allez au nord en quittant Sabot-de-sang. Marchez jusqu\'à la base des Pitons du Tonnerre. Prenez l\'ascenseur vers les sommets et demandez votre chemin à un Gardien des Hauts.', Text1_0_loc2 = 'Il paraît qu\'il y a un maître des mineurs, au nord, aux Pitons du Tonnerre. Prenez la route qui quitte le village et quand vous y serez, prenez l\'ascenseur jusqu\'au sommet. N\'importe quel Gardien des Hauts devrait pouvoir vous aider une fois que vous y serez.', Text1_1_loc2 = 'Il paraît qu\'il y a un maître des mineurs, au nord, aux Pitons du Tonnerre. Prenez la route qui quitte le village et quand vous y serez, prenez l\'ascenseur jusqu\'au sommet. N\'importe quel Gardien des Hauts devrait pouvoir vous aider une fois que vous y serez.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4066;
UPDATE locales_npc_text SET entry = 4067, Text0_0_loc2 = 'Yonn Entaille-profonde se trouve dans la grande tente au nord-ouest de Sabot-de-sang.', Text0_1_loc2 = 'Yonn Entaille-profonde se trouve dans la grande tente au nord-ouest de Sabot-de-sang.', Text1_0_loc2 = 'Ah oui, le dépeçage. Nul n\'utilise mieux les couteaux à dépecer que Yonn Entaille-profonde. Vous le trouverez dans la grande tente au nord-ouest du village.', Text1_1_loc2 = 'Ah oui, le dépeçage. Nul n\'utilise mieux les couteaux à dépecer que Yonn Entaille-profonde. Vous le trouverez dans la grande tente au nord-ouest du village.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4067;
UPDATE locales_npc_text SET entry = 4068, Text0_0_loc2 = 'Malheureusement nous n\'avons personne qui enseigne l\'art du Tailleur ici, à Sabot-de-sang, mais vous en trouverez un aux Pitons du Tonnerre. Suivez le chemin du nord qui quitte le village jusqu\'à ce que vous arriviez aux ascenseurs. Prenez-en un jusqu\'au sommet et demandez votre chemin à un Gardien des Hauts.', Text0_1_loc2 = 'Malheureusement nous n\'avons personne qui enseigne l\'art du Tailleur ici, à Sabot-de-sang, mais vous en trouverez un aux Pitons du Tonnerre. Suivez le chemin du nord qui quitte le village jusqu\'à ce que vous arriviez aux ascenseurs. Prenez-en un jusqu\'au sommet et demandez votre chemin à un Gardien des Hauts.', Text1_0_loc2 = 'Tailleur, vous dites ? Hmmm. Ah oui, bien sûr ! Vous devez aller aux Pitons du Tonnerre, au nord. Prenez le chemin qui s\'y rend depuis le village. Lorsque vous arriverez à la base des pitons, prenez l\'ascenseur jusqu\'au sommet et parlez à l\'un des Gardiens des Hauts.', Text1_1_loc2 = 'Tailleur, vous dites ? Hmmm. Ah oui, bien sûr ! Vous devez aller aux Pitons du Tonnerre, au nord. Prenez le chemin qui s\'y rend depuis le village. Lorsque vous arriverez à la base des pitons, prenez l\'ascenseur jusqu\'au sommet et parlez à l\'un des Gardiens des Hauts.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4068;
