UPDATE locales_npc_text SET entry = 4074, Text0_0_loc2 = 'Nous n\'avons pas de banque à Brill, il vous faudra aller à Fossoyeuse pour cela. Derrière les Ruines de Lordaeron, au sud, il y a des ascenseurs qui vous mèneront à Fossoyeuse. Là, demandez à un des Gardiens d\'Fossoyeuse de vous guider.', Text0_1_loc2 = 'Nous n\'avons pas de banque à Brill, il vous faudra aller à Fossoyeuse pour cela. Derrière les Ruines de Lordaeron, au sud, il y a des ascenseurs qui vous mèneront à Fossoyeuse. Là, demandez à un des Gardiens d\'Fossoyeuse de vous guider.', Text1_0_loc2 = 'Mais pourquoi avez-vous besoin d\'une banque ? Enfin, vous devrez traverser les Ruines de Lordaeron jusqu\'au sud de Brill. Derrière, vous trouverez des ascenseurs qui descendent vers Fossoyeuse. Là, demandez votre route à une de ces Abominations qui se font passer pour les Gardiens d\'Fossoyeuse.', Text1_1_loc2 = 'Mais pourquoi avez-vous besoin d\'une banque ? Enfin, vous devrez traverser les Ruines de Lordaeron jusqu\'au sud de Brill. Derrière, vous trouverez des ascenseurs qui descendent vers Fossoyeuse. Là, demandez votre route à une de ces Abominations qui se font passer pour les Gardiens d\'Fossoyeuse.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4074;
