UPDATE locales_npc_text SET entry = 4089, Text0_0_loc2 = 'Cherchez Faruza. La dernière fois que je l\'ai vue, elle se tenait entre les écuries et la maison au nord-ouest de la ville.', Text0_1_loc2 = 'Cherchez Faruza. La dernière fois que je l\'ai vue, elle se tenait entre les écuries et la maison au nord-ouest de la ville.', Text1_0_loc2 = 'En fait, je préfère les médecines traditionnelles... Sangsues, vers, scie chirurgicale... Ces trucs-là. Mais à chacun ses goûts. Vous trouverez notre Maître des Herboristes, Faruza, près des écuries de Brill.', Text1_1_loc2 = 'En fait, je préfère les médecines traditionnelles... Sangsues, vers, scie chirurgicale... Ces trucs-là. Mais à chacun ses goûts. Vous trouverez notre Maître des Herboristes, Faruza, près des écuries de Brill.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4089;
