UPDATE locales_npc_text SET entry = 4097, Text0_0_loc2 = 'Qu\'est-ce que vous cherchez ?', Text0_1_loc2 = 'Qu\'est-ce que vous cherchez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4097;
UPDATE locales_npc_text SET entry = 4113, Text0_0_loc2 = null, Text0_1_loc2 = 'Vous m\'avez déjà rendu un grand service, $N. Pour cela, je vous remercie.$B$BLe moins que je puisse faire est de vous dire ce qui m\'amène ici. Mon ami de longue date Trey Sancteforge et moi sommes venus à Jaedenar pour enquêter sur le Conseil des ombres, et nous nous sommes fait prendre. Ils ont emmené Trey au plus profond du Fort des ombres et m\'ont jetée ici après avoir pris tout notre équipement... J\'entends toujours ses hurlements qui montaient des profondeurs, $N. Ils étaient... inhumains. Je ne sais pas ce qu\'ils lui ont fait, mais ils paieront pour cela.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4113;
