UPDATE locales_npc_text SET entry = 4117, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4117;
UPDATE locales_npc_text SET entry = 4118, Text0_0_loc2 = 'Vous ne voyez pas que nous nous préparons pour la visite d\'Uther ? Hors de ma vue, maintenant ! J\'ai du travail !', Text0_1_loc2 = 'Vous ne voyez pas que nous nous préparons pour la visite d\'Uther ? Hors de ma vue, maintenant ! J\'ai du travail !', Text1_0_loc2 = 'Vous avez essayé notre pain ? Le boulanger Masterson est à coup sûr le meilleur cuisinier de l\'histoire.', Text1_1_loc2 = 'Vous avez essayé notre pain ? Le boulanger Masterson est à coup sûr le meilleur cuisinier de l\'histoire.', Text2_0_loc2 = 'Bienvenue à Caer Darrow, $gétranger:étrangère;.', Text2_1_loc2 = 'Bienvenue à Caer Darrow, $gétranger:étrangère;.', Text3_0_loc2 = 'Nous ne sommes pas vraiment discourtois, mais tout le monde est pressé, tout doit être parfait pour l\'arrivée d\'Uther. Ce n\'est pas tous les jours que le Porteur de Lumière vient en ville.', Text3_1_loc2 = 'Nous ne sommes pas vraiment discourtois, mais tout le monde est pressé, tout doit être parfait pour l\'arrivée d\'Uther. Ce n\'est pas tous les jours que le Porteur de Lumière vient en ville.', Text4_0_loc2 = 'Encore une belle journée dans notre île magnifique.', Text4_1_loc2 = 'Encore une belle journée dans notre île magnifique.', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4118;
UPDATE locales_npc_text SET entry = 4119, Text0_0_loc2 = 'Attention où vous mettez les pieds. Nous ne voulons pas de bandits dans notre ville.', Text0_1_loc2 = 'Attention où vous mettez les pieds. Nous ne voulons pas de bandits dans notre ville.', Text1_0_loc2 = 'Je vous ai à l\'oeil, $R.', Text1_1_loc2 = 'Je vous ai à l\'oeil, $R.', Text2_0_loc2 = 'Je ne suis que rage et hostilité. Donnez-moi seulement un prétexte, et...', Text2_1_loc2 = 'Je ne suis que rage et hostilité. Donnez-moi seulement un prétexte, et...', Text3_0_loc2 = 'Bah, vous avez l\'air $gdu dernier des mendiants:de la dernière des mendiantes;...', Text3_1_loc2 = 'Bah, vous avez l\'air $gdu dernier des mendiants:de la dernière des mendiantes;...', Text4_0_loc2 = 'Ici, on ne jure pas, on ne se bat pas, on ne vagabonde pas... Vous êtes à Caer Darrow.', Text4_1_loc2 = 'Ici, on ne jure pas, on ne se bat pas, on ne vagabonde pas... Vous êtes à Caer Darrow.', Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4119;
UPDATE locales_npc_text SET entry = 4122, Text0_0_loc2 = 'Le Culte des damnés a encore progressé. Presque chaque ville, des capitales aux petits hameaux, dans la Horde comme dans l\'Alliance, abrite des membres du culte.$B$BNe prenez pas cet air surpris. Ils ne portent d\'uniformes que pour les rituels et les cérémonies. Il est possible que vous ayez tissé des liens avec l\'un d\'eux sans même le savoir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4122;
UPDATE locales_npc_text SET entry = 4123, Text0_0_loc2 = 'Vous croyez ? Ils ont toujours opéré de cette manière. Qu\'est-ce qui fait la force de Kel\'Thuzad et de l\'armée sans cesse plus nombreuse du Fléau ? Un disciple fanatique et bien vivant qui prépare leur chemin dans la cité et déchaîne soudain une peste qui détruit les gens par milliers ou un simple soldat mort-vivant ? Vous n\'êtes pas assez stupide pour vous laisser avoir.$B$BCroyez-moi, $R, ils vivent, travaillent et complotent parmi nous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4123;
UPDATE locales_npc_text SET entry = 4124, Text0_0_loc2 = 'Rien. Vous pouvez les ralentir, peut-être.$B$BLa Scholomance est leur principale source d\'entraînement et d\'éducation. C\'est là que les membres du culte se forment avant de se répandre dans nos sociétés.$B$BIl faut frapper à la source. Au coeur du Culte des damnés !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4124;
UPDATE locales_npc_text SET entry = 4125, Text0_0_loc2 = 'Doucement, $R. Celui que vous devez tuer est déjà mort. Le tuer de nouveau ne servira à rien. Il n\'y a guère de moyens de détruire une liche, et nous ne pouvons rien faire pour l\'heure. Mais j\'ai un plan. Un plan qui aura un véritable impact sur le Culte.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4125;
UPDATE locales_npc_text SET entry = 4126, Text0_0_loc2 = 'C\'est très simple, en fait. Volez ses pouvoirs à la liche Ras Murmegivre. Renvoyez-le à sa forme mortelle et là, vous pourrez le tuer une fois pour toutes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4126;
UPDATE locales_npc_text SET entry = 4127, Text0_0_loc2 = 'Ah, je suppose que je me laisse emporter. Ras Murmegivre est le maître de cette école. L\'être qui forme toutes ces créatures des ténèbres. Un lieutenant et un favori de Kel\'Thuzad. Il fut naguère un mage ambigu de Stromgarde : un magicien indépendant qui ne fut pas instruit à Dalaran.$B$BLorsque Kel\'Thuzad fonda le culte, Ras fut l\'un des premiers à rejoindre ses rangs. Ras savait que par la magie noire, il atteindrait l\'immortalité.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4127;
UPDATE locales_npc_text SET entry = 4128, Text0_0_loc2 = 'Il y est parvenu. Il a rapidement gravi la hiérarchie du culte, et le roi-liche lui a donné l\'immortalité. C\'est ainsi qu\'est né Ras Murmegivre, la liche.$B$BVous comprenez, à présent ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4128;
UPDATE locales_npc_text SET entry = 4129, Text0_0_loc2 = 'Bien. Pour que mon plan fonctionne, nous devons d\'abord créer une pierre de vie. Vous pensez pouvoir agir ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4129;
UPDATE locales_npc_text SET entry = 4130, Text0_0_loc2 = 'Certains se sont vus épargner l\'angoisse des souvenirs.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4130;
UPDATE locales_npc_text SET entry = 4133, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4133;
UPDATE locales_npc_text SET entry = 4134, Text0_0_loc2 = 'Bienvenue. Je suis l\'officier Garush. J\'appartiens à une organisation mondiale connue sous le nom de l\'Aube d\'argent - et j\'en suis fier. Notre objectif est simple : nous combattons sans pitié toutes les manifestations du mal qui infecte Azeroth. Nous sommes apolitiques et nous entendons le rester. Notre cause est particulière, et nous souhaitons tant l\'amitié de la Horde que celle de l\'Alliance, contre le même ennemi : le mal !$B$BPour l\'Aube, $gmon frère:ma sœur; !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4134;
UPDATE locales_npc_text SET entry = 4135, Text0_0_loc2 = null, Text0_1_loc2 = 'Contente de vous voir. Je suis l’officier Purecarde, membre d’une organisation mondiale connue sous le nom de l\'Aube d\'argent - et j\'en suis fière. Notre objectif est simple : nous combattons sans pitié toutes les manifestations du mal qui infecte Azeroth. Nous ne sommes pas une entité politique, et nous ne désirons pas en devenir une. De fait, nous avons des relations cordiales à la fois avec l’Alliance et la Horde. Nous les considérons comme des alliés dans notre objectif d’éradication du mal !$B$BLongue vie à l’Aube d\'argent, $gmon frère:ma sœur; !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4135;
UPDATE locales_npc_text SET entry = 4173, Text0_0_loc2 = null, Text0_1_loc2 = 'L’Aube d\'argent vous salue, $c. Je suis Hasana, je suis l\'une des rares intendantes de l\'Aube d\'argent.$B$BEn combattant le mal sous notre bannière, vous gagnerez non seulement notre respect et notre confiance, mais l’officier Garush vous attribuera des marques de valeur. Ces marques ont deux usages. Tout d\'abord, elles vous permettent d\'améliorer votre statut dans nos rangs. Elles servent également de monnaie d\'échange pour acheter certains objets.$B$BVous verrez qu\'en progressant dans les rangs de l\'Aube d\'argent, vous vous verrez proposer un nombre croissant d\'objets.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4173;
