UPDATE locales_npc_text SET entry = 4213, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $C. Je suis Miranda Coinceculasse, l\'une des rares intendantes en charge de l\'équipement et du matériel pour l\'Aube d\'argent.$B$BCombattez le mal au travers des missions que nous avons à vous confier, gagnez notre respect et notre confiance, et le duc Zverenhoff vous attribuera des marques de valeur. Ces marques ont deux usages. Tout d\'abord, elles vous permettent d\'améliorer votre statut dans nos rangs. Elles servent également de monnaie d\'échange pour acheter certains objets.$B$BVous verrez qu\'en progressant dans les rangs de l\'Aube d\'argent, vous vous verrez proposer un nombre croissant d\'objets.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4213;
UPDATE locales_npc_text SET entry = 4233, Text0_0_loc2 = 'Salutations, $c.$B$BCes Sentinelles m\'ont autorisé à partager leur campement, tant que je me tiens tranquille. Je pense que vous comprenez qu\'il est parfois nécessaire de faire preuve de diplomatie, surtout par ici, non ?$B$BJ\'enquête sur certaines perturbations dans les terres, et comme cela soutient leur cause, ils ont compris que ma présence ici ne pouvait que favoriser l\'alliance ténue qui unit toutes les races d\'Azeroth. Si vous voulez m\'aider, j\'accepterai volontiers, mais quoi qu\'il en soit, ne faites rien d\'insensé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4233;
UPDATE locales_npc_text SET entry = 4253, Text0_0_loc2 = 'Bienvenue au Dépôt de Grillacier !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4253;
UPDATE locales_npc_text SET entry = 4254, Text0_0_loc2 = 'La race ne fait pas l\'honneur, $R. Tant que vous serez sur mes terres, je vous demanderai de respecter ce mot d\'ordre.$B$BJ\'ai connu des orcs aussi honorables que bon nombre de chevaliers et des humains aussi vils et brutaux que les pires créatures du Fléau.$B$BMais je ne vous raconterai pas mes exploits de jeunesse ! Il y a tant à faire, si tel est votre désir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4254;
