UPDATE locales_npc_text SET entry = 4259, Text0_0_loc2 = 'Qu\'est-ce que vous cherchez ?', Text0_1_loc2 = 'Qu\'est-ce que vous cherchez ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4259;
UPDATE locales_npc_text SET entry = 4260, Text0_0_loc2 = 'Une banque ? La plus proche que je connaisse, c\'est la Salle des dépôts de Hurlevent. Remontez la route jusqu\'à Hurlevent, elle n\'est pas loin des portes de la ville.', Text0_1_loc2 = 'Une banque ? La plus proche que je connaisse, c\'est la Salle des dépôts de Hurlevent. Remontez la route jusqu\'à Hurlevent, elle n\'est pas loin des portes de la ville.', Text1_0_loc2 = 'Oh, vous voulez parler de la Salle des dépôts de Hurlevent. Elle n\'est pas loin des portes de la ville, lorsque vous entrez dans Hurlevent.', Text1_1_loc2 = 'Oh, vous voulez parler de la Salle des dépôts de Hurlevent. Elle n\'est pas loin des portes de la ville, lorsque vous entrez dans Hurlevent.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4260;
UPDATE locales_npc_text SET entry = 4261, Text0_0_loc2 = 'Il y a un maître des griffons à Hurlevent si vous voulez voler quelque part. Vous verrez la plate-forme des griffons sur votre droite quand vous entrerez dans la vallée des Héros.', Text0_1_loc2 = 'Il y a un maître des griffons à Hurlevent si vous voulez voler quelque part. Vous verrez la plate-forme des griffons sur votre droite quand vous entrerez dans la vallée des Héros.', Text1_0_loc2 = 'Le maître des griffons s\'occupe de tous les vols en partance de Hurlevent. Si vous voulez voler, c\'est là que vous devez aller.', Text1_1_loc2 = 'Le maître des griffons s\'occupe de tous les vols en partance de Hurlevent. Si vous voulez voler, c\'est là que vous devez aller.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4261;
UPDATE locales_npc_text SET entry = 4262, Text0_0_loc2 = 'Je crois qu\'Aldwin Laughlin est toujours le maître des guildes en poste à Hurlevent. Cherchez sa résidence sur votre gauche, lorsque vous franchissez la porte intérieure de Hurlevent.', Text0_1_loc2 = 'Je crois qu\'Aldwin Laughlin est toujours le maître des guildes en poste à Hurlevent. Cherchez sa résidence sur votre gauche, lorsque vous franchissez la porte intérieure de Hurlevent.', Text1_0_loc2 = 'Toutes les affaires officielles sont traitées directement à Hurlevent de nos jours. Arrêtez-vous au Centre des visiteurs de Hurlevent si vous voulez créer une guilde.', Text1_1_loc2 = 'Toutes les affaires officielles sont traitées directement à Hurlevent de nos jours. Arrêtez-vous au Centre des visiteurs de Hurlevent si vous voulez créer une guilde.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4262;
UPDATE locales_npc_text SET entry = 4263, Text0_0_loc2 = 'Ne cherchez pas plus loin qu\'à l\'auberge de la Fierté du lion à Comté-de-l’or. De bons lits douillets, une large sélection de bières, c\'est un endroit parfait pour se reposer.', Text0_1_loc2 = 'Ne cherchez pas plus loin qu\'à l\'auberge de la Fierté du lion à Comté-de-l’or. De bons lits douillets, une large sélection de bières, c\'est un endroit parfait pour se reposer.', Text1_0_loc2 = 'L\'auberge de la Fierté du lion à Comté-de-l’or vaut bien toutes les auberges que vous trouverez dans les environs. Allez y faire un tour et l\'aubergiste Farley vous fera vous y sentir chez vous.', Text1_1_loc2 = 'L\'auberge de la Fierté du lion à Comté-de-l’or vaut bien toutes les auberges que vous trouverez dans les environs. Allez y faire un tour et l\'aubergiste Farley vous fera vous y sentir chez vous.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4263;
UPDATE locales_npc_text SET entry = 4264, Text0_0_loc2 = 'Il y a plusieurs maîtres dans la forêt d\'Elwynn. Lequel cherchez-vous ?', Text0_1_loc2 = 'Il y a plusieurs maîtres dans la forêt d\'Elwynn. Lequel cherchez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4264;
UPDATE locales_npc_text SET entry = 4265, Text0_0_loc2 = 'Des druides ? Je n\'ai jamais vu de druide par ici. Mais des elfes de la nuit se sont installés dans un des quartiers de Hurlevent.', Text0_1_loc2 = 'Des druides ? Je n\'ai jamais vu de druide par ici. Mais des elfes de la nuit se sont installés dans un des quartiers de Hurlevent.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4265;
UPDATE locales_npc_text SET entry = 4266, Text0_0_loc2 = 'On m\'a dit qu\'un des nains qui habitent Hurlevent s\'est fait une belle réputation de chasseur. Vous devriez aller voir s\'il peut quelque chose pour vous.', Text0_1_loc2 = 'On m\'a dit qu\'un des nains qui habitent Hurlevent s\'est fait une belle réputation de chasseur. Vous devriez aller voir s\'il peut quelque chose pour vous.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4266;
UPDATE locales_npc_text SET entry = 4267, Text0_0_loc2 = 'Josetta est une vraie bénédiction pour notre ville et ses gardes. Si vous cherchez à approfondir vos connaissances de prêtre, allez la voir à l\'auberge de la Fierté du lion.', Text0_1_loc2 = 'Josetta est une vraie bénédiction pour notre ville et ses gardes. Si vous cherchez à approfondir vos connaissances de prêtre, allez la voir à l\'auberge de la Fierté du lion.', Text1_0_loc2 = 'Je parie que Josetta sera ravie de vous enseigner quelques sorts. Elle est probablement dans sa chambre de l\'auberge de la Fierté du lion.', Text1_1_loc2 = 'Je parie que Josetta sera ravie de vous enseigner quelques sorts. Elle est probablement dans sa chambre de l\'auberge de la Fierté du lion.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4267;
UPDATE locales_npc_text SET entry = 4268, Text0_0_loc2 = 'Zaldimar pourrait sans doute vous apprendre quelques nouveaux sorts. Il s\'est installé il y a peu à l\'auberge de la Fierté du lion. Allez voir dans les chambres s\'il est chez lui.', Text0_1_loc2 = 'Zaldimar pourrait sans doute vous apprendre quelques nouveaux sorts. Il s\'est installé il y a peu à l\'auberge de la Fierté du lion. Allez voir dans les chambres s\'il est chez lui.', Text1_0_loc2 = 'Je n\'y connais pas grand-chose en magie, mais il y un mage, Zaldimar, qui s\'est installé à l\'auberge de la Fierté du lion. Il doit pouvoir vous aider.', Text1_1_loc2 = 'Je n\'y connais pas grand-chose en magie, mais il y un mage, Zaldimar, qui s\'est installé à l\'auberge de la Fierté du lion. Il doit pouvoir vous aider.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4268;
UPDATE locales_npc_text SET entry = 4269, Text0_0_loc2 = 'On trouve généralement le frère Wilhelm derrière la forge, à bavarder avec Lyria. Il est toujours prêt à apprendre quelques sorts à ses compagnons paladins.', Text0_1_loc2 = 'On trouve généralement le frère Wilhelm derrière la forge, à bavarder avec Lyria. Il est toujours prêt à apprendre quelques sorts à ses compagnons paladins.', Text1_0_loc2 = 'Le frère Wilhelm s\'entraîne souvent avec Lyria Du Lac, à l\'arrière de la forge de Comté-de-l’or. Vous pouvez sans doute apprendre une chose ou deux auprès d\'un paladin comme lui.', Text1_1_loc2 = 'Le frère Wilhelm s\'entraîne souvent avec Lyria Du Lac, à l\'arrière de la forge de Comté-de-l’or. Vous pouvez sans doute apprendre une chose ou deux auprès d\'un paladin comme lui.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4269;
UPDATE locales_npc_text SET entry = 4270, Text0_0_loc2 = 'Un voleur ? Hmmm... Ma foi, il paraît que Keryn Sylvius a des accointances avec eux... Parlez-lui, vous verrez bien ce qu\'elle vous dira.', Text0_1_loc2 = 'Un voleur ? Hmmm... Ma foi, il paraît que Keryn Sylvius a des accointances avec eux... Parlez-lui, vous verrez bien ce qu\'elle vous dira.', Text1_0_loc2 = 'Je parie que Keryn Sulvius connaît le type que vous cherchez. Elle a des connaissances douteuses. Vous la trouverez généralement à l\'auberge de la Fierté du lion.', Text1_1_loc2 = 'Je parie que Keryn Sulvius connaît le type que vous cherchez. Elle a des connaissances douteuses. Vous la trouverez généralement à l\'auberge de la Fierté du lion.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4270;
UPDATE locales_npc_text SET entry = 4271, Text0_0_loc2 = 'Rares sont ceux qui montrent autant de talent avec une lame que Lyria Du Lac. Allez la voir derrière la forge. Elle sera peut-être d\'humeur à vous apprendre une chose ou deux.', Text0_1_loc2 = 'Rares sont ceux qui montrent autant de talent avec une lame que Lyria Du Lac. Allez la voir derrière la forge. Elle sera peut-être d\'humeur à vous apprendre une chose ou deux.', Text1_0_loc2 = 'Lyria est la personne à qui vous devez parler si vous avez besoin d\'entraînement. On la trouve généralement à l\'extérieur de la forge, attendant qu\'une arme ou une pièce d\'armure soit terminée.', Text1_1_loc2 = 'Lyria est la personne à qui vous devez parler si vous avez besoin d\'entraînement. On la trouve généralement à l\'extérieur de la forge, attendant qu\'une arme ou une pièce d\'armure soit terminée.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4271;
UPDATE locales_npc_text SET entry = 4272, Text0_0_loc2 = 'Oh... Un de ceux-là. Eh bien, Maximillian est le seul dans les environs qui maîtrise les arts noirs. Vous le trouverez sans doute dans la pénombre de la cave de l\'auberge de la Fierté du lion.', Text0_1_loc2 = 'Oh... Un de ceux-là. Eh bien, Maximillian est le seul dans les environs qui maîtrise les arts noirs. Vous le trouverez sans doute dans la pénombre de la cave de l\'auberge de la Fierté du lion.', Text1_0_loc2 = 'Un démoniste ? Si j\'étais démoniste, je chercherais l\'endroit le plus sombre du coin... Et c\'est à n\'en pas douter la cave de l\'auberge de la Fierté du lion. C\'est là que j\'irais en premier.', Text1_1_loc2 = 'Un démoniste ? Si j\'étais démoniste, je chercherais l\'endroit le plus sombre du coin... Et c\'est à n\'en pas douter la cave de l\'auberge de la Fierté du lion. C\'est là que j\'irais en premier.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4272;
