UPDATE locales_npc_text SET entry = 4274, Text0_0_loc2 = 'On dit que l\'ermite Mallory concocte des potions pour les villageois à l\'occasion. Il a une maison dans les collines entre Hurlevent et Comté-du-nord.', Text0_1_loc2 = 'On dit que l\'ermite Mallory concocte des potions pour les villageois à l\'occasion. Il a une maison dans les collines entre Hurlevent et Comté-du-nord.', Text1_0_loc2 = 'Il y a un alchimiste qui vit dans les collines entre Comté-du-nord et Hurlevent et qui pourrait vous aider. Pourquoi ne pas lui rendre une visite ?', Text1_1_loc2 = 'Il y a un alchimiste qui vit dans les collines entre Comté-du-nord et Hurlevent et qui pourrait vous aider. Pourquoi ne pas lui rendre une visite ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4274;
