UPDATE locales_npc_text SET entry = 4276, Text0_0_loc2 = 'Tomas n\'est peut-être pas le meilleur cuisinier de la région, mais il a quelques recettes qui font mouche. Il travaille à l\'auberge de la Fierté du lion, si vous voulez apprendre les rudiments de cette profession.', Text0_1_loc2 = 'Tomas n\'est peut-être pas le meilleur cuisinier de la région, mais il a quelques recettes qui font mouche. Il travaille à l\'auberge de la Fierté du lion, si vous voulez apprendre les rudiments de cette profession.', Text1_0_loc2 = 'Pourquoi n\'allez-vous pas voir à l\'auberge de la Fierté du lion ? Le cuisinier pourrait sans doute vous apprendre quelques recettes.', Text1_1_loc2 = 'Pourquoi n\'allez-vous pas voir à l\'auberge de la Fierté du lion ? Le cuisinier pourrait sans doute vous apprendre quelques recettes.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4276;
UPDATE locales_npc_text SET entry = 4277, Text0_0_loc2 = 'Non, je ne pense pas qu\'il y ait d\'Enchanteur ici, dans la forêt d\'Elwynn. Il y a un type nommé Lucan qui tient une échoppe à Hurlevent, cela dit. Voyez avec lui s\'il veut bien vous entraîner.', Text0_1_loc2 = 'Non, je ne pense pas qu\'il y ait d\'Enchanteur ici, dans la forêt d\'Elwynn. Il y a un type nommé Lucan qui tient une échoppe à Hurlevent, cela dit. Voyez avec lui s\'il veut bien vous entraîner.', Text1_0_loc2 = 'Hum... Les seuls enchanteurs que je connaisse par ici sont à Hurlevent. Allez demander à l\'un des gardes de Hurlevent, il pourra vous renseigner.', Text1_1_loc2 = 'Hum... Les seuls enchanteurs que je connaisse par ici sont à Hurlevent. Allez demander à l\'un des gardes de Hurlevent, il pourra vous renseigner.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4277;
UPDATE locales_npc_text SET entry = 4278, Text0_0_loc2 = 'Je laisse généralement ces choses-là aux gnomes. Si vous voulez vous lancer dans la profession, je pense que vous trouverez des gnomes à Hurlevent qui seront ravis de vous aider.', Text0_1_loc2 = 'Je laisse généralement ces choses-là aux gnomes. Si vous voulez vous lancer dans la profession, je pense que vous trouverez des gnomes à Hurlevent qui seront ravis de vous aider.', Text1_0_loc2 = 'Il y a certainement un gnome à Hurlevent qui peut vous enseigner cette profession. Les gardes de Hurlevent pourront sûrement vous mettre sur la voie.', Text1_1_loc2 = 'Il y a certainement un gnome à Hurlevent qui peut vous enseigner cette profession. Les gardes de Hurlevent pourront sûrement vous mettre sur la voie.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4278;
UPDATE locales_npc_text SET entry = 4279, Text0_0_loc2 = 'J\'ai fait une mauvaise rencontre avec certains Défias par ici récemment, mais Michelle Belle m\'a rapidement rendu ma santé. Allez la voir à l\'auberge de la Fierté du lion si vous cherchez un bon professeur.', Text0_1_loc2 = 'J\'ai fait une mauvaise rencontre avec certains Défias par ici récemment, mais Michelle Belle m\'a rapidement rendu ma santé. Allez la voir à l\'auberge de la Fierté du lion si vous cherchez un bon professeur.', Text1_0_loc2 = 'La douceur de Michelle Belle au chevet des malades est extraordinaire ! Elle ne saura peut-être pas vous transmettre cela, mais elle vous donnera de solides connaissances en secourisme. Elle réside actuellement à l\'auberge de la Fierté du lion.', Text1_1_loc2 = 'La douceur de Michelle Belle au chevet des malades est extraordinaire ! Elle ne saura peut-être pas vous transmettre cela, mais elle vous donnera de solides connaissances en secourisme. Elle réside actuellement à l\'auberge de la Fierté du lion.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4279;
UPDATE locales_npc_text SET entry = 4280, Text0_0_loc2 = 'Lee Brown passe ses journées à pêcher dans le lac de Cristal. Il a sans doute des anecdotes à raconter et pourra peut-être vous apprendre quelques petites choses.', Text0_1_loc2 = 'Lee Brown passe ses journées à pêcher dans le lac de Cristal. Il a sans doute des anecdotes à raconter et pourra peut-être vous apprendre quelques petites choses.', Text1_0_loc2 = 'Lee a pris des choses vraiment incroyables dans le lac de Cristal. Il m\'a parlé d\'un drôle de masque qu\'il a sorti de l\'eau il y a quelques mois. À mon avis, il avait surtout abusé de la chopine à l\'auberge cette nuit-là.', Text1_1_loc2 = 'Lee a pris des choses vraiment incroyables dans le lac de Cristal. Il m\'a parlé d\'un drôle de masque qu\'il a sorti de l\'eau il y a quelques mois. À mon avis, il avait surtout abusé de la chopine à l\'auberge cette nuit-là.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4280;
UPDATE locales_npc_text SET entry = 4281, Text0_0_loc2 = 'Mme Pomeroy cueille des herbes pour un Alchimiste qui vit dans les collines entre Comté-du-nord et Hurlevent. Allez la voir dans son cottage et elle vous apprendra la profession.', Text0_1_loc2 = 'Mme Pomeroy cueille des herbes pour un Alchimiste qui vit dans les collines entre Comté-du-nord et Hurlevent. Allez la voir dans son cottage et elle vous apprendra la profession.', Text1_0_loc2 = 'Il y a une herboriste qui vit dans les collines à l\'est de Hurlevent, avec l\'alchimiste local. Un couple bien associé, non ?', Text1_1_loc2 = 'Il y a une herboriste qui vit dans les collines à l\'est de Hurlevent, avec l\'alchimiste local. Un couple bien associé, non ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4281;
UPDATE locales_npc_text SET entry = 4282, Text0_0_loc2 = 'Du côté du lac de Cristal vit une dame nommée Adele Fielder. Elle répare les tenues des gardes locaux avec le cuir qu\'elle obtient auprès d\'Helene, notre expert en dépeçage. C\'est sûrement à elle qu\'il faut parler si vous voulez apprendre la profession.', Text0_1_loc2 = 'Du côté du lac de Cristal vit une dame nommée Adele Fielder. Elle répare les tenues des gardes locaux avec le cuir qu\'elle obtient auprès d\'Helene, notre expert en dépeçage. C\'est sûrement à elle qu\'il faut parler si vous voulez apprendre la profession.', Text1_0_loc2 = 'Adele Fielder s\'est fait un nom comme travailleur du cuir à Elwynn. Vous la trouverez à l\'œuvre dans sa maison, près du lac de Cristal.', Text1_1_loc2 = 'Adele Fielder s\'est fait un nom comme travailleur du cuir à Elwynn. Vous la trouverez à l\'œuvre dans sa maison, près du lac de Cristal.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4282;
UPDATE locales_npc_text SET entry = 4283, Text0_0_loc2 = 'Rendez-vous plutôt à Hurlevent, au quartier des nains, si vous voulez apprendre à miner. Un des nains saura sûrement vous enseigner les rudiments de la profession.', Text0_1_loc2 = 'Rendez-vous plutôt à Hurlevent, au quartier des nains, si vous voulez apprendre à miner. Un des nains saura sûrement vous enseigner les rudiments de la profession.', Text1_0_loc2 = 'Je pense que vous avez plus de chances de trouver à Hurlevent. Demandez à un garde là-bas et il vous donnera la bonne direction.', Text1_1_loc2 = 'Je pense que vous avez plus de chances de trouver à Hurlevent. Demandez à un garde là-bas et il vous donnera la bonne direction.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4283;
UPDATE locales_npc_text SET entry = 4284, Text0_0_loc2 = 'Helene Tannepeau a des années d\'expérience dans le dépeçage d\'animaux, pour permettre à Adele Fielder de faire des objets en cuir. Vous trouverez Helene dans sa maison du lac de Cristal.', Text0_1_loc2 = 'Helene Tannepeau a des années d\'expérience dans le dépeçage d\'animaux, pour permettre à Adele Fielder de faire des objets en cuir. Vous trouverez Helene dans sa maison du lac de Cristal.', Text1_0_loc2 = 'Un expert en dépeçage vit dans la maison près du lac de Cristal. Elle vous montrera comment vous lancer dans cette profession.', Text1_1_loc2 = 'Un expert en dépeçage vit dans la maison près du lac de Cristal. Elle vous montrera comment vous lancer dans cette profession.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4284;
