UPDATE locales_npc_text SET entry = 4289, Text0_0_loc2 = 'Vous ne trouverez pas de maître des griffons où que ce soit à Dun Morogh, $gmon garçon : ma fille;. Vous devez vous rendre à Forgefer si vous voulez chevaucher une de ces bestioles.', Text0_1_loc2 = 'Vous ne trouverez pas de maître des griffons où que ce soit à Dun Morogh, $gmon garçon : ma fille;. Vous devez vous rendre à Forgefer si vous voulez chevaucher une de ces bestioles.', Text1_0_loc2 = 'Le maître des griffons le plus proche se trouve à Forgefer, près de la Grande Forge elle-même. Vous devez vous diriger vers Forgefer si vous souhaitez voyager en griffon.', Text1_1_loc2 = 'Le maître des griffons le plus proche se trouve à Forgefer, près de la Grande Forge elle-même. Vous devez vous diriger vers Forgefer si vous souhaitez voyager en griffon.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4289;
UPDATE locales_npc_text SET entry = 4290, Text0_0_loc2 = 'Tous ces officiels sont à Forgefer. Allez voir le Centre des visiteurs de Forgefer pour les papiers de création d\'une guilde.', Text0_1_loc2 = 'Tous ces officiels sont à Forgefer. Allez voir le Centre des visiteurs de Forgefer pour les papiers de création de Guilde.', Text1_0_loc2 = 'Si vous voulez mettre la main sur des papiers de création de guilde, vous devrez aller à Forgefer et visiter le Centre des visiteurs de Forgefer.', Text1_1_loc2 = 'Si vous voulez mettre la main sur des papiers de création de guilde, vous devrez aller à Forgefer et visiter le Centre des visiteurs de Forgefer.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4290;
UPDATE locales_npc_text SET entry = 4291, Text0_0_loc2 = 'Les Tonnebière dirigent une excellente auberge à Kharanos, elle porte leur nom. Prenez-y une bonne pinte et dormez un peu.', Text0_1_loc2 = 'Les Tonnebière dirigent une excellente auberge à Kharanos, elle porte leur nom. Prenez-y une bonne pinte et dormez un peu.', Text1_0_loc2 = 'Oooh, une auberge, hein ? Y a rien de mieux que la Distillerie Tonnebière. Reposez-vous si c\'est nécessaire, mais surtout, buvez-y une bière ou deux.', Text1_1_loc2 = 'Oooh, une auberge, hein ? Y a rien de mieux que la Distillerie Tonnebière. Reposez-vous si c\'est nécessaire, mais surtout, buvez-y une bière ou deux.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4291;
UPDATE locales_npc_text SET entry = 4292, Text0_0_loc2 = 'Quel maître de classe cherchez-vous ?', Text0_1_loc2 = 'Quel maître de classe cherchez-vous ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4292;
UPDATE locales_npc_text SET entry = 4293, Text0_0_loc2 = 'S\'il y a un maître des chasseurs à qui parler ici, c\'est bien Grif Cœur-sauvage. Vous le trouverez à l\'extérieur de la forge, dans Kharanos.', Text0_1_loc2 = 'S\'il y a un maître des chasseurs à qui parler ici, c\'est bien Grif Cœur-sauvage. Vous le trouverez à l\'extérieur de la forge, dans Kharanos.', Text1_0_loc2 = 'À mon avis, Grif passe un peu trop de temps avec les animaux. Mais personne ne connaît la voie du chasseur mieux que lui. Cherchez-le à l\'extérieur de la forge de Kharanos.', Text1_1_loc2 = 'À mon avis, Grif passe un peu trop de temps avec les animaux. Mais personne ne connaît la voie du chasseur mieux que lui. Cherchez-le à l\'extérieur de la forge de Kharanos.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4293;
UPDATE locales_npc_text SET entry = 4294, Text0_0_loc2 = 'Il y a un mage à la Distillerie Tonnebière qui pourrait vous apprendre quelques nouveaux sorts. Je crois que son nom est Magis Mantincelle.', Text0_1_loc2 = 'Il y a un mage à la Distillerie Tonnebière qui pourrait vous apprendre quelques nouveaux sorts. Je crois que son nom est Magis Mantincelle.', Text1_0_loc2 = 'Vous êtes $gsûr:sûre; que vous ne voulez pas devenir guerrier ? Comme vous voulez, c\'est votre peau après tout. Si vous voulez vraiment apprendre à remuer les doigts, parlez à Magis Mantincelle dans la Distillerie Tonnebière.', Text1_1_loc2 = 'Vous êtes $gsûr:sûre; que vous ne voulez pas devenir guerrier ? Comme vous voulez, c\'est votre peau après tout. Si vous voulez vraiment apprendre à remuer les doigts, parlez à Magis Mantincelle dans la Distillerie Tonnebière.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4294;
UPDATE locales_npc_text SET entry = 4295, Text0_0_loc2 = 'Och... Un paladin ? Eh bien, $gmon garçon : ma fille;, vous avez de la chance ! Azar Fortmartel est de retour de son entraînement et se trouve actuellement à la Distillerie Tonnebière, dans Kharanos.', Text0_1_loc2 = 'Och... Un paladin ? Eh bien, $gmon garçon : ma fille;, vous avez de la chance ! Azar Fortmartel est de retour de son entraînement et se trouve actuellement à la Distillerie Tonnebière, dans Kharanos.', Text1_0_loc2 = 'Vous avez besoin d\'entraînement ? Ma foi, Azar Fortmartel est toujours prêt à enseigner deux ou trois choses aux jeunes paladins. Cherchez-le à la Distillerie Tonnebière.', Text1_1_loc2 = 'Vous avez besoin d\'entraînement ? Ma foi, Azar Fortmartel est toujours prêt à enseigner deux ou trois choses aux jeunes paladins. Cherchez-le à la Distillerie Tonnebière.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4295;
UPDATE locales_npc_text SET entry = 4296, Text0_0_loc2 = 'Oh, un prêtre, vraiment ? Eh bien, le vieux Maxan est à coup sûr en train de boire un verre dans la Distillerie Tonnebière, si vous avez besoin de leçons.', Text0_1_loc2 = 'Oh, un prêtre, vraiment ? Eh bien, le vieux Maxan est à coup sûr en train de boire un verre dans la Distillerie Tonnebière, si vous avez besoin de leçons.', Text1_0_loc2 = 'Vous trouverez sûrement Maxan Anvol en train de picoler à la Distillerie Tonnebière. Pourquoi n\'allez-vous pas prendre une pinte et voir s\'il peut vous enseigner quelque chose ?', Text1_1_loc2 = 'Vous trouverez sûrement Maxan Anvol en train de picoler à la Distillerie Tonnebière. Pourquoi n\'allez-vous pas prendre une pinte et voir s\'il peut vous enseigner quelque chose ?', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4296;
UPDATE locales_npc_text SET entry = 4297, Text0_0_loc2 = 'Ma foi, il semble que nous soyons tous deux à la recherche de Hogral Bakkan. C\'est un nain, pas de doute, à un moment ou à une autre vous le trouverez donc à la distillerie Tonnebière, à la recherche d\'une bière. Il y est peut-être en ce moment même.', Text0_1_loc2 = 'Ma foi, il semble que nous soyons tous deux à la recherche de Hogral Bakkan. C\'est un nain, pas de doute, à un moment ou à une autre vous le trouverez donc à la distillerie Tonnebière, à la recherche d\'une bière. Il y est peut-être en ce moment même.', Text1_0_loc2 = 'Vous cherchez Hogral ? Avez-vous été à la taverne ? Och ! Si vous cherchez un voleur, et un nain en plus, commencez par l\'auberge ! Allez-y sans tarder.', Text1_1_loc2 = 'Vous cherchez Hogral ? Avez-vous été à la taverne ? Och ! Si vous cherchez un voleur, et un nain en plus, commencez par l\'auberge ! Allez-y sans tarder.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4297;
UPDATE locales_npc_text SET entry = 4298, Text0_0_loc2 = 'Les démonistes... C\'est pas mon truc. Comme ce Grimrizz qui a monté son échoppe à la sortie de la Distillerie Tonnebière. La bière va sans doute devenir amère à cause de lui ! Les démonistes... Bah !', Text0_1_loc2 = 'Les démonistes... C\'est pas mon truc. Comme ce Grimrizz qui a monté son échoppe à la sortie de la Distillerie Tonnebière. La bière va sans doute devenir amère à cause de lui ! Les démonistes... Bah !', Text1_0_loc2 = 'Ce genre de magie n\'apporte que des ennuis, je vous le dis ! Je ne sais pas pourquoi ce Grimrizz a été autorisé à s\'installer à la sortie de la Distillerie Tonnebière ! Ne vous en approchez pas !', Text1_1_loc2 = 'Ce genre de magie n\'apporte que des ennuis, je vous le dis ! Je ne sais pas pourquoi ce Grimrizz a été autorisé à s\'installer à la sortie de la Distillerie Tonnebière ! Ne vous en approchez pas !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4298;
UPDATE locales_npc_text SET entry = 4299, Text0_0_loc2 = 'Ah ! Ça, c\'est parlé ! Je parie que Granis Vivehache pourrait vous apprendre une chose ou deux sur la manière de faire tournoyer l\'acier. Il est à la Distillerie Tonnebière de Kharanos à cette heure.', Text0_1_loc2 = 'Ah ! Ça, c\'est parlé ! Je parie que Granis Vivehache pourrait vous apprendre une chose ou deux sur la manière de faire tournoyer l\'acier. Il est à la Distillerie Tonnebière de Kharanos à cette heure.', Text1_0_loc2 = 'Ce Granis Vivehache pourrait boire à en rouler sous la table et néanmoins manier sa hache avec habileté. Si vous cherchez de l\'entraînement, essayez quand même de lui parler tant qu\'il est compréhensible.', Text1_1_loc2 = 'Ce Granis Vivehache pourrait boire à en rouler sous la table et néanmoins manier sa hache avec habileté. Si vous cherchez de l\'entraînement, essayez quand même de lui parler tant qu\'il est compréhensible.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4299;
