UPDATE locales_npc_text SET entry = 4301, Text0_0_loc2 = 'Non, pas d\'alchimistes ici, à Dun Morogh. Vous les trouverez à Forgefer... Essayez les gnomes de Brikabrok.', Text0_1_loc2 = 'Non, pas d\'alchimistes ici, à Dun Morogh. Vous les trouverez à Forgefer... Essayez les gnomes de Brikabrok.', Text1_0_loc2 = 'Il paraît qu\'il y a une échoppe à Forgefer, tenue par des gnomes, spécialisée dans l\'alchimie. Allez y jeter un coup d\'œil.', Text1_1_loc2 = 'Il paraît qu\'il y a une échoppe à Forgefer, tenue par des gnomes, spécialisée dans l\'alchimie. Allez y jeter un coup d\'œil.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4301;
UPDATE locales_npc_text SET entry = 4302, Text0_0_loc2 = 'Och ! Ça, c\'est une compétence digne de ce nom ! Tognus utilise l\'enclume comme personne. Allez donc lui parler et voyez ce qu\'il peut vous enseigner.', Text0_1_loc2 = 'Och ! Ça, c\'est une compétence digne de ce nom ! Tognus utilise l\'enclume comme personne. Allez donc lui parler et voyez ce qu\'il peut vous enseigner.', Text1_0_loc2 = 'Vous entendez sans doute Tognus battre l\'enclume quand vous êtes à Kharanos. Cela dit, si vous ne vous êtes pas lavé les oreilles depuis longtemps, allez jeter un coup d\'œil à la Forge.', Text1_1_loc2 = 'Vous entendez sans doute Tognus battre l\'enclume quand vous êtes à Kharanos. Cela dit, si vous ne vous êtes pas lavé les oreilles depuis longtemps, allez jeter un coup d\'œil à la Forge.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4302;
UPDATE locales_npc_text SET entry = 4303, Text0_0_loc2 = 'Une pinte de Tonnebière et un petit plat de Gremlock, voilà un bon repas ! Cherchez le cuistot si vous voulez apprendre à cuisinier.', Text0_1_loc2 = 'Une pinte de Tonnebière et un petit plat de Gremlock, voilà un bon repas ! Cherchez le cuistot si vous voulez apprendre à cuisinier.', Text1_0_loc2 = 'Gremlock n\'est pas toujours bien chanceux en cuisine, mais il a de bonnes recettes qui méritent d\'être apprises. Allez lui parler à la Distillerie Tonnebière et voyez ce qu\'il peut vous enseigner.', Text1_1_loc2 = 'Gremlock n\'est pas toujours bien chanceux en cuisine, mais il a de bonnes recettes qui méritent d\'être apprises. Allez lui parler à la Distillerie Tonnebière et voyez ce qu\'il peut vous enseigner.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4303;
UPDATE locales_npc_text SET entry = 4304, Text0_0_loc2 = 'Pour autant que je sache, il n\'y a pas d\'enchanteurs dans Dun Morogh. Il va falloir aller à Forgefer si vous voulez apprendre la profession d\'enchanteur.', Text0_1_loc2 = 'Pour autant que je sache, il n\'y a pas d\'enchanteurs dans Dun Morogh. Il va falloir aller à Forgefer si vous voulez apprendre la profession d\'enchanteur.', Text1_0_loc2 = 'Allez donc à Forgefer si vous voulez en apprendre plus sur les enchantements. Je crois y avoir vu une échoppe près du maître des griffons la dernière fois que j\'y suis allé.', Text1_1_loc2 = 'Allez donc à Forgefer si vous voulez en apprendre plus sur les enchantements. Je crois y avoir vu une échoppe près du maître des griffons la dernière fois que j\'y suis allée.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4304;
UPDATE locales_npc_text SET entry = 4305, Text0_0_loc2 = 'Cherchez donc Bronk au Dépôt de Grillacier. Quand il ne bricole pas quelque machin bizarre, il discute généralement avec les pilotes sur leurs chars à vapeur.', Text0_1_loc2 = 'Cherchez donc Bronk au Dépôt de Grillacier. Quand il ne bricole pas quelque machin bizarre, il discute généralement avec les pilotes sur leurs chars à vapeur.', Text1_0_loc2 = 'Croyez-moi, depuis qu\'il fait ce métier, Bronk a toutes les vis ! Mais personne n\'est meilleur ingénieur que lui. Allez donc lui rendre une petite visite au Dépôt de Grillacier.', Text1_1_loc2 = 'Croyez-moi, depuis qu\'il fait ce métier, Bronk a toutes les vis ! Mais personne n\'est meilleur ingénieur que lui. Allez donc lui rendre une petite visite au Dépôt de Grillacier.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4305;
UPDATE locales_npc_text SET entry = 4306, Text0_0_loc2 = 'Le secourisme, ça ne vaut peut-être pas les secours prodigués par les prêtres, mais c\'est quand même utile. Allez donc parler à Thamner dans la Distillerie Tonnebière, il pourra sans doute vous enseigner bien des choses.', Text0_1_loc2 = 'Le secourisme, ça ne vaut peut-être pas les secours prodigués par les prêtres, mais c\'est quand même utile. Allez donc parler à Thamner dans la Distillerie Tonnebière, il pourra sans doute vous enseigner bien des choses.', Text1_0_loc2 = 'Ce vieux Thamner m\'a bandé, l\'autre jour, quand j\'ai échappé de justesse à un mauvais trogg. Je suis comme neuf, je vous le dis ! J\'ai la conviction que Thamner sera enchanté de vous apprendre à faire de bons bandages.', Text1_1_loc2 = 'Ce vieux Thamner m\'a bandée, l\'autre jour, quand j\'ai échappé de justesse à un mauvais trogg. Je suis comme neuve, je vous le dis ! J\'ai la conviction que Thamner sera enchanté de vous apprendre à faire de bons bandages.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4306;
UPDATE locales_npc_text SET entry = 4307, Text0_0_loc2 = 'On ne peut guère pêcher par ici, mais si quelqu\'un y peut quelque chose, c\'est bien Paxton ! Vous le trouverez en train de pêcher au bord du Lac glacial.', Text0_1_loc2 = 'On ne peut guère pêcher par ici, mais si quelqu\'un y peut quelque chose, c\'est bien Paxton ! Vous le trouverez en train de pêcher au bord du Lac glacial.', Text1_0_loc2 = 'Il n\'y a pas beaucoup de bons coins de pêche dans les environs. Paxton les connaît tous et il y en a sans doute dont il ne vous parlera pas. Allez donc au Lac glacial et voyez ce qu\'il vous dira.', Text1_1_loc2 = 'Il n\'y a pas beaucoup de bons coins de pêche dans les environs. Paxton les connaît tous et il y en a sans doute dont il ne vous parlera pas. Allez donc au Lac glacial et voyez ce qu\'il vous dira.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4307;
UPDATE locales_npc_text SET entry = 4308, Text0_0_loc2 = 'Quelle surprise de voir de petites fleurs pousser même sous le rude climat de Dun Morogh... Mais vous ne trouverez pas d\'herboristes par ici. Il vous faudra aller à Forgefer pour vous former à ce métier.', Text0_1_loc2 = 'Quelle surprise de voir de petites fleurs pousser même sous le rude climat de Dun Morogh... Mais vous ne trouverez pas d\'herboristes par ici. Il vous faudra aller à Forgefer pour vous former à ce métier.', Text1_0_loc2 = 'Non, personne ne peut vous apprendre l\'herboristerie ici. Je pense que vous y arriverez mieux à Forgefer.', Text1_1_loc2 = 'Non, personne ne peut vous apprendre l\'herboristerie ici. Je pense que vous y arriverez mieux à Forgefer.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4308;
UPDATE locales_npc_text SET entry = 4310, Text0_0_loc2 = 'Si vous voulez apprendre à travailler le cuir, il va vous falloir aller à Forgefer. Il paraît qu\'il y a une sympathique échoppe près de la Grande Forge où vous pourrez apprendre le métier.', Text0_1_loc2 = 'Si vous voulez apprendre à travailler le cuir, il va vous falloir aller à Forgefer. Il paraît qu\'il y a une sympathique échoppe près de la Grande Forge où vous pourrez apprendre le métier.', Text1_0_loc2 = 'Forgefer est le meilleur endroit pour se perfectionner en travail du cuir. Si je me souviens bien, il y a une échoppe du côté de la Grande Forge où vous trouverez votre bonheur.', Text1_1_loc2 = 'Forgefer est le meilleur endroit pour se perfectionner en travail du cuir. Si je me souviens bien, il y a une échoppe du côté de la Grande Forge où vous trouverez votre bonheur.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4310;
UPDATE locales_npc_text SET entry = 4311, Text0_0_loc2 = 'Vous ne pensiez pas trouver une ville sans mineur, n\'est-ce pas ? Yarr se trouve généralement au Dépôt de Grillacier, à l\'extérieur de Kharanos.', Text0_1_loc2 = 'Vous ne pensiez pas trouver une ville sans mineur, n\'est-ce pas ? Yarr se trouve généralement au Dépôt de Grillacier, à l\'extérieur de Kharanos.', Text1_0_loc2 = 'Un mineur ? Il suffit de demander où en trouver un ! Yarr, c\'est lui que vous cherchez. Vous le trouverez au Dépôt de Grillacier.', Text1_1_loc2 = 'Un mineur ? Il suffit de demander où en trouver un ! Yarr, c\'est lui que vous cherchez. Vous le trouverez au Dépôt de Grillacier.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4311;
UPDATE locales_npc_text SET entry = 4312, Text0_0_loc2 = 'Il n\'y a pas de dépeceurs à Dun Morogh $gmon garçon : ma petite;, ils sont presque tous à Forgefer si c\'est un apprentissage qui vous intéresse.', Text0_1_loc2 = 'Il n\'y a pas de dépeceurs à Dun Morogh $gmon garçon:ma petite;, ils sont presque tous à Forgefer si c\'est un apprentissage qui vous intéresse.', Text1_0_loc2 = 'Les dépeceurs apportent généralement leurs peaux à Forgefer pour les y faire sécher. Vous devriez vous y rendre et voir qui peut vous entraîner !', Text1_1_loc2 = 'Les dépeceurs apportent généralement leurs peaux à Forgefer pour les y faire sécher. Vous devriez vous y rendre et voir qui peut vous entraîner !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4312;
UPDATE locales_npc_text SET entry = 4313, Text0_0_loc2 = 'Ah, Front-de-pierre ne quitte pas beaucoup Forgefer ces temps-ci. Si vous voulez devenir tailleur, c\'est lui qu\'il faut aller voir à Forgefer.', Text0_1_loc2 = 'Ah, Front-de-pierre ne quitte pas beaucoup Forgefer ces temps-ci. Si vous voulez devenir tailleur, c\'est lui qu\'il faut aller voir à Forgefer.', Text1_0_loc2 = 'Ma foi, vous avez peu de chances de trouver un tailleur ici. Forgefer en abrite quelques-uns, en revanche. Allez-y et demandez de l\'aide à un garde.', Text1_1_loc2 = 'Ma foi, vous avez peu de chances de trouver un tailleur ici. Forgefer en abrite quelques-uns, en revanche. Allez-y et demandez de l\'aide à un garde.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4313;
UPDATE locales_npc_text SET entry = 4316, Text0_0_loc2 = 'Il y a bien des endroits à visiter à Teldrassil. Dites-moi ce que vous cherchez.', Text0_1_loc2 = 'Il y a bien des endroits à visiter à Teldrassil. Dites-moi ce que vous cherchez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4316;
UPDATE locales_npc_text SET entry = 4317, Text0_0_loc2 = 'La banque se trouve dans la sereine cité de Darnassus. C\'est là que vous devez aller pour le dépôt de vos biens.', Text0_1_loc2 = 'La banque se trouve dans la sereine cité de Darnassus. C\'est là que vous devez aller pour le dépôt de vos biens.', Text1_0_loc2 = 'Si c\'est une banque que vous cherchez, alors rendez-vous au cœur de Darnassus. Vous y trouverez un endroit où stocker vos trésors.', Text1_1_loc2 = 'Si c\'est une banque que vous cherchez, alors rendez-vous au cœur de Darnassus. Vous y trouverez un endroit où stocker vos trésors.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4317;
UPDATE locales_npc_text SET entry = 4318, Text0_0_loc2 = 'Pour atteindre le bac de Rut\'theran, vous devez vous rendre à Darnassus et prendre le portail vers le village de Rut\'theran, au pied de Teldrassil.', Text0_1_loc2 = 'Pour atteindre le bac de Rut\'theran, vous devez vous rendre à Darnassus et prendre le portail vers le village de Rut\'theran, au pied de Teldrassil.', Text1_0_loc2 = 'Le bac de Rut\'theran part pour Auberdine depuis le village de Rut\'theran, à la base de Teldrassil. Le portail de Darnassus vous permettra d\'atteindre tranquillement Rut\'theran.', Text1_1_loc2 = 'Le bac de Rut\'theran part pour Auberdine depuis le village de Rut\'theran, à la base de Teldrassil. Le portail de Darnassus vous permettra d\'atteindre tranquillement Rut\'theran.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4318;
UPDATE locales_npc_text SET entry = 4319, Text0_0_loc2 = 'De telles affaires ne sont traitées qu\'à Darnassus. Vous devez vous y rendre pour commencer à créer votre guilde.', Text0_1_loc2 = 'De telles affaires ne sont traitées qu\'à Darnassus. Vous devez vous y rendre pour commencer à créer votre guilde.', Text1_0_loc2 = 'Les documents administratifs pour la création d\'une guilde sont disponibles auprès du maître des guildes, à Darnassus.', Text1_1_loc2 = 'Les documents administratifs pour la création d\'une guilde sont disponibles auprès du maître des guildes, à Darnassus.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4319;
UPDATE locales_npc_text SET entry = 4320, Text0_0_loc2 = 'Un repos paisible, c\'est ce que vous trouverez dans les lits de l\'aubergiste Keldamyr, à Dolanaar.', Text0_1_loc2 = 'Un repos paisible, c\'est ce que vous trouverez dans les lits de l\'aubergiste Keldamyr, à Dolanaar.', Text1_0_loc2 = 'Si vous voulez du temps et du repos pour retrouver des forces, le mieux est de vous rendre à l\'auberge de Dolanaar. L\'aubergiste Keldamyr s\'occupera bien de vous.', Text1_1_loc2 = 'Si vous voulez du temps et du repos pour retrouver des forces, le mieux est de vous rendre à l\'auberge de Dolanaar. L\'aubergiste Keldamyr s\'occupera bien de vous.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4320;
UPDATE locales_npc_text SET entry = 4322, Text0_0_loc2 = 'Vous trouverez de nombreux maîtres autour de Dolanaar, riches d\'années d\'expérience qu\'ils vous transmettront. Lequel cherchez-vous plus précisément ?', Text0_1_loc2 = 'Vous trouverez de nombreux maîtres autour de Dolanaar, riches d\'années d\'expérience qu\'ils vous transmettront. Lequel cherchez-vous plus précisément ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4322;
UPDATE locales_npc_text SET entry = 4323, Text0_0_loc2 = 'Vous cherchez l\'aide d\'un druide ? Alors allez voir Kal au puits de lune, au sud de l\'auberge de Dolanaar.', Text0_1_loc2 = 'Vous cherchez l\'aide d\'un druide ? Alors allez voir Kal au puits de lune, au sud de l\'auberge de Dolanaar.', Text1_0_loc2 = 'Le druide, Kal, passe bien du temps au puits de lune de Dolanaar. J\'ai la conviction qu\'il pourra vous proposer l\'apprentissage que vous recherchez.', Text1_1_loc2 = 'Le druide, Kal, passe bien du temps au puits de lune de Dolanaar. J\'ai la conviction qu\'il pourra vous proposer l\'apprentissage que vous recherchez.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4323;
UPDATE locales_npc_text SET entry = 4324, Text0_0_loc2 = 'Si c\'est un chasseur que vous cherchez, c\'est Dazalar que vous devez voir. Vous le trouverez juste à l\'extérieur de l\'auberge de Dolanaar.', Text0_1_loc2 = 'Si c\'est un chasseur que vous cherchez, c\'est Dazalar que vous devez voir. Vous le trouverez juste à l\'extérieur de l\'auberge de Dolanaar.', Text1_0_loc2 = 'Dazalar est bien connu autour de Dolanaar pour ses aptitudes de chasseur. Allez le voir et demandez-lui ce qu\'il peut vous apprendre.', Text1_1_loc2 = 'Dazalar est bien connu autour de Dolanaar pour ses aptitudes de chasseur. Allez le voir et demandez-lui ce qu\'il peut vous apprendre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4324;
UPDATE locales_npc_text SET entry = 4325, Text0_0_loc2 = 'Une prêtresse du nom de Laurna Clairematin s\'est établie à Dolanaar. Je pense que vous la trouverez au nord de l\'auberge.', Text0_1_loc2 = 'Une prêtresse du nom de Laurna Clairematin s\'est établie à Dolanaar. Je pense que vous la trouverez au nord de l\'auberge.', Text1_0_loc2 = 'Laurna Clairematin propose des soins à ceux qui vivent près de Dolanaar. Elle pourra peut-être vous guider sur la voie des prêtres.', Text1_1_loc2 = 'Laurna Clairematin propose des soins à ceux qui vivent près de Dolanaar. Elle pourra peut-être vous guider sur la voie des prêtres.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4325;
UPDATE locales_npc_text SET entry = 4326, Text0_0_loc2 = 'Un voleur ? Je n\'ai pas l\'habitude de faire référence à ces gens-là, mais il me semble que Jannok est celui que vous cherchez. Vous le trouverez à l\'auberge de Dolanaar.', Text0_1_loc2 = 'Un voleur ? Je n\'ai pas l\'habitude de faire référence à ces gens-là, mais il me semble que Jannok est celui que vous cherchez. Vous le trouverez à l\'auberge de Dolanaar.', Text1_0_loc2 = 'Vous cherchez un voleur ? Je pense que Jannok correspond à cette description. Allez donc lui parler à l\'auberge de Dolanaar.', Text1_1_loc2 = 'Vous cherchez un voleur ? Je pense que Jannok correspond à cette description. Allez donc lui parler à l\'auberge de Dolanaar.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4326;
UPDATE locales_npc_text SET entry = 4327, Text0_0_loc2 = 'Kyra Lamevent manie l\'épée avec grand talent. Elle devrait pouvoir vous donner des conseils judicieux. Vous la dénicherez à l\'auberge de Dolanaar.', Text0_1_loc2 = 'Kyra Lamevent manie l\'épée avec grand talent. Elle devrait pouvoir vous donner des conseils judicieux. Vous la dénicherez à l\'auberge de Dolanaar.', Text1_0_loc2 = 'J\'ai eu le plaisir, une fois, de voir Kyra Lamevent combattre. Je n\'ai pas de doute qu\'elle puisse bien vous instruire. Allez lui rendre visite à l\'auberge de Dolanaar.', Text1_1_loc2 = 'J\'ai eu le plaisir, une fois, de voir Kyra Lamevent combattre. Je ne doute pas qu\'elle puisse bien vous instruire. Allez lui rendre visite à l\'auberge de Dolanaar.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4327;
UPDATE locales_npc_text SET entry = 4328, Text0_0_loc2 = 'La route de ceux qui choisissent une profession est longue mais riche de récompenses. Quelle voie voulez-vous emprunter ?', Text0_1_loc2 = 'La route de ceux qui choisissent une profession est longue mais riche de récompenses. Quelle voie voulez-vous emprunter ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4328;
UPDATE locales_npc_text SET entry = 4329, Text0_0_loc2 = 'Cyndra n\'est pas aussi pétillante que ses potions, mais elle est douce dans l\'enseignement de son art. Vous la trouverez à l\'est de l\'auberge de Dolanaar.', Text0_1_loc2 = 'Cyndra n\'est pas aussi pétillante que ses potions, mais elle est douce dans l\'enseignement de son art. Vous la trouverez à l\'est de l\'auberge de Dolanaar.', Text1_0_loc2 = 'La connaissance qu\'a Cyndra de l\'alchimie est quelque peu limitée, mais elle suffira sûrement à vous transmettre les bases. Elle pratique son art à l\'est de l\'auberge de Dolanaar.', Text1_1_loc2 = 'La connaissance qu\'a Cyndra de l\'alchimie est quelque peu limitée, mais elle suffira sûrement à vous transmettre les bases. Elle pratique son art à l\'est de l\'auberge de Dolanaar.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4329;
UPDATE locales_npc_text SET entry = 4330, Text0_0_loc2 = 'Zarrin semble bien connaître l\'art de la cuisine. Allez le voir, il vous apprendra peut-être quelques recettes. Il cuisine généralement à l\'auberge de Dolanaar.', Text0_1_loc2 = 'Zarrin semble bien connaître l\'art de la cuisine. Allez le voir, il vous apprendra peut-être quelques recettes. Il cuisine généralement à l\'auberge de Dolanaar.', Text1_0_loc2 = 'On aurait bien besoin d\'un autre cuistot à Dolanaar, pas que la cuisine de Zarrin soit mauvaise, remarquez. Allez donc voir ce qu\'il peut vous enseigner !', Text1_1_loc2 = 'On aurait bien besoin d\'un autre cuistot à Dolanaar, pas que la cuisine de Zarrin soit mauvaise, remarquez. Allez donc voir ce qu\'il peut vous enseigner !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4330;
UPDATE locales_npc_text SET entry = 4331, Text0_0_loc2 = 'Alanna Œil-de-corbeau consacre une partie de ses talents à approfondir sa communion avec les esprits de la Clairière de l\'Oracle. Vous devriez pouvoir l\'y trouver bien vite.', Text0_1_loc2 = 'Alanna Œil-de-corbeau consacre une partie de ses talents à approfondir sa communion avec les esprits de la Clairière de l\'Oracle. Vous devriez pouvoir l\'y trouver bien vite.', Text1_0_loc2 = 'Alanna passe la majeure partie de son temps à la Clairière de l\'Oracle, où elle exerce sa profession. Si vous souhaitez devenir enchanteur, vous devrez aller la trouver là-bas.', Text1_1_loc2 = 'Alanna passe la majeure partie de son temps à la Clairière de l\'Oracle, où elle exerce sa profession. Si vous souhaitez devenir enchanteur, vous devrez aller la trouver là-bas.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4331;
UPDATE locales_npc_text SET entry = 4332, Text0_0_loc2 = 'Byancie loue souvent ses services aux sentinelles qui patrouillent dans la nature sauvage. Parlez-lui si vous voulez apprendre le secourisme.', Text0_1_loc2 = 'Byancie loue souvent ses services aux sentinelles qui patrouillent dans la nature sauvage. Parlez-lui si vous voulez apprendre le secourisme.', Text1_0_loc2 = 'Les aptitudes au secourisme de Byancie se sont montrées utiles en plus d\'une occasion. Vous la trouverez au nord de l\'auberge de Dolanaar si vous voulez profiter de son enseignement.', Text1_1_loc2 = 'Les aptitudes au secourisme de Byancie se sont montrées utiles en plus d\'une occasion. Vous la trouverez au nord de l\'auberge de Dolanaar si vous voulez profiter de son enseignement.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4332;
UPDATE locales_npc_text SET entry = 4333, Text0_0_loc2 = 'Tous les pêcheurs sont allés à Darnassus après les récentes attaques du Lac Al\'Ameth. Cherchez-y quelqu\'un qui sera susceptible de vous apprendre quelque chose.', Text0_1_loc2 = 'Tous les pêcheurs sont allés à Darnassus après les récentes attaques du Lac Al\'Ameth. Cherchez-y quelqu\'un qui sera susceptible de vous apprendre quelque chose.', Text1_0_loc2 = 'À cause des sylvains, il est devenu difficile pour les pêcheurs d\'attraper quoi que ce soit du côté de Dolanaar. Cherchez plutôt à Darnassus quelqu\'un qui puisse vous aider.', Text1_1_loc2 = 'À cause des sylvains, il est devenu difficile pour les pêcheurs d\'attraper quoi que ce soit du côté de Dolanaar. Cherchez plutôt à Darnassus quelqu\'un qui puisse vous aider.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4333;
UPDATE locales_npc_text SET entry = 4334, Text0_0_loc2 = 'Malorne parvient à prendre les herbes les plus délicates sans les abîmer. C\'est lui sûrement qui pourra vous instruire. Il vit à l\'est de l\'auberge de Dolanaar.', Text0_1_loc2 = 'Malorne parvient à prendre les herbes les plus délicates sans les abîmer. C\'est lui sûrement qui pourra vous instruire. Il vit à l\'est de l\'auberge de Dolanaar.', Text1_0_loc2 = 'L\'Herboristerie requiert qu\'on ait la main verte et peu peuvent s\'en targuer comme Malorne. Cherchez-le à l\'est de l\'auberge de Dolanaar.', Text1_1_loc2 = 'L\'Herboristerie requiert qu\'on ait la main verte et peu peuvent s\'en targuer comme Malorne. Cherchez-le à l\'est de l\'auberge de Dolanaar.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4334;
UPDATE locales_npc_text SET entry = 4335, Text0_0_loc2 = 'Les Tissecrin pratiquent leur profession sur le seuil de leur maison, à Darnassus. Nadyia est un excellent professeur en ce qui concerne le travail du cuir. Allez voir ce qu\'elle peut vous enseigner.', Text0_1_loc2 = 'Les Tissecrin pratiquent leur profession sur le seuil de leur maison, à Darnassus. Nadyia est un excellent professeur en ce qui concerne le travail du cuir. Allez voir ce qu\'elle peut vous enseigner.', Text1_0_loc2 = 'Nadyia a pas mal travaillé le cuir au cours de ses années d\'apprentissage. Allez vers l\'ouest sur la route de Darnassus pour la trouver, elle voudra peut-être bien vous transmettre son savoir.', Text1_1_loc2 = 'Nadyia a pas mal travaillé le cuir au cours de ses années d\'apprentissage. Allez vers l\'ouest sur la route de Darnassus pour la trouver, elle voudra peut-être bien vous transmettre son savoir.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4335;
UPDATE locales_npc_text SET entry = 4336, Text0_0_loc2 = 'Vous trouverez Radnaal pratiquant son art devant sa maison, sur la route de Darnassus. Allez donc voir ce qu\'il peut vous apprendre.', Text0_1_loc2 = 'Vous trouverez Radnaal pratiquant son art devant sa maison, sur la route de Darnassus. Allez donc voir ce qu\'il peut vous apprendre.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4336;
UPDATE locales_npc_text SET entry = 4337, Text0_0_loc2 = 'Je pense que vous trouverez un Tailleur susceptible de vous aider à Darnassus. Lorsque vous arriverez, demandez à l\'une des Sentinelles de Darnassus de vous guider.', Text0_1_loc2 = 'Je pense que vous trouverez un Tailleur susceptible de vous aider à Darnassus. Lorsque vous arriverez, demandez à l\'une des Sentinelles de Darnassus de vous guider.', Text1_0_loc2 = 'Bien des matériaux dont les tailleurs ont besoin viennent de Darnassus. Pas étonnant qu\'ils soient tous allés y travailler. Si vous voulez être tailleur, c\'est à Darnassus que vous devez vous rendre.', Text1_1_loc2 = 'Bien des matériaux dont les tailleurs ont besoin viennent de Darnassus. Pas étonnant qu\'ils soient tous allés y travailler. Si vous voulez être tailleur, c\'est à Darnassus que vous devez vous rendre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4337;
UPDATE locales_npc_text SET entry = 4353, Text0_0_loc2 = 'Bonjour, $C. Je me suis établi ici, à la Barricade, pour me préparer à mon voyage dans les Maleterres. Il paraît qu\'un grand mage humain s\'est installé à Âtreval il y a quelque temps. La Croisade écarlate lui a donné pour mission de fabriquer les arcs et les fusils de ses guerriers et chasseurs. On dit qu\'il a fabriqué une arme remarquable avant d\'être tué par la Croisade.$B$BJe pense qu\'ils ont de moins en moins confiance dans les arcanes.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4353;
UPDATE locales_npc_text SET entry = 4354, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $c. Je n\'ai guère le temps de parler, je ne me sers du camp du Noroît que pour me reposer avant de partir pour Âtreval. On dit qu\'avant de devenir fous, les types de la Croisade écarlate avaient un mage avec eux, qui essayait de créer des arcs et des fusils puissants pour leurs troupes. J\'ignore s\'il a réussi ou non... La Croisade l\'a tué avant qu\'il n\'ait terminé sa tâche. Peut-être l\'ont-ils éliminé pour conserver un secret... ou peut-être haïssent-ils les arcanes...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4354;
UPDATE locales_npc_text SET entry = 4355, Text0_0_loc2 = null, Text0_1_loc2 = 'Heu... Bonjour, $N. Mille pardons, je me remets de mes blessures.$B$BCes types de la Croisade écarlate sont plus solides que je ne pensais. Ils m\'ont presque eue.$B$BFaites bien attention, vous aussi. Je ne voudrais pas qu\'ils vous fassent du mal.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4355;
UPDATE locales_npc_text SET entry = 4356, Text0_0_loc2 = null, Text0_1_loc2 = 'Ma famille travaillait dans la scierie de la Crête du nord, dans ce qu\'on appelle maintenant les Maleterres de l\'ouest. Ils étaient dévoués à la famille Dumah, et maintenant la Croisade écarlate s\'en est emparée avec tout Âtreval !$B$BJe n\'aurai pas de repos tant que ces fanatiques n\'auront pas été chassés d\'Azeroth.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4356;
UPDATE locales_npc_text SET entry = 4357, Text0_0_loc2 = 'Il y a longtemps, avant la dernière grande guerre, mon père dirigeait la scierie de la Crête du nord. C\'était sa vie, et il s\'y consacrait complètement. Puis la guerre est venue et, la mort dans l\'âme, nous avons dû fuir à Hurlevent.$B$BNous avons perdu bien des amis et des proches à l\'époque, et ça me fait mal au cœur de savoir que la Croisade écarlate se sert de notre scierie.$B$BC\'est pour ça que je suis revenu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4357;
UPDATE locales_npc_text SET entry = 4393, Text0_0_loc2 = 'Nous sommes les derniers des furbolgs... La tribu Grumegueule reste pure, mais les étrangers nous témoignent la même hostilité qu\'aux corrompus. La plupart des gens pensent qu\'aucun furbolg n\'a pu échapper à la corruption...$B$BFurieux contre ceux qui vous considèrent trop vite comme des ennemis, mes frères attaquent désormais quiconque approche, ils ne font confiance à personne.$B$BMais peut-être, si vous démontrez que vous êtes là en paix, vous considéreront-ils comme un allié ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4393;
UPDATE locales_npc_text SET entry = 4394, Text0_0_loc2 = 'Vous devez trouver un moyen de leur prouver que vous combattez dans leur camp. Les Grumegueules seront hostiles au début, mais si vous faites vite, vous trouverez sûrement un moyen...$B$BRappelez-vous que toute attaque sur les furbolgs Grumegueules réduit considérablement vos chances de vous en faire des alliés.$B$BJe vous suggère de commencer auprès des furbolgs Mort-bois qu\'on trouve un peu partout dans Gangrebois. Ces furbolgs sont les ennemis de la tribu Grumegueules, et les éliminer augmentera votre réputation auprès de ces derniers.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4394;
UPDATE locales_npc_text SET entry = 4395, Text0_0_loc2 = 'Nous autres, les Grumegueules, pouvons vous aider à passer en sécurité, mais vous devez nous prouver d\'abord votre amitié.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4395;
UPDATE locales_npc_text SET entry = 4396, Text0_0_loc2 = 'Les furbolgs Tombe-hiver qui résident ici, au Berceau-de-l\'Hiver, furent naguère nos alliés... Mais tout a changé.$B$BSuivant les ordres étranges de leur Grand Chef, ils ont commencé à attaquer quiconque entrait en contact avec eux, y compris notre tribu. Ils restent entre eux désormais...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4396;
UPDATE locales_npc_text SET entry = 4398, Text0_0_loc2 = 'Je ne fais guère confiance aux gens de votre espèce, $N. Mais je vois bien que vous êtes $gun ami:une amie;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4398;
UPDATE locales_npc_text SET entry = 4399, Text0_0_loc2 = 'Oui, oui, je vous vois.$B$BPartez. Vous avez peut-être gagné la confiance de quelques-uns, mais il va en falloir plus pour me convaincre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4399;
UPDATE locales_npc_text SET entry = 4401, Text0_0_loc2 = 'Si vous démontrez que vous êtes $gun véritable allié:une véritable alliée;, j\'envisagerai de vous instruire...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4401;
UPDATE locales_npc_text SET entry = 4433, Text0_0_loc2 = 'La Lumière est avec vous, $N. Puis-je vous aider en quoi que ce soit ?', Text0_1_loc2 = 'La Lumière est avec vous, $N. Puis-je vous aider en quoi que ce soit ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4433;
UPDATE locales_npc_text SET entry = 4434, Text0_0_loc2 = 'Ne tournez pas le dos à la Lumière, $c, c\'est peut-être ce qui vous sauvera un jour.', Text0_1_loc2 = 'Ne tournez pas le dos à la Lumière, $c, c\'est peut-être ce qui vous sauvera un jour.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4434;
