UPDATE locales_npc_text SET entry = 4441, Text0_0_loc2 = 'Je ferai ce que je peux pour faire de vous un prêtre, si c\'est ça que vous voulez.', Text0_1_loc2 = 'Je ferai ce que je peux pour faire de vous un prêtre, si c\'est ça que vous voulez.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4441;
UPDATE locales_npc_text SET entry = 4442, Text0_0_loc2 = 'Que peut pour vous cet insignifiant serviteur, $N ? Vous entraîner peut-être ?', Text0_1_loc2 = 'Que peut pour vous cet insignifiant serviteur, $N ? Vous entraîner peut-être ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4442;
UPDATE locales_npc_text SET entry = 4443, Text0_0_loc2 = 'Je cherche quelqu\'un, $c. Laissez-moi... Il est important que je le trouve.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4443;
UPDATE locales_npc_text SET entry = 4444, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4444;
UPDATE locales_npc_text SET entry = 4445, Text0_0_loc2 = 'Silence ! Vous ne voyez pas que le Maître parle ?', Text0_1_loc2 = 'Silence ! Vous ne voyez pas que le maître parle ?', Text1_0_loc2 = 'Arrêtez de bavasser. C\'est l\'heure d\'écouter !', Text1_1_loc2 = 'Arrêtez de bavasser. C\'est l\'heure d\'écouter !', Text2_0_loc2 = 'Nous devons nous concentrer si nous voulons faire croître nos dragons !', Text2_1_loc2 = 'Nous devons nous concentrer si nous voulons faire croître nos dragons !', Text3_0_loc2 = 'Silence ! Je ne veux pas perdre le moindre moment de ce cours !', Text3_1_loc2 = 'Silence ! Je ne veux pas perdre le moindre moment de cette conférence !', Text4_0_loc2 = 'Vous avez oublié vos vêtements d\'étudiant ? Vous pensez que la mode est plus importante que la domination du Fléau ?', Text4_1_loc2 = 'Vous avez oublié vos vêtements d\'étudiant ? Vous pensez que la mode est plus importante que la domination du Fléau ?', Text5_0_loc2 = 'Vous avez fini les devoirs d\'hier ? Si oui, prenez place à côté de moi !', Text5_1_loc2 = 'Vous avez fini les devoirs d\'hier ? Si oui, prenez place à côté de moi !', Text6_0_loc2 = 'Faites attention où vous mettez les pieds, $gmon garçon : ma fille;. Le Maître voit tout...', Text6_1_loc2 = 'Faites attention où vous mettez les pieds, $gmon garçon : ma fille;. Le Maître voit tout...', Text7_0_loc2 = 'Vous avez apporté vos manuels ? Je peux regarder avec vous ?', Text7_1_loc2 = 'Vous avez apporté vos manuels ? Je peux regarder avec vous ?' WHERE entry = 4445;
UPDATE locales_npc_text SET entry = 4446, Text0_0_loc2 = 'Qu\'est-ce que vous faites ici ? Mettez vos vêtements d\'étudiant et étudiez !', Text0_1_loc2 = null, Text1_0_loc2 = 'Silence ! Vous croyez que nos dragons vont éclore et s\'entraîner tout seuls ?', Text1_1_loc2 = null, Text2_0_loc2 = 'Vous venez des Steppes ardentes ? Vous avez mes œufs de dragon noir ?', Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4446;
UPDATE locales_npc_text SET entry = 4447, Text0_0_loc2 = 'Vous ne vous concentrez pas. Seriez-vous en train de me cacher quelque chose ?', Text0_1_loc2 = null, Text1_0_loc2 = 'Je sens une perturbation qui vous entoure...', Text1_1_loc2 = null, Text2_0_loc2 = 'Votre futur est obscur. Concentrez-vous et demandez encore...', Text2_1_loc2 = null, Text3_0_loc2 = 'Vous ne ressemblez pas à $gun étudiant:une étudiante;...', Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4447;
UPDATE locales_npc_text SET entry = 4449, Text0_0_loc2 = 'Ce kodo ne ressemble en rien à la bête que j\'ai attrapée. Je me demande si je peux utiliser sur moi le kombobulateur.', Text0_1_loc2 = 'Ce kodo ne ressemble en rien à la bête que j\'ai attrapée. Je me demande si je peux utiliser sur moi le kombobulateur.', Text1_0_loc2 = 'Pourquoi les petites créatures comme les gobelins sont-elles toujours fascinées par les créatures de plus grande taille ?', Text1_1_loc2 = 'Pourquoi les petites créatures comme les gobelins sont-elles toujours fascinées par les créatures de plus grande taille ?', Text2_0_loc2 = 'Comment les gobelins font-ils pour charger ces paquets sur ces bêtes ? Les kodos font au moins dix fois leur taille !', Text2_1_loc2 = 'Comment les gobelins font-ils pour charger ces paquets sur ces bêtes ? Les kodos font au moins dix fois leur taille !', Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4449;
UPDATE locales_npc_text SET entry = 4450, Text0_0_loc2 = null, Text0_1_loc2 = 'Les Maleterres sont un endroit parfait pour frapper le Fléau !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4450;
UPDATE locales_npc_text SET entry = 4473, Text0_0_loc2 = 'Salutations, $c. Vous pouvez me parler, à moins de n\'avoir à discuter directement avec ma maîtresse Magatha.$B$BJe m\'occupe de la plupart des choses pour elle, afin qu\'elle puisse se concentrer sur des choses plus importantes. Peut-être pouvons-nous parler ensemble avant que vous ne lui demandiez audience ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4473;
UPDATE locales_npc_text SET entry = 4474, Text0_0_loc2 = 'Bonjour, $N.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4474;
UPDATE locales_npc_text SET entry = 4475, Text0_0_loc2 = 'N\'hésitez pas à venir me parler, $N. Vous avez bien servi Magatha. Je pense qu\'elle fera de nouveau appel à vous dans le futur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4475;
UPDATE locales_npc_text SET entry = 4476, Text0_0_loc2 = 'N\'escomptez pas me parler comme à un égal, $c. Sylvanas est ma reine et je me soumets à son autorité.$B$BSi vous ne me montrez pas le respect approprié, je vous assure que nous trouverons pour vous une place adaptée, dans le Néant.$B$BMais nous n\'avons pas de raison de nous quereller, si vous souhaitez soutenir l\'action de la Dame noire...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4476;
UPDATE locales_npc_text SET entry = 4477, Text0_0_loc2 = 'Tous les membres de la Horde sont égaux à mes yeux, $N. Nous avons tous souffert de nombreux fardeaux et sans la sagesse et l\'honneur, nous n\'aurions pas plus de valeur que le Fléau ou que mon peuple lorsque notre sang était corrompu par le démon Mannoroth.$B$BIl est de votre devoir d\'aider la Horde et de défendre notre façon de vivre. Mais c\'est également votre devoir de savoir quand la pitié et la compassion doivent prendre le pas, même contre nos ennemis.$B$BComprenez bien cela. Nous sommes la nouvelle Horde, pas une armée démoniaque sans volonté propre.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4477;
UPDATE locales_npc_text SET entry = 4493, Text0_0_loc2 = 'Je dois m\'excuser de ne pas vous avoir dévoilé complètement mon identité. Je devais m\'assurer que je pouvais vous faire confiance. Rares sont ceux qui sont prêts à offrir une aide désintéressée à un vieil ermite oublié de tous.$B$BVous avez démontré que vous étiez l\'un d\'eux : une personne d\'intégrité et d\'honneur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4493;
UPDATE locales_npc_text SET entry = 4494, Text0_0_loc2 = 'Mon fils, Taelan, n\'était encore qu\'un enfant lorsqu\'on m\'a jugé pour trahison et banni de l\'Alliance, pour vivre une vie d\'exil. J\'ai choisi de rester ici afin de pouvoir le surveiller, de m\'assurer qu\'il grandirait pour devenir un homme honorable.$B$BComme je l\'avais espéré, il est devenu seigneur de Mardenholde à ma place, mais la cruelle ironie du destin a fait qu\'il rejoigne les rangs de la Croisade écarlate. Il en est même devenu le nouveau grand maître, $R.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4494;
UPDATE locales_npc_text SET entry = 4495, Text0_0_loc2 = 'En effet. La Croisade écarlate est une aberration née de l\'Ordre de la Main d\'argent.$B$BVous devez me croire, $R, Taelan est un homme bon. Il a besoin d\'être guidé. Il a besoin de se souvenir... De se souvenir de ce qui est noble et honorable. J\'ai la certitude qu\'au fond de son coeur, il sait que ce qu\'il fait est mal. Allez-vous l\'aider ? Allez-vous l\'aider à se souvenir ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4495;
UPDATE locales_npc_text SET entry = 4513, Text0_0_loc2 = 'Écoutez attentivement ce qu\'on vous dit et ne faites pas aveuglément confiance, ni ne vous méfiez trop vite. Car même les amis servent leurs propres intérêts, $c.$B$BNotre grand Chef de guerre le sait, et pour prouver qu\'il y a des démonistes dans la Horde qui ne souhaitent ni la détruire, ni en prendre le contrôle depuis l\'intérieur, je me suis porté volontaire pour soutenir notre jeune chef autant que je le peux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4513;
UPDATE locales_npc_text SET entry = 4516, Text0_0_loc2 = 'L\'endurance de Woo Ping est tout simplement incroyable. Quand je l\'ai observé s\'entraîner pendant des heures d\'affilée, j\'avais vraiment mal pour le mannequin d\'entraînement. Je ne connais personne de mieux placé pour vous entraîner au combat armé.', Text0_1_loc2 = 'L\'endurance de Woo Ping est tout simplement incroyable. Quand je l\'ai observé s\'entraîner pendant des heures d\'affilée, j\'avais vraiment mal pour le mannequin d\'entraînement. Je ne connais personne de mieux placé pour vous entraîner au combat armé.', Text1_0_loc2 = 'Maître Woo Ping connaît presque toutes les armes créées en ce monde. Il est particulièrement redoutable en combat à l\'épée. Il passe pas mal de temps à l\'Arsenal de Weller, attendant de nouvelles livraisons d\'armes, si vous voulez qu\'il vous entraîne.', Text1_1_loc2 = 'Maître Woo Ping connaît presque toutes les armes créées en ce monde. Il est particulièrement redoutable en combat à l\'épée. Il passe pas mal de temps à l\'Arsenal de Weller, attendant de nouvelles livraisons d\'armes, si vous voulez qu\'il vous entraîne.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4516;
UPDATE locales_npc_text SET entry = 4517, Text0_0_loc2 = 'Au long des millénaires, Ilyenia Lunéclat a appris à maîtriser nombre d\'armes et elle propose désormais de former les autres. Vous la trouverez sur la Terrasse des guerriers si vous souhaitez vous entraîner.', Text0_1_loc2 = 'Au long des millénaires, Ilyenia Lunéclat a appris à maîtriser nombre d\'armes et elle propose désormais de former les autres. Vous la trouverez sur la Terrasse des guerriers si vous souhaitez vous entraîner.', Text1_0_loc2 = 'Ma formation aux armes, je la dois principalement à l\'entraînement d\'Ilyenia Lunéclat, à la Terrasse des guerriers. Vous devriez aller la voir si vous désirez apprendre à vous servir d\'une nouvelle arme.', Text1_1_loc2 = 'Ma formation aux armes, je la dois principalement à l\'entraînement d\'Ilyenia Lunéclat, à la Terrasse des guerriers. Vous devriez aller la voir si vous désirez apprendre à vous servir d\'une nouvelle arme.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4517;
UPDATE locales_npc_text SET entry = 4518, Text0_0_loc2 = 'Bixi et Buliwyf ont dû écraser ou taillader des orcs avec à peu près tous les types d\'armes existant. Vous les trouverez près des Armes de Timberline si vous avez besoin d\'une formation.', Text0_1_loc2 = 'Bixi et Buliwyf ont dû écraser ou taillader des orcs avec à peu près tous les types d\'armes existant. Vous les trouverez près des Armes de Timberline si vous avez besoin d\'une formation.', Text1_0_loc2 = 'Vous voulez apprendre à utiliser de nouvelles armes, pas vrai ? Ma foi, vous devriez parler à Bixi ou Buliwyf, aux Armes de Timberline. Ils savent tout utiliser, des arbalètes aux.. aux zweihanders !', Text1_1_loc2 = 'Vous voulez apprendre à utiliser de nouvelles armes, pas vrai ? Ma foi, vous devriez parler à Bixi ou Buliwyf, aux Armes de Timberline. Ils savent tout utiliser, des arbalètes aux.. aux zweihanders !', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4518;
UPDATE locales_npc_text SET entry = 4519, Text0_0_loc2 = 'Vous voulez apprendre à utiliser une nouvelle arme ? Trouvez Sayoc et Hanashi aux "Armes de légende". C\'est dans la Vallée de l\'Honneur.', Text0_1_loc2 = 'Vous voulez apprendre à utiliser une nouvelle arme ? Trouvez Sayoc et Hanashi aux "Armes de légende". C\'est dans la Vallée de l\'Honneur.', Text1_0_loc2 = 'Cherchez Sayoc et Hanashi dans la Vallée de l\'Honneur, à l\'intérieur des "Armes de légende". Tenez votre langue, car si vous lui parlez mal, Sayoc pourrait demander à Rictus de vous grignoter un peu.', Text1_1_loc2 = 'Cherchez Sayoc et Hanashi dans la Vallée de l\'Honneur, à l\'intérieur des "Armes de légende". Tenez votre langue, car si vous lui parlez mal, Sayoc pourrait demander à Rictus de vous grignoter un peu.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4519;
UPDATE locales_npc_text SET entry = 4520, Text0_0_loc2 = 'Ah ! Vous cherchez la sagesse d\'Ansekhwa. Vous le trouverez sur la Côte inférieure, aux Armes des Pitons du Tonnerre.', Text0_1_loc2 = 'Ah ! Vous cherchez la sagesse d\'Ansekhwa. Vous le trouverez sur la Côte inférieure, aux Armes des Pitons du Tonnerre.', Text1_0_loc2 = 'Personne ne manie aussi bien les armes qu\'Ansekhwa. On dit qu\'il est guidé par les esprits des meilleurs guerriers de nos ancêtres. Allez le voir sur la Côte inférieure et il vous enseignera ce que vous voulez savoir.', Text1_1_loc2 = 'Personne ne manie aussi bien les armes qu\'Ansekhwa. On dit qu\'il est guidé par les esprits des meilleurs guerriers de nos ancêtres. Allez le voir sur la Côte inférieure et il vous enseignera ce que vous voulez savoir.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4520;
UPDATE locales_npc_text SET entry = 4521, Text0_0_loc2 = 'Archibald, que vous cherchez, est devant la boutique d’un marchand d’armes, dans l’anneau intérieur du Quartier de la guerre.', Text0_1_loc2 = 'Archibald, que vous cherchez, est devant la boutique d’un marchand d’armes, dans l’anneau intérieur du Quartier de la guerre.', Text1_0_loc2 = 'Peu de Réprouvés connaître toutes les armes. Lui, Archibald. Vous trouver lui dans anneau intérieur du Quartier de guerre.', Text1_1_loc2 = 'Peu de Réprouvés connaître toutes les armes. Lui, Archibald. Vous trouver lui dans anneau intérieur du Quartier de guerre.', Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4521;
UPDATE locales_npc_text SET entry = 4522, Text0_0_loc2 = 'Il me paraît sage de vous expliquer à quelles profondeurs nos ennemis iront pour se cacher sous notre propre nez.$B$BSachez avant tout que notre ennemi est le Conseil des ombres. Quelle que soit la tâche que je vous donnerai, quel que soit celui contre qui je vous demanderai d\'agir, n\'oubliez pas cela.$B$BDe nombreux cultes existent au sein du Conseil, mais ils existent seulement pour camoufler ses agissements aux yeux des naïfs.$B$BDes groupes comme la Lame ardente, la Veille d\'Argus et la Lame brûlante ne sont que des pions du Conseil. Vous devriez les considérer comme un seul et même groupe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4522;
UPDATE locales_npc_text SET entry = 4533, Text0_0_loc2 = 'Ah, vous êtes des nôtres. Il est plaisant de constater que notre nombre croît si rapidement que je ne parviens pas à mémoriser tous les nouveaux visages.$B$BCet idiot de Thrall pense que je travaille dur pour améliorer l\'image des démonistes et prouver que nous sommes ses serviteurs. Imbécile ! La Légion ardente réduira ce monde en cendres après l\'avoir vidé de tout ce qu\'il contient.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4533;
UPDATE locales_npc_text SET entry = 4534, Text0_0_loc2 = 'Quelle naïveté. La Lame ardente EST le Conseil des ombres ! Le Conseil règne sur plusieurs cultes et organisations qui sont toutes des extensions de sa volonté. Je dirige peut-être la Lame ardente, mais je ne suis qu\'un pion pour le Conseil, comme vous n\'êtes qu\'un pion pour moi !$B$BPrenez les membres de la Lame brûlante : la plupart d\'entre eux pensent qu\'ils sont de puissants démonistes, qui bientôt prendront la place du Conseil des ombres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4534;
UPDATE locales_npc_text SET entry = 4535, Text0_0_loc2 = 'Ha ! À l\'exception de leurs chefs, le satyre Bazzalan et le démoniste Jergosh, oui, c\'est le cas pour tous ! C\'est pourquoi je leur envoie constamment des adorateurs de Thrall. Cela donne à ce dernier l\'impression que je travaille à éliminer le mal dans sa grande ville. La victoire tient en deux missions : tuez les fidèles de Thrall et testez la force de ceux de la Lame brûlante pour voir s\'ils méritent réellement de se joindre à moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4535;
UPDATE locales_npc_text SET entry = 4536, Text0_0_loc2 = 'Je vous le ferai savoir. Vous avez visiblement déjà fait vos preuves dans nos rangs pour devenir lieutenant si rapidement. Si vous reveniez me voir plus tard, j\'aurais sans doute à vous confier des missions. Le Conseil apprécie particulièrement ceux qui sont versés dans l\'art des arcanes, mais n\'importe qui est bienvenu s\'il est disposé à nous servir avec abnégation.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4536;
UPDATE locales_npc_text SET entry = 4573, Text0_0_loc2 = 'Faisant partie de la Ligue des explorateurs, j’ai écumé la région à la recherche de reliques. Le Berceau-de-l\'Hiver est connu pour abriter des objets de grande magie... La plupart sont, bien sûr, férocement gardés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4573;
UPDATE locales_npc_text SET entry = 4633, Text0_0_loc2 = null, Text0_1_loc2 = 'Les temps sont périlleux, voyagez avec prudence.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4633;
UPDATE locales_npc_text SET entry = 4634, Text0_0_loc2 = null, Text0_1_loc2 = 'Salut ! Qu’est-ce qui vous amène au Berceau-de-l\'Hiver ? Ces immenses terres gelées peuvent sembler sinistres, mais elles regorgent de secrets...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4634;
UPDATE locales_npc_text SET entry = 4635, Text0_0_loc2 = null, Text0_1_loc2 = 'Quelle tristesssss’…. Le passé, personne ne l’inversssss’…', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4635;
UPDATE locales_npc_text SET entry = 4673, Text0_0_loc2 = 'Prenez place et écoutez mon histoire, $R.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4673;
UPDATE locales_npc_text SET entry = 4693, Text0_0_loc2 = '$gSalut, beauté, vous cherchez à aller jusqu\'à Grom\'gol ? Il paraît que ça chauffe pas mal dans ces jungles, je pourrais peut-être prendre quelques vacances avec vous, vous montrer deux ou trois aspects... de la vie sauvage. : Allez ! Faites un voyage dans les magnifiques et luxuriantes jungles tropicales de Strangleronce. C\'est ça, vous aussi vous pourriez prendre le soleil près des eaux cristallines pendant que je reste ici sous un soleil de plomb, sans rien d\'autre à faire que de regarder toute la journée mon frère Frezza ! Bon voyage !;', Text0_1_loc2 = '$gSalut, beauté, vous cherchez à aller jusqu\'à Grom\'gol ? Il paraît que ça chauffe pas mal dans ces jungles, je pourrais peut-être prendre quelques vacances avec vous, vous montrer deux ou trois aspects... de la vie sauvage. : Allez ! Faites un voyage dans les magnifiques et luxuriantes jungles tropicales de Strangleronce. C\'est ça, vous aussi vous pourriez prendre le soleil près des eaux cristallines pendant que je reste ici sous un soleil de plomb, sans rien d\'autre à faire que de regarder toute la journée mon frère Frezza ! Bon voyage !;', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4693;
UPDATE locales_npc_text SET entry = 4694, Text0_0_loc2 = 'Glissez une petite pièce dans la main du chef et voyez où cela mène. J\'aime mon travail. Mais si je connaissais le moyen de faire affecter cette canaille de Snurk à ce port... Enfin, à quoi bon. Mais je parie que vous n\'êtes pas là pour parler de ça. Vous cherchez le zeppelin pour Fossoyeuse, non ? Eh bien c\'est parfait, parce qu\'on embarque là, juste derrière moi. Et si vous voyez Snurk, dites-lui bien des choses de ma part, d\'accord ?', Text0_1_loc2 = 'Glissez une petite pièce dans la main du chef et voyez où cela mène. J\'aime mon travail. Mais si je connaissais le moyen de faire affecter cette canaille de Snurk à ce port... Enfin, à quoi bon. Mais je parie que vous n\'êtes pas là pour parler de ça. Vous cherchez le zeppelin pour Fossoyeuse, non ? Eh bien c\'est parfait, parce qu\'on embarque là, juste derrière moi. Et si vous voyez Snurk, dites-lui bien des choses de ma part, d\'accord ?', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4694;
UPDATE locales_npc_text SET entry = 4697, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4697;
UPDATE locales_npc_text SET entry = 4713, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4713;
UPDATE locales_npc_text SET entry = 4714, Text0_0_loc2 = 'L\'esprit abattu de Lunegriffe vous regarde intensément, comme s\'il attendait quelque chose de vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4714;
UPDATE locales_npc_text SET entry = 4715, Text0_0_loc2 = 'L\'esprit vous regarde intensément et saisit votre main. Vous sentez une vague de chaleur déferler dans vos membres et vous tombez à genoux.$B$BLa vague passe et vous détectez en vous la présence de l\'esprit de l\'ours... Vous vous sentez soudain plus $gfort:forte;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4715;
UPDATE locales_npc_text SET entry = 4716, Text0_0_loc2 = 'Nous avons tous été témoins de tragédies, mais nous ne devons pas céder au désespoir.$B$BAu contraire, conservez précieusement ces souvenirs, pour qu\'un jour nous ayons notre revanche.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4716;
UPDATE locales_npc_text SET entry = 4718, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4718;
UPDATE locales_npc_text SET entry = 4719, Text0_0_loc2 = 'L\'esprit de l\'ours vous regarde d\'un œil calme et paisible. Une voix vous parle comme si elle venait de l\'ours, mais rien n\'indique que la créature soit en train de parler.$B$B"Je vous salue, jeune $gami:amie;. Si vous venez à moi en quête de conseils, peut-être puis-je vous aider à trouver ce que vous cherchez."', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4719;
UPDATE locales_npc_text SET entry = 4721, Text0_0_loc2 = 'Je représente la force qui vous soutient, jeune druide. Vous êtes là pour apprendre cette force auprès de moi, et je vous l\'enseignerai. Je vous mettrai en contact avec tout ce qui fait l\'esprit de l\'ours, si vous écoutez et comprenez.$B$BPour savoir ce que signifie faire appel à mon esprit, vous devez comprendre l\'importance de la force du corps et de la force du cœur.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4721;
UPDATE locales_npc_text SET entry = 4732, Text0_0_loc2 = 'Salutations, $N !', Text0_1_loc2 = 'Salutations, $N !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4732;
UPDATE locales_npc_text SET entry = 4733, Text0_0_loc2 = 'La force du corps est la puissance et la rapidité de l\'action. Vous devez vous maintenir en forme à tout instant, physiquement et mentalement. La corpulence de l\'ours augmente sa force, et c\'est un ennemi féroce au combat. Mais elle réduit son agilité et la finesse de son esprit. Voici des surprises que vous saurez utiliser à votre avantage.$B$BVous devez vous reposer sur la force du corps de l\'ours pour maîtriser la voie de la Griffe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4733;
UPDATE locales_npc_text SET entry = 4734, Text0_0_loc2 = 'La force du cœur est ce qui vous donne le courage d\'agir, d\'entreprendre des actions dont les racines sont pures et droites. Préserver l\'Equilibre ne signifie pas être banal ou falot.$B$BVous devez faire preuve de conviction, défendre ce en quoi vous croyez, et être $gprêt:prête; à vous battre pour cette cause. Les ourses font preuve d\'un dévouement sans borne dans la protection de leurs petits, tout comme les vieux ours dans celle de leur clan. C\'est ce courage, cette force du cœur, que vous devez montrer si vous voulez maîtriser la voie de la Griffe.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4734;
UPDATE locales_npc_text SET entry = 4735, Text0_0_loc2 = 'Il faut encore prouver que vous êtes à la hauteur. Même le plus sage et le plus ancien des druides ne peut se flatter d\'être totalement prêt à l\'heure de l\'épreuve ultime.$B$BVous avez entendu mes paroles, et maintenant vous devez continuer. Tenez compte de ce que je vous ai dit. Le temps viendra où votre force sera mise à l\'épreuve. Vous devez affronter votre ennemi comme un ours le ferait. Avec la force du corps et la force du cœur. Apprenez au combat. Allez. Vous avez ma bénédiction.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4735;
UPDATE locales_npc_text SET entry = 4753, Text0_0_loc2 = 'Salutations, $c. Je vous souhaite la bienvenue à Havrenuit, comme à Reflet-de-Lune. Le Cercle cénarien accueille tous les êtres qui respectent l\'autorité et les ordres de la nature.$B$BTant que vous êtes ici, assurez-vous que vous préservez l\'équilibre de toutes choses. Ici les troubles sont mal acceptés, et nos gardiens sont entraînés à faire cesser toute hostilité, sans poser de question.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4753;
UPDATE locales_npc_text SET entry = 4773, Text0_0_loc2 = 'Salutations, $N !', Text0_1_loc2 = 'Vous avez besoin d\'une illusion ?$B$BSi votre illusion flanche au milieu de votre quête, revenez me voir et je vous offrirai de nouveau le déguisement.$B$BSi vous échouez, n\'abandonnez pas ! Revenez me voir, je vous aiderai jusqu\'à ce que vous ayez atteint vos objectifs.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4773;
