UPDATE locales_npc_text SET entry = 4859, Text0_0_loc2 = 'Ces chevaux sont le résultat des meilleures techniques de reproduction et d\'une attention de tous les instants ! Prenez votre temps pour choisir votre monture. Un cheval, c\'est un compagnon pour la vie. Il faut vous mettre en valeur. Achetez votre monture ici aujourd\'hui même !', Text0_1_loc2 = 'Ces chevaux sont le résultat des meilleures techniques de reproduction et d\'une attention de tous les instants ! Prenez votre temps pour choisir votre monture. Un cheval, c\'est un compagnon pour la vie. Il faut vous mettre en valeur. Achetez votre monture ici aujourd\'hui même !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4859;
