UPDATE locales_npc_text SET entry = 4889, Text0_0_loc2 = 'Rappelez-vous, tout est lié. Si les animaux quittaient ces terres, notre espèce disparaîtrait, victime d\'une grande solitude spirituelle, car tout ce qui arrive aux animaux arrive également aux taurens. Ce qui touche Azeroth touche ceux qui l\'habitent.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4889;
