UPDATE locales_npc_text SET entry = 4891, Text0_0_loc2 = 'Salut ! Vous avez déjà plongé profondément sous l\'eau ? Je vais vous confier un petit secret. Au plus profond des océans, il y a des fissures bouillonnantes qui libèrent des bulles d’air : elles permettent de respirer sous l\'eau. Que ce secret reste entre vous et moi, hein ? Je ne veux pas de concurrents qui viendraient piller mes casiers à crustacés... Si vous voyez ce que je veux dire !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4891;
