UPDATE locales_npc_text SET entry = 4917, Text0_0_loc2 = 'Si vous cherchez un vol jusqu\'au village de Rut\'theran, parlez à mon alter ego chez les elfes de la nuit, Silva Fil\'naveth.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4917;
UPDATE locales_npc_text SET entry = 4918, Text0_0_loc2 = 'Voulez-vous un vol jusqu\'aux Pitons du Tonnerre, aux frais du Cercle cénarien ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4918;
UPDATE locales_npc_text SET entry = 4933, Text0_0_loc2 = 'Les temps que nous vivons sont inconstants comme les vents, $c. Rappelez-vous ce qui est important et durable dans le monde, et préservez-le. Tout le reste n\'est que perte de temps.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4933;
UPDATE locales_npc_text SET entry = 4934, Text0_0_loc2 = 'Aucune race d\'Azeroth n\'a autant souffert que la nôtre, $c. Rire de la mort est devenu une sorte de réflexe chez nous autres.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4934;
UPDATE locales_npc_text SET entry = 4935, Text0_0_loc2 = 'Salutations, $N, et bienvenue au Camp Narache ! Toute la tribu ne parle que de votre venue parmi nous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4935;
UPDATE locales_npc_text SET entry = 4936, Text0_0_loc2 = 'Ah ! La beauté de Sombrevallon n\'aura jamais fini de régaler mes sens !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4936;
UPDATE locales_npc_text SET entry = 4937, Text0_0_loc2 = 'Ah, c\'est que vous m\'avez l\'air solide ! Vous pourrez peut-être m\'aider pour un détail ou deux. Je ne trouve guère d\'aide ici, sauf auprès d\'apprentis sans expérience, et ils ont d\'autres chats à fouetter.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4937;
UPDATE locales_npc_text SET entry = 4938, Text0_0_loc2 = 'Hé ! Vous m\'avez l\'air d\'être costaud. Nous autres gardes sommes en sous-effectif. Vous pourriez nous aider...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4938;
UPDATE locales_npc_text SET entry = 4953, Text0_0_loc2 = 'Parce que vous m\'avez aidé, je vais vous dire ce que je sais de ce pays. Mes connaissances ne sont pas très vastes, mais il y a certaines choses qui peuvent intéresser quelqu\'un comme vous.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4953;
UPDATE locales_npc_text SET entry = 4954, Text0_0_loc2 = 'Ma connaissance de l\'histoire elfique est limitée, mais on dit qu\'il y a longtemps, lorsque les elfes de la nuit tenaient pour sacrée la magie des arcanes, Azshara était la plus grande d\'entre eux. Une très belle femme, qu\'ils appelaient leur reine. Malheureusement, c\'est son addiction à la magie qui allait déclencher les événements les plus sanglants que notre monde ait connus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4954;
UPDATE locales_npc_text SET entry = 4955, Text0_0_loc2 = 'Autrefois les Bien-nés dirigeaient une grande ville des elfes de la nuit et y pratiquaient une puissante magie des arcanes. Désormais ils sont maudits et hantent les terres. Leur ville en ruine a été victime des nagas Fouette-bile. Apparemment, les nagas fouillent les ruines à la recherche de reliques des Bien-nés.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4955;
UPDATE locales_npc_text SET entry = 4956, Text0_0_loc2 = 'On dit qu\'un puissant mage humain vit au nord. Il veillerait sur cette région pour une raison inconnue. Je n\'y suis pas allé moi-même, mais on dit que les visiteurs sont les bienvenus et peuvent même gagner ses faveurs s\'ils accomplissent quelques tâches pour lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4956;
UPDATE locales_npc_text SET entry = 4957, Text0_0_loc2 = 'J\'ai vu de terribles choses arriver aux furbolgs au cours des dernières années. Ils ont été des victimes, un peu partout. Mais j\'ignore ce qu\'il est advenu des membres de cette tribu. Ont-ils été atteints par la peste qui ravage Gangrebois ? Ou sont-ils furieux de ce qui est arrivé à leur peuple, naguère pacifique, et ils cherchent désormais à se défendre... Dans tous les cas, la plupart d\'entre eux se montrent hostiles envers ceux qui entrent dans cette région.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4957;
UPDATE locales_npc_text SET entry = 4958, Text0_0_loc2 = 'Les dragons bleus ne sont pas là, mais les disciples de Malygos s\'y trouvent. Je ne sais pas pourquoi, mais je peux vous dire ceci : quel que soit l\'endroit où se trouvent les dragons bleus, attendez-vous à trouver de la magie des arcanes. Je ne serais pas surpris si les draconides étaient là, à protéger quelque chose... peut-être quelque chose que les Bien-nés ont laissé derrière eux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4958;
UPDATE locales_npc_text SET entry = 4959, Text0_0_loc2 = null, Text0_1_loc2 = 'Il nous faudrait engager de l\'aide. Je n\'aime pas rester là toute la journée à vendre des accessoires de pêche. Je préférerais aller à la pêche moi-même, puis m\'occuper de mon étal.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4959;
UPDATE locales_npc_text SET entry = 4960, Text0_0_loc2 = null, Text0_1_loc2 = 'Throm-Ka, $C. Je n\'ai guère le moral aujourd\'hui, alors à moins que vous ne puissiez m\'aider, je préférerais rester seule.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4960;
UPDATE locales_npc_text SET entry = 4973, Text0_0_loc2 = 'Je ne peux pas vous former, $C.', Text0_1_loc2 = 'Je ne peux pas vous former, $C.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4973;
UPDATE locales_npc_text SET entry = 4974, Text0_0_loc2 = 'Je suis Ulysses Rackmore, capitaine du Hardi. Et voici le journal des derniers jours de mon infortuné navire.$B$BNous voguions vers le bastion de Pennelune avec une cargaison de fournitures lorsque des nagas nous ont attaqués. Nous les avons repoussées, mais nous savions qu\'elles reviendraient. Craignant pour ma précieuse cargaison, j\'ai mené le Hardi vers une île au large de Désolace, sur laquelle se trouvent de vieilles ruines elfiques. J\'y ai laissé mon coffre à trésor.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4974;
UPDATE locales_npc_text SET entry = 4975, Text0_0_loc2 = 'Mon coffre, de conception ingénieuse, nécessite deux clés pour être déverrouillé. Une en argent, une en or. J’ai confié la clé en argent à mon maître d\'équipage et j\'ai gardé la clé en or.$B$BMais nous étions voués à les perdre… J\'ai perdu la mienne au cours de la deuxième attaque des nagas, et celle de mon maître d\'équipage a disparu avec lui, un jour plus tard, quand des makruras Claquesec nous ont attaqué et l’ont entraîné sous les flots.$B$BLes nagas et les makruras ont sûrement les clés. Je suis heureux que ces deux races soient ennemies : elles ne les réuniront pas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4975;
UPDATE locales_npc_text SET entry = 4976, Text0_0_loc2 = 'Après l\'attaque des makruras, nous avons compris que le Hardi ne survivrait pas à un autre assaut. Les marins survivants et moi-même l’avons donc abandonné.$B$BNous sommes désormais sur le rivage, et nous partirons bientôt pour la ville de la Combe de Nijel. Mais bien des dangers nous guettent avant de pouvoir arriver à bon port, et j\'ai peur que nous ne perdions la vie dans Désolace.$B$BJ\'écris cela dans l\'espoir que quelqu\'un entende notre plainte et nous venge un jour des créatures à qui nous devons notre ruine.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4976;
UPDATE locales_npc_text SET entry = 4977, Text0_0_loc2 = null, Text0_1_loc2 = 'Le sanctuaire de Remulos est un lieu saint pour nous, membres du Cercle cénarien, ici à Reflet-de-Lune... Alors conduisez-vous d\'une manière qui convient à un hôte.$B$BUn hôte qui ne souhaite pas servir de râtelier à mon sabre...', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4977;
UPDATE locales_npc_text SET entry = 4978, Text0_0_loc2 = null, Text0_1_loc2 = 'Que Cénarius veille sur vous, $gfrère:sœur;.$B$BMon nom est Tajarri, et je suis la protectrice attitrée de cet autel. Mon honneur est de servir Remulos en tant que gardienne de cette terre sacrée.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4978;
UPDATE locales_npc_text SET entry = 4979, Text0_0_loc2 = 'Avant la venue du Fléau, j\'avais un bon commerce. J\'achetais et je vendais de tout. De tout !$B$BEt mes prix étaient DÉMENTIELS !$B$BDemandez à n\'importe qui. À ceux qui ne sont pas morts.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4979;
UPDATE locales_npc_text SET entry = 4980, Text0_0_loc2 = 'Salut $N ! Êtes-vous là pour acheter quelque chose ? Pour vendre quelque chose peut-être ?$B$BVous ne trouverez pas de meilleurs prix ailleurs, ou alors mon nom n\'est pas Augustus !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4980;
UPDATE locales_npc_text SET entry = 4982, Text0_0_loc2 = 'C\'est un endroit dangereux, $N. Soyez $gprudent:prudente;.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4982;
UPDATE locales_npc_text SET entry = 4984, Text0_0_loc2 = 'Ne me faites pas perdre mon temps, $C.', Text0_1_loc2 = 'Ne me faites pas perdre mon temps, $C.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4984;
UPDATE locales_npc_text SET entry = 4985, Text0_0_loc2 = 'Je n\'entraîne pas les gens de votre classe, $c. Trouvez-vous un maître avant que je ne m\'énerve.', Text0_1_loc2 = 'Je n\'entraîne pas les gens de votre classe, $c. Trouvez-vous un maître avant que je ne m\'énerve.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4985;
UPDATE locales_npc_text SET entry = 4986, Text0_0_loc2 = 'Doucement, doucement ! Je ne forme que des guerriers.', Text0_1_loc2 = 'Doucement, doucement ! Je ne forme que des guerriers.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 4986;
