UPDATE locales_npc_text SET entry = 5027, Text0_0_loc2 = null, Text0_1_loc2 = 'C\'est aimable à vous de venir me voir, mais vous dépassez mes modestes aptitudes. Allez dire quelques mots à Sylvanna Sylvelune, là-bas. Elle cherche un nouvel apprenti.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5027;
UPDATE locales_npc_text SET entry = 5028, Text0_0_loc2 = 'Est-ce ma douce et bonne amie Kali qui vous envoie à moi ? Elle est la prunelle de mes yeux.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5028;
UPDATE locales_npc_text SET entry = 5029, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5029;
UPDATE locales_npc_text SET entry = 5030, Text0_0_loc2 = 'Vous êtes au-delà de ce que je peux vous apprendre, $N. Allez voir Tally Berryfizz, c\'est une gnome qui vit à Forgefer, dans Brikabrok. Oh, et puisque vous allez par là, dites à Kali combien je l\'aime.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5030;
UPDATE locales_npc_text SET entry = 5031, Text0_0_loc2 = 'Ne laissez pas ces grands yeux innocents vous tromper. Tally s\'y connaît parfaitement. J\'apprends chaque jour quelque chose de nouveau. Pourquoi croyez-vous que je suis ici à Brikabrok ? Est-ce que j\'ai l\'air d\'un ingénieur ? Je ne crois pas.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5031;
UPDATE locales_npc_text SET entry = 5032, Text0_0_loc2 = 'Remettez sans cesse votre ouvrage sur le métier, $gmon garçon:jeune fille;. Le travail et la détermination, c\'est la seule méthode ! Ne laissez personne vous dire que les livres sont une instruction suffisante !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5032;
UPDATE locales_npc_text SET entry = 5033, Text0_0_loc2 = 'Je ne peux rien vous enseigner, $N. Parlez à Tally Berryfizz, par là. Elle peine à atteindre les tables, mais quand elle commence à préparer une potion, faites attention !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5033;
UPDATE locales_npc_text SET entry = 5034, Text0_0_loc2 = 'Vous avez tout l\'air de quelqu\'un qui cherche l\'aventure. Moi, j\'en avais déjà assez à l\'époque où les trolls sont arrivés. Aujourd\'hui, je prends les jours comme ils viennent. Et j\'entraîne les autres à courir l\'aventure à ma place.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5034;
UPDATE locales_npc_text SET entry = 5035, Text0_0_loc2 = 'Une autre leçon ? Très bien. D\'abord, relaxez-vous. Vous devez être calme pour bien remuer une potion.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5035;
UPDATE locales_npc_text SET entry = 5036, Text0_0_loc2 = 'Vous avez des questions, mais je ne peux pas y répondre, $N. Discutez avec l\'orc Yelmak de la Herse d\'Orgrimmar. Il vous donnera un coup de main.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5036;
UPDATE locales_npc_text SET entry = 5037, Text0_0_loc2 = 'Toutes les bouteilles sur cette table sont des potions que j\'ai préparées aujourd\'hui. Contrairement à ce qu\'on dit, ce n\'est pas forcément dans les vieilles outres... Enfin, bon...', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5037;
UPDATE locales_npc_text SET entry = 5038, Text0_0_loc2 = 'On me dit stupide, mais je leur montrerai ! Je vais étudier chaque pot que je possède et je prouverai qu\'on peut y faire bouillir ce qu\'on veut. Oui, ils verront ! Alors qui est stupide, hein ? Hein ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5038;
UPDATE locales_npc_text SET entry = 5039, Text0_0_loc2 = 'Peux pas vous aider. Vous en savez plus sur les potions que j\'en ai jamais su. Et puis j\'ai des trucs sur le feu. Parlez à Yelmak là-bas. Il en connaît un rayon sur les potions.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5039;
UPDATE locales_npc_text SET entry = 5040, Text0_0_loc2 = 'Bena travaille comme une possédée. Jour et nuit, je la vois penchée sur ces potions, sans qu\'elle ne s\'arrête pour dormir ou manger. Je sais bien que nos alliés morts-vivants ont besoin d\'aide, mais je crains qu\'elle ne finisse par y laisser sa santé.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5040;
UPDATE locales_npc_text SET entry = 5041, Text0_0_loc2 = 'Lorsque j\'ai commencé à faire des potions, j\'ai cassé des centaines de fioles. Le verre était si fragile entre mes mains maladroites. J\'espère que vous ferez plus attention que moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5041;
UPDATE locales_npc_text SET entry = 5042, Text0_0_loc2 = 'Votre capacité à apprendre vous a permis de me surpasser. Vous allez devoir rencontrer Bena Winterhoof, là-bas, pour progresser encore. Cependant ne prenez pas trop de son temps : elle a besoin de dormir.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5042;
UPDATE locales_npc_text SET entry = 5043, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5043;
UPDATE locales_npc_text SET entry = 5044, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5044;
UPDATE locales_npc_text SET entry = 5045, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5045;
UPDATE locales_npc_text SET entry = 5046, Text0_0_loc2 = 'C\'est vrai, j\'étais humain jadis. Ce qui semble un point faible pourrait en fait travailler en notre faveur, $N, car je connais bien les faiblesses humaines.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5046;
UPDATE locales_npc_text SET entry = 5047, Text0_0_loc2 = 'Très bien. Coupez un autre lambeau de chair à l\'un de ces spécimens. Mettons à l\'épreuve ce que vous avez appris, et nous irons ensuite de l\'avant.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5047;
UPDATE locales_npc_text SET entry = 5048, Text0_0_loc2 = 'Votre connaissance des potions va au-delà de la mienne, mais vous avez encore beaucoup à apprendre. Montez l\'escalier au sud-est et allez voir le docteur Marsh. Il vous enseignera la vraie souffran... Euh... C\'est un Réprouvé qui vous fera progresser.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5048;
UPDATE locales_npc_text SET entry = 5049, Text0_0_loc2 = 'Arrêtez de me regarder comme ça. Je ne suis peut-être pas un aventurier comme vous, mais ça ne veut pas dire que je n\'ai pas de muscles. Mon travail demande de la force, croyez-moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5049;
UPDATE locales_npc_text SET entry = 5050, Text0_0_loc2 = 'Je ne peux rien vous apprendre de plus. Il paraît qu\'une elfe de la nuit en serait capable. Son nom est Ainethil, et elle vit à la Terrasse des Artisans de Darnassus.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5050;
