UPDATE locales_npc_text SET entry = 5064, Text0_0_loc2 = null, Text0_1_loc2 = 'Même la plus légère altération d\'un élément constitutif d\'une potion peut la modifier complètement. Patience et précision, c\'est cela dont vous aurez besoin tout au long de votre vie.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5064;
