UPDATE locales_npc_text SET entry = 5068, Text0_0_loc2 = null, Text0_1_loc2 = 'J\'ai beaucoup appris sur les potions au fil des ans, $N, et ce savoir est à votre disposition. Vous n\'avez qu\'à demander.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5068;
UPDATE locales_npc_text SET entry = 5069, Text0_0_loc2 = 'Je n\'ai pas le temps, $N. Dites à Whuut de s\'en occuper. Ce chiot a mariné toute la journée. Il a passé trop de temps comme ça, il va finir par blesser quelqu\'un.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5069;
UPDATE locales_npc_text SET entry = 5070, Text0_0_loc2 = 'C\'est un honneur de vous rencontrer, $N, mais vous maîtrisez les potions bien mieux que moi. Allez voir le docteur Herbert Halsey, un mort-vivant résidant à l\'Apothicarium d\'Fossoyeuse. Il pourra vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5070;
UPDATE locales_npc_text SET entry = 5072, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5072;
UPDATE locales_npc_text SET entry = 5073, Text0_0_loc2 = 'Un autre élève, hein ? Bien. Je vais voir ce que je peux faire pour vous aider.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5073;
UPDATE locales_npc_text SET entry = 5074, Text0_0_loc2 = 'Glyx et moi avons un marché : il me fournit ce dont j\'ai besoin, je lui fournis de l\'or. Sérieusement ! Ce que cet escroc m\'a fait payer pour un malheureux sac de sang-royal... Il y a de quoi faire pleurer un gobelin adulte !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5074;
UPDATE locales_npc_text SET entry = 5075, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5075;
UPDATE locales_npc_text SET entry = 5078, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5078;
UPDATE locales_npc_text SET entry = 5079, Text0_0_loc2 = null, Text0_1_loc2 = 'La première fois que j\'ai vu les morts-vivants, j\'ai pleuré. Car je ne pouvais concevoir la profondeur de leur douleur ou de leur solitude. La Terre-mère m\'a alors parlé, elle a chanté dans mon sang. Elle me disait : "Sauve-les."', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5079;
UPDATE locales_npc_text SET entry = 5080, Text0_0_loc2 = null, Text0_1_loc2 = 'Ne sentez-vous pas l\'angoisse de la Terre-mère pour ses enfants perdus, les morts-vivants ? Parlez, vite, $N. Chaque seconde me coûte terriblement. Nous devons les aider à revenir à elle !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5080;
UPDATE locales_npc_text SET entry = 5081, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5081;
UPDATE locales_npc_text SET entry = 5083, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5083;
UPDATE locales_npc_text SET entry = 5084, Text0_0_loc2 = 'Comment voulez-vous que je travaille si on m\'interrompt tout le temps ? Ne répondez pas. Est-ce que vous croyez que je suis d\'humeur à entendre quoi que ce soit ? Pourquoi n\'allez-vous pas plutôt embêter Monsieur Sa Majesté ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5084;
UPDATE locales_npc_text SET entry = 5085, Text0_0_loc2 = 'Moi ? Vous former ? Allons, nous savons tous les deux que c\'est impossible. Mes compétences en souffriraient. Si vous voulez une formation, allez voir le docteur Herbert Halsey, un Réprouvé qui se trouve à l\'Apothicarium d\'Fossoyeuse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5085;
UPDATE locales_npc_text SET entry = 5087, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5087;
UPDATE locales_npc_text SET entry = 5088, Text0_0_loc2 = '$gUn:Une; élève de plus. Merveilleux. Voilà pourquoi Lydon appartient à la Société royale des Snobs et peut faire de la recherche, alors que je suis là à servir d\'instructeur à des ignorants. Si je gagne encore un diplôme du meilleur professeur, je ne m\'en remettrai jamais !', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5088;
UPDATE locales_npc_text SET entry = 5089, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5089;
UPDATE locales_npc_text SET entry = 5090, Text0_0_loc2 = 'Je n\'ai plus rien à vous apprendre. Passez l\'arche et descendez la rampe vers le sud-est, et allez trouver le docteur Herbert Halsey. C\'est un Réprouvé qui saura vous apprendre les techniques les plus avancées.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5090;
UPDATE locales_npc_text SET entry = 5092, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5092;
UPDATE locales_npc_text SET entry = 5093, Text0_0_loc2 = 'Vous êtes là pour progresser ? Très bien. Je suppose que vous ne pourrez pas faire beaucoup de mal à nos alliés si je vous apprends deux ou trois choses. Et puis vous parviendrez peut-être à faire du mal à nos ennemis.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5093;
UPDATE locales_npc_text SET entry = 5094, Text0_0_loc2 = null, Text0_1_loc2 = 'Chaque fois que je la prépare, cette potion semble moins efficace, bien que j\'utilise toujours les mêmes ingrédients. Il n\'y a qu\'une conclusion à en tirer : les plantes changent. J\'ignore pourquoi et de quelle manière.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5094;
UPDATE locales_npc_text SET entry = 5095, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5095;
UPDATE locales_npc_text SET entry = 5097, Text0_0_loc2 = null, Text0_1_loc2 = 'Vos aptitudes à concocter des potions doivent être développées avant que vous ne deveniez un de mes apprentis. En attendant, Sylvanna Sylvelune ou Milla Bellancora pourront sûrement vous aider. Vous les trouverez juste là.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5097;
UPDATE locales_npc_text SET entry = 5098, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5098;
UPDATE locales_npc_text SET entry = 5099, Text0_0_loc2 = 'Mes collègues et moi-même sommes en train de réunir les substances les plus mortelles que nous connaissions et de tester leurs effets sur nos ennemis. Dès que j\'aurai tout ce dont j\'ai besoin, je vais concocter une épidémie comme ce monde n\'en a jamais connu.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5099;
UPDATE locales_npc_text SET entry = 5100, Text0_0_loc2 = 'Incroyable ! Vos potions prouvent un savoir-faire qui rivalise avec le mien. Je ne peux pas continuer à vous entraîner, mais adressez-vous à l\'orc Rogvar de Pierrêche dans le marais des Chagrins. Il est connu pour la qualité de ses expériences.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5100;
UPDATE locales_npc_text SET entry = 5102, Text0_0_loc2 = 'Vous n\'avez pas les compétences voulues pour étudier avec moi. Allez parler au docteur Martin Felben, là-bas, ou grimpez la rampe au sud-est pour rencontrer le docteur Marsh. Ce sont des Réprouvés qui pourront vous transmettre les bases.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5102;
UPDATE locales_npc_text SET entry = 5103, Text0_0_loc2 = 'Vous voulez commencer votre apprentissage ? Alors gardez le rythme, $N. Nous avons du chemin à faire.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5103;
UPDATE locales_npc_text SET entry = 5104, Text0_0_loc2 = null, Text0_1_loc2 = 'Naguère, j\'accueillais volontiers les ténèbres, car elles étaient sœurs de la nuit. Maintenant la pénombre est permanente, même dans la journée, et les forêts crient vers nous. À moins que nous n\'agissions vite, cette infection couvrira bientôt toute la planète.', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5104;
UPDATE locales_npc_text SET entry = 5105, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5105;
UPDATE locales_npc_text SET entry = 5106, Text0_0_loc2 = null, Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5106;
UPDATE locales_npc_text SET entry = 5108, Text0_0_loc2 = 'Le Chef Thrall lui-même m\'a demandé de venir ici. Ce fut un honneur d\'obéir, et du reste ça n\'a rien de bien embêtant. On ne peut rêver de meilleur poste que Pierrêche. On peut trouver ici toutes les herbes dont j\'ai besoin.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5108;
UPDATE locales_npc_text SET entry = 5110, Text0_0_loc2 = 'Hmm… Ma foi, je vois que vous avez quelques prédispositions à faire les potions. Cependant, il faut voir si vous êtes digne d\'être mon élève. J\'ai un travail à vous proposer. Étonnez-moi.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5110;
