UPDATE locales_npc_text SET entry = 5207, Text0_0_loc2 = 'Si vous avez ce qu\'il faut, j\'aurai plaisir à vous former.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5207;
UPDATE locales_npc_text SET entry = 5208, Text0_0_loc2 = 'Un vrai professionnel connaît ses limites, et je n\'ai pas honte de dire qu\'en ce qui vous concerne les miennes sont atteintes. Rendez donc visite à Kitta Ventefeu, à la Tour d\'Azora. Elle est bien connue et sa réputation n\'est pas usurpée.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5208;
UPDATE locales_npc_text SET entry = 5209, Text0_0_loc2 = 'Vous semblez avoir confiance en vous. Vous ne rechignerez donc pas à ce que je vous fasse passer une petite épreuve ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5209;
UPDATE locales_npc_text SET entry = 5210, Text0_0_loc2 = 'Il est trop tôt pour vous. Parlez à Betty Quinn, elle se trouve sur les escaliers, par là. Revenez me voir quand vous aurez progressé dans la voie de la sagesse.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5210;
