UPDATE locales_npc_text SET entry = 5212, Text0_0_loc2 = 'Si vous cherchez à chasser des nagas, vous aurez besoin de toute l\'aide possible. Au fond de l\'eau, vous trouverez des fissures bouillonnantes, qui fournissent assez de bulles d\'air pour vous permettre de respirer sous l\'eau. Nagez assez près pour utiliser ces bulles.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5212;
UPDATE locales_npc_text SET entry = 5213, Text0_0_loc2 = 'Il est vrai que j\'ai des choses à apprendre à ceux qui le méritent. Cela vous concerne-t-il ?', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5213;
UPDATE locales_npc_text SET entry = 5214, Text0_0_loc2 = 'Vous êtes un enchanteur d\'exception. Au-delà de mes talents d\'instructeur, j\'en ai peur. Cherchez Kitta Firewind, à la Tour d\'Azora. Désormais, elle seule peut encore vous enseigner quelque chose.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5214;
