UPDATE locales_npc_text SET entry = 5218, Text0_0_loc2 = null, Text0_1_loc2 = 'Si vous cherchez un but vraiment digne d\'y consacrer sa vie, je vous conseille de devenir enchanteur !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5218;
