UPDATE locales_npc_text SET entry = 5223, Text0_0_loc2 = 'Dans ma famille, on est enchanteur depuis des générations. J\'ai commencé ma formation très jeune, comme c\'est le cas aujourd\'hui de mon fils.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5223;
UPDATE locales_npc_text SET entry = 5224, Text0_0_loc2 = 'Il est quelqu\'un dont les connaissances dépassent les miennes. Vous avez le niveau requis pour la rencontrer. Allez à la Retraite de Roche-Soleil, dans les Serres-Rocheuses. Trouvez Hgarth et continuez votre formation.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5224;
UPDATE locales_npc_text SET entry = 5225, Text0_0_loc2 = 'Je vais bientôt m\'occuper de votre formation. J\'ai d\'abord besoin d\'une démonstration de votre loyauté.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5225;
UPDATE locales_npc_text SET entry = 5226, Text0_0_loc2 = 'Ce n\'est sûrement pas Mot qui vous envoie, n\'est-ce pas ? Il n\'est pas idiot. Vous n\'êtes pas encore en mesure de comprendre ce que j\'ai à enseigner. Allez voir mon fils Mot, juste dehors, là, et finissez votre entraînement avec lui.', Text0_1_loc2 = null, Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5226;
