UPDATE locales_npc_text SET entry = 5253, Text0_0_loc2 = null, Text0_1_loc2 = 'Salutations, $gvoyageur:voyageuse;, vous avez ma gratitude éternelle pour vous être $goccupé:occupée; de ces terribles créatures ! Je commençais à croire qu\'il me faudrait me cacher jusqu\'à la fin des temps !', Text1_0_loc2 = null, Text1_1_loc2 = null, Text2_0_loc2 = null, Text2_1_loc2 = null, Text3_0_loc2 = null, Text3_1_loc2 = null, Text4_0_loc2 = null, Text4_1_loc2 = null, Text5_0_loc2 = null, Text5_1_loc2 = null, Text6_0_loc2 = null, Text6_1_loc2 = null, Text7_0_loc2 = null, Text7_1_loc2 = null WHERE entry = 5253;
